import { NextRequest, NextResponse } from 'next/server';
import { GoogleGenerativeAI } from '@google/generative-ai';
import { verifyIdToken } from '@/lib/firebase/admin';

// For local development fallback
const GEMINI_API_KEY = process.env.GEMINI_API_KEY || process.env.NEXT_PUBLIC_GEMINI_API_KEY || '';

// Vertex AI client (lazy initialized)
let vertexAIClient: unknown = null;
let vertexInitAttempted = false;
let vertexInitError: string | null = null;

// Google GenAI client (lazy initialized)  
let genAI: GoogleGenerativeAI | null = null;

async function tryInitializeVertexAI(): Promise<unknown> {
    if (vertexAIClient) return vertexAIClient;
    if (vertexInitAttempted) return null; // Don't retry if already failed

    vertexInitAttempted = true;

    try {
        const { VertexAI } = await import('@google-cloud/vertexai');
        const projectId = process.env.GOOGLE_CLOUD_PROJECT ||
            process.env.NEXT_PUBLIC_FIREBASE_PROJECT_ID ||
            'antigrav-tracking-final';
        vertexAIClient = new VertexAI({ project: projectId, location: 'us-central1' });
        return vertexAIClient;
    } catch (e) {
        vertexInitError = e instanceof Error ? e.message : 'Unknown error';
        console.warn('[AI Route] Vertex AI initialization failed:', vertexInitError);
        return null;
    }
}

export async function POST(req: NextRequest) {
    try {
        const body = await req.json();
        const { prompt, modelName = "gemini-2.0-flash" } = body;

        if (!prompt) {
            return NextResponse.json({ error: "Prompt is required" }, { status: 400 });
        }

        // --- AUTHENTICATION CHECK ---
        // For production, we must verify the user is authenticated
        const authHeader = req.headers.get('Authorization');
        if (!authHeader || !authHeader.startsWith('Bearer ')) {
            // Local dev exception (optional, but better to be strict)
            if (process.env.NODE_ENV !== 'development') {
                return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
            }
        } else {
            const token = authHeader.split('Bearer ')[1];
            const decodedToken = await verifyIdToken(token);
            if (!decodedToken) {
                return NextResponse.json({ error: "Invalid token" }, { status: 401 });
            }
            // User is authenticated
        }

        // ALWAYS try Vertex AI first (works in App Hosting with ADC)

        // ALWAYS try Vertex AI first (works in App Hosting with ADC)
        try {
            const vertexAI = await tryInitializeVertexAI();
            if (vertexAI) {
                const model = (vertexAI as any).getGenerativeModel({ model: modelName });
                const result = await model.generateContent(prompt);
                const text = result.response?.candidates?.[0]?.content?.parts?.[0]?.text || '';
                return NextResponse.json({ text });
            }
        } catch (e) {
            const errorMsg = e instanceof Error ? e.message : 'Unknown error';
            console.warn('[AI Route] Vertex AI generation failed:', errorMsg);
            // Fall through to API key fallback
        }

        // Fallback to Google Generative AI SDK with API key
        if (!GEMINI_API_KEY) {
            console.error('[AI Route] No API key available for fallback');
            return NextResponse.json(
                {
                    error: "AI service unavailable. Vertex AI failed to initialize and no API key is configured.",
                    vertexError: vertexInitError
                },
                { status: 500 }
            );
        }

        if (!genAI) {
            genAI = new GoogleGenerativeAI(GEMINI_API_KEY);
        }

        const model = genAI.getGenerativeModel({ model: modelName });
        const result = await model.generateContent(prompt);
        const response = await result.response;
        const text = response.text();

        return NextResponse.json({ text });
    } catch (error: unknown) {
        const errorMessage = error instanceof Error ? error.message : 'Internal Server Error';
        console.error('[AI Route] Fatal error:', error);
        return NextResponse.json(
            { error: errorMessage },
            { status: 500 }
        );
    }
}

import { NextRequest, NextResponse } from 'next/server';
import { db } from '@/lib/firebase/config';
import { collection, query, where, getDocs, addDoc } from 'firebase/firestore';

/**
 * Twilio Inbound Webhook (Dec 2025)
 * Captures replies and routes them to the correct lead history.
 */
export async function POST(req: NextRequest) {
    try {
        const formData = await req.formData();
        const from = formData.get('From') as string;
        const body = formData.get('Body') as string;
        const messageSid = formData.get('MessageSid') as string;

        if (!from || !body) {
            return new Response('Invalid Request', { status: 400 });
        }

        // 1. Find the lead associated with this phone number
        // We search across all users since webhooks are global
        // In production, you'd use a more efficient lookup index
        const leadsQuery = query(collection(db, 'leads'), where('phone', '==', from));
        const leadsSnapshot = await getDocs(leadsQuery);

        if (leadsSnapshot.empty) {
            console.log(`No lead found for number: ${from}`);
            return new Response('Lead not found', { status: 200 }); // Still return 200 to Twilio
        }

        // 2. Log the activity for each user/lead matching this number
        const logPromises = leadsSnapshot.docs.map(async (leadDoc) => {
            const leadData = leadDoc.data();
            const userId = leadData.ownerId; // Assuming ownerId exists on lead

            if (userId) {
                return addDoc(collection(db, 'users', userId, 'activities'), {
                    type: 'social',
                    outcome: 'connected',
                    timestamp: Date.now(),
                    repId: userId,
                    leadId: leadDoc.id,
                    notes: `Inbound Message: ${body}`,
                    channel: 'sms',
                    messageSid
                });
            }
        });

        await Promise.all(logPromises);

        // 3. Respond with empty TwiML to acknowledge receipt
        return new Response('<Response></Response>', {
            headers: { 'Content-Type': 'text/xml' }
        });

    } catch (error) {
        console.error('Webhook Error:', error);
        return new Response('Internal error', { status: 500 });
    }
}

import { NextRequest, NextResponse } from 'next/server';
import { lookupPhoneNumber } from '@/lib/twilio/lookup-service';
import { isTwilioConfigured } from '@/lib/twilio/twilio-config';
import { withAuth } from '@/lib/auth';

export async function POST(request: NextRequest) {
    try {
        if (!isTwilioConfigured()) {
            return NextResponse.json(
                { error: 'Twilio not configured' },
                { status: 503 }
            );
        }

        const body = await request.json();
        const { phoneNumber } = body;

        if (!phoneNumber) {
            return NextResponse.json(
                { error: 'Phone number is required' },
                { status: 400 }
            );
        }

        const result = await lookupPhoneNumber(phoneNumber);

        return NextResponse.json(result);

    } catch (error) {
        console.error('Lookup API Error:', error);
        return NextResponse.json(
            { error: 'Failed to verify phone number' },
            { status: 500 }
        );
    }
}

import { NextRequest, NextResponse } from 'next/server';
import { updateSMSStatus } from '@/lib/twilio/sms-service';

export async function POST(request: NextRequest) {
    try {
        const formData = await request.formData();

        const messageSid = formData.get('MessageSid') as string;
        const messageStatus = formData.get('MessageStatus') as string;

        // Get generic params
        const userId = request.nextUrl.searchParams.get('userId');
        const leadId = request.nextUrl.searchParams.get('leadId');



        // Map Twilio status
        const validStatuses = ['queued', 'sent', 'delivered', 'failed', 'undelivered'];
        const status = validStatuses.includes(messageStatus) ? messageStatus as any : 'sent';

        if (userId && messageSid) {
            try {
                await updateSMSStatus(messageSid, userId, status);
                await updateSMSStatus(messageSid, userId, status);
            } catch (error) {
                console.error(`Failed to update SMS status:`, error);
            }
        } else {
            console.warn('Missing userId or messageSid in SMS webhook');
        }

        return new NextResponse(
            '<?xml version="1.0" encoding="UTF-8"?><Response></Response>',
            {
                status: 200,
                headers: { 'Content-Type': 'text/xml' }
            }
        );
    } catch (error) {
        console.error('Error processing SMS webhook:', error);
        return new NextResponse(
            '<?xml version="1.0" encoding="UTF-8"?><Response></Response>',
            {
                status: 200,
                headers: { 'Content-Type': 'text/xml' }
            }
        );
    }
}

import { NextRequest, NextResponse } from 'next/server';
import { updateCallStatus } from '@/lib/twilio/call-service';
import { validateRequest } from 'twilio';

/**
 * Twilio webhook for call status updates
 * Twilio sends POST requests here when call status changes
 */
export async function POST(request: NextRequest) {
    try {
        const formData = await request.formData();

        // 1. Security Check: Validate Request Signature
        const twilioSignature = request.headers.get('x-twilio-signature');
        const authToken = process.env.TWILIO_AUTH_TOKEN;
        const url = `${process.env.NEXT_PUBLIC_APP_URL}/api/twilio/webhook`;

        // Convert formData to object for validation
        const params: Record<string, string> = {};
        formData.forEach((value, key) => {
            params[key] = value as string;
        });

        if (process.env.NODE_ENV === 'production') {
            if (!twilioSignature || !authToken || !validateRequest(authToken, twilioSignature, url, params)) {
                console.error('Security Alert: Invalid Twilio Signature');
                return new NextResponse('Unauthorized', { status: 403 });
            }
        }

        const callSid = params['CallSid'];
        const callStatus = params['CallStatus'];
        const callDuration = params['CallDuration'];
        const recordingUrl = params['RecordingUrl'];

        // Get userId from query params (passed in statusCallback)
        const userId = request.nextUrl.searchParams.get('userId');
        const leadId = request.nextUrl.searchParams.get('leadId');



        // Map Twilio status to our status type
        const statusMap: Record<string, string> = {
            'queued': 'queued',
            'ringing': 'ringing',
            'in-progress': 'in-progress',
            'completed': 'completed',
            'busy': 'busy',
            'failed': 'failed',
            'no-answer': 'no-answer',
            'canceled': 'canceled',
        };

        const status = (statusMap[callStatus] || 'failed') as any;
        const duration = callDuration ? parseInt(callDuration) : undefined;

        if (userId) {
            try {
                await updateCallStatus(callSid, userId, status, duration, recordingUrl);
                console.log(`Updated call ${callSid} status to ${status} for user ${userId}`);
            } catch (error) {
                console.error(`Failed to update call status for ${callSid}:`, error);
                // Don't fail the webhook response, just log the error
            }
        } else {
            console.warn(`Received webhook for call ${callSid} without userId`);
        }

        // Return TwiML response (required by Twilio)
        return new NextResponse(
            '<?xml version="1.0" encoding="UTF-8"?><Response></Response>',
            {
                status: 200,
                headers: {
                    'Content-Type': 'text/xml',
                },
            }
        );
    } catch (error) {
        console.error('Error processing Twilio webhook:', error);
        return new NextResponse(
            '<?xml version="1.0" encoding="UTF-8"?><Response></Response>',
            {
                status: 200,
                headers: {
                    'Content-Type': 'text/xml',
                },
            }
        );
    }
}

'use client';

import React, { useState, useEffect } from 'react';
import { GlassCard } from '@/components/ui/GlassCard';
import { useAuth } from '@/components/providers/AuthProvider';
import { DashboardService } from '@/lib/firebase/services';
import { formatCurrency } from '@/lib/utils/formatters';
import { TrendingUp, TrendingDown, Phone, Users, DollarSign, Calendar } from 'lucide-react';

// Fallback mock data when not logged in
const MOCK_ACTIVITY_DATA = [
  { day: 'Mon', dials: 52, connects: 6 },
  { day: 'Tue', dials: 48, connects: 5 },
  { day: 'Wed', dials: 55, connects: 8 },
  { day: 'Thu', dials: 45, connects: 4 },
  { day: 'Fri', dials: 38, connects: 3 },
  { day: 'Sat', dials: 12, connects: 1 },
  { day: 'Sun', dials: 0, connects: 0 },
];

const MOCK_PIPELINE_STAGES = [
  { stage: 'New Leads', count: 12, color: 'bg-blue-500' },
  { stage: 'Contacted', count: 8, color: 'bg-cyan-500' },
  { stage: 'Qualified', count: 6, color: 'bg-purple-500' },
  { stage: 'Proposal', count: 4, color: 'bg-amber-500' },
  { stage: 'Negotiation', count: 2, color: 'bg-green-500' },
];

interface DashboardMetrics {
  dials: number;
  pipelineValue: number;
  meetingsHeld: number;
  pipelineStatusCounts: Record<string, number>;
  totalLeads: number;
  emailsSent: number;
  activityChart: { day: string; dials: number; connects: number }[];
}

export default function Dashboard() {
  const { user, loading: authLoading } = useAuth();
  const [isLoading, setIsLoading] = useState(true);
  const [metrics, setMetrics] = useState<DashboardMetrics | null>(null);

  useEffect(() => {
    if (authLoading) return;

    if (!user) {
      setIsLoading(false);
      return;
    }

    loadMetrics();
  }, [user, authLoading]);

  const loadMetrics = async () => {
    if (!user) return;
    setIsLoading(true);
    try {
      const data = await DashboardService.getMetrics(user.uid);
      setMetrics(data);
    } catch (error) {
      console.error('Error loading metrics:', error);
    } finally {
      setIsLoading(false);
    }
  };

  if (isLoading) {
    return (
      <main className="min-h-screen p-8 flex items-center justify-center">
        <div className="text-slate-500">Loading dashboard...</div>
      </main>
    );
  }

  // Use real data if available, otherwise use mock data
  const activityData = metrics?.activityChart || MOCK_ACTIVITY_DATA;
  const pipelineStages = metrics ? [
    { stage: 'New Leads', count: metrics.pipelineStatusCounts['New'] || 0, color: 'bg-blue-500' },
    { stage: 'Contacted', count: metrics.pipelineStatusCounts['Contacted'] || 0, color: 'bg-cyan-500' },
    { stage: 'Qualified', count: metrics.pipelineStatusCounts['Qualified'] || 0, color: 'bg-purple-500' },
    { stage: 'Proposal', count: metrics.pipelineStatusCounts['Proposal'] || 0, color: 'bg-amber-500' },
    { stage: 'Negotiation', count: metrics.pipelineStatusCounts['Negotiation'] || 0, color: 'bg-green-500' },
  ] : MOCK_PIPELINE_STAGES;

  const maxDials = Math.max(...activityData.map(d => d.dials));
  const totalDials = metrics?.dials ?? activityData.reduce((sum, d) => sum + d.dials, 0);
  const totalConnects = activityData.reduce((sum, d) => sum + d.connects, 0);
  const connectRate = totalDials > 0 ? ((totalConnects / totalDials) * 100).toFixed(1) : '0';
  const pipelineValue = metrics?.pipelineValue ?? 124000;
  const meetingsHeld = metrics?.meetingsHeld ?? 12;
  const totalLeads = metrics?.totalLeads ?? pipelineStages.reduce((sum, s) => sum + s.count, 0);

  return (
    <main className="min-h-screen p-8">
      <header className="mb-8">
        <h1 className="text-4xl font-bold mb-2 bg-clip-text text-transparent bg-gradient-to-r from-blue-400 to-violet-400">
          Sales &amp; Lead Tracking
        </h1>
        <p className="text-slate-400">
          {user ? 'Real-time performance metrics' : 'Demo Mode - Log in to see your data'}
        </p>
      </header>

      {/* KPI Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        <GlassCard>
          <div className="flex items-center justify-between">
            <div>
              <h3 className="text-sm text-slate-400 font-medium">Today&apos;s Dials</h3>
              <div className="text-3xl font-bold text-blue-400 mt-2">{totalDials}</div>
              <div className="flex items-center gap-1 mt-1">
                <TrendingUp className="w-3 h-3 text-green-400" />
                <span className="text-xs text-green-400">Keep it up!</span>
              </div>
            </div>
            <div className="p-3 bg-blue-500/20 rounded-lg">
              <Phone className="w-6 h-6 text-blue-400" />
            </div>
          </div>
        </GlassCard>

        <GlassCard>
          <div className="flex items-center justify-between">
            <div>
              <h3 className="text-sm text-slate-400 font-medium">Connect Rate</h3>
              <div className="text-3xl font-bold text-green-400 mt-2">{connectRate}%</div>
              <div className="flex items-center gap-1 mt-1">
                <TrendingUp className="w-3 h-3 text-green-400" />
                <span className="text-xs text-green-400">Above target (3-10%)</span>
              </div>
            </div>
            <div className="p-3 bg-green-500/20 rounded-lg">
              <Users className="w-6 h-6 text-green-400" />
            </div>
          </div>
        </GlassCard>

        <GlassCard>
          <div className="flex items-center justify-between">
            <div>
              <h3 className="text-sm text-slate-400 font-medium">Pipeline Value</h3>
              <div className="text-3xl font-bold text-violet-400 mt-2">{formatCurrency(pipelineValue, { compact: true })}</div>
              <div className="flex items-center gap-1 mt-1">
                <TrendingDown className="w-3 h-3 text-amber-400" />
                <span className="text-xs text-slate-500">Forecast: {formatCurrency(pipelineValue * 0.7, { compact: true })}</span>
              </div>
            </div>
            <div className="p-3 bg-violet-500/20 rounded-lg">
              <DollarSign className="w-6 h-6 text-violet-400" />
            </div>
          </div>
        </GlassCard>

        <GlassCard>
          <div className="flex items-center justify-between">
            <div>
              <h3 className="text-sm text-slate-400 font-medium">Meetings This Week</h3>
              <div className="text-3xl font-bold text-amber-400 mt-2">{meetingsHeld}</div>
              <div className="flex items-center gap-1 mt-1">
                <TrendingUp className="w-3 h-3 text-green-400" />
                <span className="text-xs text-green-400">Great progress!</span>
              </div>
            </div>
            <div className="p-3 bg-amber-500/20 rounded-lg">
              <Calendar className="w-6 h-6 text-amber-400" />
            </div>
          </div>
        </GlassCard>
      </div>

      {/* Main Content Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Activity Chart */}
        <GlassCard className="lg:col-span-2 min-h-[400px]">
          <h2 className="text-xl font-semibold mb-6">Activity Performance</h2>
          <div className="flex items-end justify-between h-64 gap-2 px-4">
            {activityData.map((data) => (
              <div key={data.day} className="flex-1 flex flex-col items-center gap-2">
                <div className="w-full flex flex-col items-center gap-1" style={{ height: '200px' }}>
                  {/* Connects bar (smaller, on top) */}
                  <div
                    className="w-3 bg-green-500 rounded-t transition-all"
                    style={{
                      height: maxDials > 0 ? `${(data.connects / maxDials) * 200}px` : '0px',
                      marginTop: 'auto'
                    }}
                    title={`${data.connects} connects`}
                  />
                  {/* Dials bar */}
                  <div
                    className="w-full max-w-[40px] bg-gradient-to-t from-blue-600 to-blue-400 rounded-t transition-all"
                    style={{
                      height: maxDials > 0 ? `${(data.dials / maxDials) * 200}px` : '0px',
                    }}
                    title={`${data.dials} dials`}
                  />
                </div>
                <span className="text-xs text-slate-500">{data.day}</span>
              </div>
            ))}
          </div>
          <div className="flex justify-center gap-6 mt-4 pt-4 border-t border-slate-800">
            <div className="flex items-center gap-2">
              <div className="w-3 h-3 bg-blue-500 rounded" />
              <span className="text-xs text-slate-400">Dials</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-3 h-3 bg-green-500 rounded" />
              <span className="text-xs text-slate-400">Connects</span>
            </div>
          </div>
        </GlassCard>

        {/* Lead Status */}
        <GlassCard className="min-h-[400px]">
          <h2 className="text-xl font-semibold mb-4">Pipeline Status</h2>
          <div className="space-y-4">
            {pipelineStages.map((item) => (
              <div key={item.stage} className="group">
                <div className="flex items-center justify-between mb-2">
                  <span className="text-sm font-medium text-slate-300">{item.stage}</span>
                  <span className="text-sm font-bold text-white">{item.count}</span>
                </div>
                <div className="h-2 bg-slate-800 rounded-full overflow-hidden">
                  <div
                    className={`h-full ${item.color} transition-all group-hover:opacity-80`}
                    style={{ width: `${totalLeads > 0 ? (item.count / totalLeads) * 100 : 0}%` }}
                  />
                </div>
              </div>
            ))}
          </div>
          <div className="mt-6 pt-4 border-t border-slate-800">
            <div className="flex justify-between text-sm">
              <span className="text-slate-400">Total in Pipeline</span>
              <span className="text-white font-bold">
                {totalLeads} leads
              </span>
            </div>
          </div>
        </GlassCard>
      </div>
    </main>
  );
}

'use client';

import React, { useState, useEffect, useRef } from 'react';
import { GlassCard } from '@/components/ui/GlassCard';
import { ResourcesService } from '@/lib/firebase/resources';
import { Resource } from '@/types';
import { useAuth } from '@/components/providers/AuthProvider';
import {
    Mic, MicOff, Radio, Zap, ExternalLink,
    AlertTriangle, Shield, CheckCircle, Search,
    Terminal, Activity, Sparkles, XCircle
} from 'lucide-react';
import { toast } from 'sonner';

export default function RealityLinkPage() {
    const { user } = useAuth();
    const [isListening, setIsListening] = useState(false);
    const [transcript, setTranscript] = useState('');
    const [companyResources, setCompanyResources] = useState<Resource[]>([]);
    const [activeCards, setActiveCards] = useState<Resource[]>([]);
    const [aiTactics, setAiTactics] = useState<string[]>([]);
    const [activeLead, setActiveLead] = useState({ name: 'Jordan Smith', company: 'Global Tech Inc.', value: '$45,000', status: 'Negotiation' });
    const [loading, setLoading] = useState(true);

    // Simulating "Live" typing
    const typingInterval = useRef<NodeJS.Timeout | null>(null);

    // Fetch resources on mount
    useEffect(() => {
        async function fetchResources() {
            if (!user) return;
            try {
                // Determine potential "Battle Cards" from shared resources
                const data = await ResourcesService.getCompanyResources();
                setCompanyResources(data);
            } catch (e) {
                console.error(e);
            } finally {
                setLoading(false);
            }
        }
        fetchResources();
    }, [user]);

    // Matching Logic
    useEffect(() => {
        if (!transcript) return;

        // Simple fuzzy match: Check if resource title or key words appear in transcript
        const lastSentence = transcript.split('.').pop()?.toLowerCase() || '';

        const matched = companyResources.filter(res => {
            // Match against title
            if (lastSentence.includes(res.title.toLowerCase())) return true;
            // Match against category
            if (lastSentence.includes(res.category.toLowerCase())) return true;

            // Heuristic matches (hardcoded for demo flair)
            if (res.title.toLowerCase().includes('competitor') && lastSentence.includes('competitor')) return true;
            if (res.title.toLowerCase().includes('security') && lastSentence.includes('security')) return true;
            if (res.title.toLowerCase().includes('pricing') && (lastSentence.includes('price') || lastSentence.includes('cost'))) return true;

            return false;
        });

        // Add matches to active cards if not already present
        if (matched.length > 0) {
            setActiveCards(prev => {
                const newCards = matched.filter(m => !prev.find(p => p.id === m.id));
                if (newCards.length > 0) {
                    // Flash effect
                    toast(`${newCards.length} Battle Card${newCards.length > 1 ? 's' : ''} Detected!`);
                }
                return [...newCards, ...prev].slice(0, 5); // Keep top 5
            });

            // Generate "Tactics" based on matches
            const newTactics: string[] = [];
            matched.forEach(res => {
                if (res.title.toLowerCase().includes('competitor')) {
                    newTactics.push("Strategy: Use 'The Differentiator' script to highlight our unique AI moat.");
                }
                if (res.category.toLowerCase().includes('pricing')) {
                    newTactics.push("Tactic: Shift focus to 'Cost of Inaction' rather than Implementation Fee.");
                }
                if (res.title.toLowerCase().includes('security')) {
                    newTactics.push("Advice: Mention our dual-layer encryption and ISO compliance immediately.");
                }
            });
            if (newTactics.length > 0) {
                setAiTactics(prev => [...new Set([...newTactics, ...prev])].slice(0, 3));
            }
        }

    }, [transcript, companyResources]);

    // Simuation Scripts
    const startSimulation = (type: 'competitor' | 'security' | 'pricing') => {
        setIsListening(true);
        setTranscript('');
        setActiveCards([]);

        let script = '';
        if (type === 'competitor') {
            script = "Yeah so we've been looking at Competitor X recently. They seem to have good features. Also I heard their pricing is lower.";
        } else if (type === 'security') {
            script = "My main concern right now is compliance. We have strict ISO 27001 requirements. How do you handle data security?";
        } else if (type === 'pricing') {
            script = "Basically we don't have budget for this. Your implementation costs are just too high compared to what we're paying now.";
        }

        let i = 0;
        if (typingInterval.current) clearInterval(typingInterval.current);

        typingInterval.current = setInterval(() => {
            if (i < script.length) {
                setTranscript(prev => prev + script.charAt(i));
                i++;
            } else {
                if (typingInterval.current) clearInterval(typingInterval.current);
                setIsListening(false);
            }
        }, 50); // Typing speed
    };

    const toggleListening = () => {
        setIsListening(!isListening);
        if (!isListening) {
            setTranscript('');
            setActiveCards([]);
        } else {
            if (typingInterval.current) clearInterval(typingInterval.current);
        }
    };

    return (
        <div className="min-h-screen bg-[#050b14] text-slate-200 font-sans p-6 overflow-hidden flex flex-col relative">

            {/* Background Effects */}
            <div className="absolute inset-0 z-0 opacity-20 pointer-events-none">
                <div className="absolute top-0 inset-x-0 h-px bg-gradient-to-r from-transparent via-blue-500 to-transparent"></div>
                <div className="absolute bottom-0 inset-x-0 h-px bg-gradient-to-r from-transparent via-purple-500 to-transparent"></div>
                <div className="absolute inset-y-0 left-0 w-px bg-gradient-to-b from-transparent via-blue-500 to-transparent"></div>
                <div className="absolute inset-y-0 right-0 w-px bg-gradient-to-b from-transparent via-purple-500 to-transparent"></div>
            </div>

            {/* Header HUD */}
            <header className="relative z-10 flex justify-between items-center mb-8 pb-4 border-b border-white/5">
                <div className="flex items-center gap-4">
                    <div className="relative">
                        <div className={`w-3 h-3 rounded-full ${isListening ? 'bg-red-500 animate-pulse' : 'bg-slate-600'}`}></div>
                        {isListening && <div className="absolute inset-0 rounded-full bg-red-500 animate-ping opacity-50"></div>}
                    </div>
                    <div>
                        <h1 className="text-2xl font-bold text-white tracking-widest uppercase flex items-center gap-2">
                            Reality Link <span className="text-xs bg-blue-900/50 text-blue-400 px-2 py-0.5 rounded border border-blue-500/30">V.1.0</span>
                        </h1>
                        <p className="text-xs text-slate-500 font-mono">LIVE AUDIO INTELLIGENCE FEED</p>
                    </div>
                </div>

                <div className="flex items-center gap-4">
                    <div className="hidden md:flex items-center gap-6 px-4 py-2 bg-slate-900/50 border border-white/5 rounded-full">
                        <div className="text-center">
                            <div className="text-[10px] text-slate-500 uppercase tracking-wider">Latency</div>
                            <div className="text-sm font-mono text-green-400">12ms</div>
                        </div>
                        <div className="w-px h-6 bg-white/10"></div>
                        <div className="text-center">
                            <div className="text-[10px] text-slate-500 uppercase tracking-wider">Resources</div>
                            <div className="text-sm font-mono text-blue-400">{companyResources.length}</div>
                        </div>
                    </div>
                </div>
            </header>

            <div className="flex-1 grid grid-cols-1 lg:grid-cols-12 gap-6 relative z-10 min-h-0">

                {/* Visualizer & Transcript (Left) */}
                <div className="lg:col-span-6 flex flex-col gap-6">

                    {/* Visualizer Area */}
                    <div className="h-48 rounded-2xl border border-white/10 bg-slate-900/50 backdrop-blur relative overflow-hidden flex items-center justify-center group">
                        {isListening ? (
                            <div className="flex items-center gap-1 h-20">
                                {[...Array(20)].map((_, i) => (
                                    <div
                                        key={i}
                                        className="w-2 bg-blue-500/80 rounded-full animate-wave"
                                        style={{
                                            height: `${Math.random() * 100}%`,
                                            animationDuration: `${0.5 + Math.random() * 0.5}s`
                                        }}
                                    ></div>
                                ))}
                            </div>
                        ) : (
                            <div className="text-center">
                                <MicOff className="w-12 h-12 text-slate-700 mx-auto mb-2" />
                                <div className="text-slate-500 font-mono text-sm">AUDIO FEED OFFLINE</div>
                            </div>
                        )}

                        {/* Status Overlay */}
                        <div className="absolute top-4 left-4 flex gap-2">
                            <button
                                onClick={toggleListening}
                                className={`px-3 py-1.5 rounded-full text-xs font-bold uppercase tracking-wider border transition-all ${isListening
                                    ? 'bg-red-500/20 text-red-400 border-red-500/50 hover:bg-red-500/30'
                                    : 'bg-green-500/20 text-green-400 border-green-500/50 hover:bg-green-500/30'}`}
                            >
                                {isListening ? 'Stop Feed' : 'Start Feed'}
                            </button>
                        </div>
                    </div>

                    {/* Simulation Controls (Demo) */}
                    <div className="flex gap-3 overflow-x-auto pb-2">
                        <button onClick={() => startSimulation('competitor')} className="px-4 py-2 rounded-lg bg-slate-800 border border-slate-700 hover:border-blue-500 hover:text-blue-400 text-xs font-mono uppercase transition-colors whitespace-nowrap">
                            Simulate: Competitor Mention
                        </button>
                        <button onClick={() => startSimulation('security')} className="px-4 py-2 rounded-lg bg-slate-800 border border-slate-700 hover:border-orange-500 hover:text-orange-400 text-xs font-mono uppercase transition-colors whitespace-nowrap">
                            Simulate: Security Audit
                        </button>
                        <button onClick={() => startSimulation('pricing')} className="px-4 py-2 rounded-lg bg-slate-800 border border-slate-700 hover:border-green-500 hover:text-green-400 text-xs font-mono uppercase transition-colors whitespace-nowrap">
                            Simulate: Pricing Objection
                        </button>
                    </div>

                    {/* Transcript Log */}
                    <div className="flex-1 bg-black/40 border border-white/5 rounded-2xl p-6 relative overflow-hidden">
                        <div className="absolute top-4 right-4 text-[10px] font-mono text-slate-600 uppercase tracking-widest flex items-center gap-2">
                            <Terminal size={12} /> Transcript Log
                        </div>
                        <div className="font-mono text-lg text-slate-300 leading-relaxed whitespace-pre-wrap">
                            {transcript || <span className="text-slate-700 animate-pulse">Waiting for audio speech...</span>}
                            {isListening && <span className="inline-block w-2.5 h-5 bg-blue-500 ml-1 animate-blink align-middle"></span>}
                        </div>
                    </div>

                </div>

                {/* AI Strategy Stream & Lead Context (Center/Right) */}
                <div className="lg:col-span-3 flex flex-col gap-6 h-full min-h-0">
                    <h2 className="text-sm font-bold text-slate-400 opacity-50 uppercase tracking-widest flex items-center gap-2">
                        <Activity size={14} className="text-blue-500" />
                        AI Strategy Stream
                    </h2>

                    <div className="flex flex-col gap-4">
                        {aiTactics.length === 0 ? (
                            <div className="p-4 rounded-xl border border-white/5 bg-white/5 text-slate-600 text-xs font-mono italic animate-pulse">
                                ANALYZING DEAL DYNAMICS...
                            </div>
                        ) : (
                            aiTactics.map((tactic, i) => (
                                <div key={i} className="p-4 rounded-xl border border-blue-500/20 bg-blue-500/5 animate-in slide-in-from-top-2">
                                    <div className="flex items-center gap-2 mb-1">
                                        <Sparkles size={12} className="text-blue-400" />
                                        <span className="text-[10px] font-bold text-blue-400 uppercase">Live Intelligence</span>
                                    </div>
                                    <p className="text-xs text-slate-300 font-medium leading-relaxed">{tactic}</p>
                                </div>
                            ))
                        )}
                    </div>

                    <h2 className="text-sm font-bold text-slate-400 opacity-50 uppercase tracking-widest mt-4">Active Lead Context</h2>
                    <GlassCard className="!p-4 border-l-2 border-l-blue-500">
                        <div className="flex items-center gap-3 mb-4">
                            <div className="w-10 h-10 rounded-full bg-slate-800 flex items-center justify-center text-white font-bold">
                                JS
                            </div>
                            <div>
                                <div className="text-sm font-bold text-white">{activeLead.name}</div>
                                <div className="text-[10px] text-slate-500">{activeLead.company}</div>
                            </div>
                        </div>
                        <div className="grid grid-cols-2 gap-4">
                            <div>
                                <div className="text-[10px] text-slate-500 uppercase">Deal Value</div>
                                <div className="text-sm font-mono text-green-400 font-bold">{activeLead.value}</div>
                            </div>
                            <div>
                                <div className="text-[10px] text-slate-500 uppercase">Stage</div>
                                <div className="text-sm text-blue-400 font-bold">{activeLead.status}</div>
                            </div>
                        </div>
                    </GlassCard>
                </div>

                {/* Battle Cards (Right) */}
                <div className="lg:col-span-3 flex flex-col h-full min-h-0">
                    <h2 className="text-sm font-bold text-slate-400 uppercase tracking-widest mb-4 flex items-center justify-between">
                        <span>Live Battle Cards</span>
                        <span className="bg-blue-600 text-white text-[10px] px-1.5 py-0.5 rounded-full">{activeCards.length}</span>
                    </h2>

                    <div className="flex-1 overflow-y-auto space-y-4 pr-2 custom-scrollbar">
                        {activeCards.length === 0 ? (
                            <div className="h-64 border-2 border-dashed border-slate-800 rounded-xl flex flex-col items-center justify-center text-center p-6 opacity-50">
                                <Search className="w-12 h-12 text-slate-700 mb-2" />
                                <div className="text-slate-500 font-medium">Scanning for Context...</div>
                                <p className="text-xs text-slate-600 mt-2">AI will surface relevant enablement materials here automatically.</p>
                            </div>
                        ) : (
                            activeCards.map((card, idx) => (
                                <div
                                    key={card.id}
                                    className="animate-in slide-in-from-right-4 fade-in duration-500 fill-mode-forwards"
                                    style={{ animationDelay: `${idx * 100}ms` }}
                                >
                                    <div className="bg-slate-900/80 border border-blue-500/30 rounded-xl p-4 shadow-[0_0_20px_rgba(59,130,246,0.15)] relative overflow-hidden group hover:border-blue-500/60 transition-colors">

                                        {/* Glow Effect */}
                                        <div className="absolute top-0 right-0 w-24 h-24 bg-blue-500/10 blur-2xl -translate-y-1/2 translate-x-1/2 rounded-full pointer-events-none"></div>

                                        <div className="flex justify-between items-start mb-2 relative z-10">
                                            <div className="px-2 py-0.5 rounded bg-blue-500/10 text-blue-400 text-[10px] font-bold uppercase border border-blue-500/20">
                                                {card.category} Match
                                            </div>
                                            <button
                                                onClick={() => setActiveCards(prev => prev.filter(p => p.id !== card.id))}
                                                className="text-slate-600 hover:text-white transition-colors"
                                            >
                                                <XCircle size={16} />
                                            </button>
                                        </div>

                                        <h3 className="text-lg font-bold text-white mb-1 group-hover:text-blue-300 transition-colors">{card.title}</h3>
                                        <p className="text-xs text-slate-400 mb-4 line-clamp-2">{card.description}</p>

                                        <a
                                            href={card.url}
                                            target="_blank"
                                            rel="noreferrer"
                                            className="w-full flex items-center justify-center gap-2 py-2 bg-blue-600 hover:bg-blue-500 text-white text-xs font-bold uppercase rounded-lg transition-all shadow-lg hover:shadow-blue-500/25"
                                        >
                                            <ExternalLink size={14} /> Open Resource
                                        </a>

                                    </div>
                                </div>
                            ))
                        )}
                    </div>
                </div>

            </div>

            <style jsx>{`
                @keyframes blink {
                    0%, 100% { opacity: 1; }
                    50% { opacity: 0; }
                }
                .animate-blink {
                    animation: blink 1s step-end infinite;
                }
                @keyframes wave {
                    0%, 100% { height: 10%; }
                    50% { height: 100%; }
                }
                .animate-wave {
                    animation-iteration-count: infinite;
                    animation-timing-function: ease-in-out;
                }
            `}</style>
        </div>
    );
}

'use client';

import React, { useState, useEffect } from 'react';
import { GlassCard } from '@/components/ui/GlassCard';
import { Badge } from '@/components/ui/Badge';
import { Resource, ResourceCategory } from '@/types';
import { MOCK_RESOURCES } from '@/lib/mock-data/resources'; // Keep for defaults
import { ResourcesService } from '@/lib/firebase/resources';
import { useAuth } from '@/components/providers/AuthProvider';
import { Search, FileText, Video, Link as LinkIcon, Download, FileSpreadsheet, Presentation, Plus, X, UploadCloud, Loader2 } from 'lucide-react';
import { toast } from 'sonner';

const CATEGORIES: ResourceCategory[] = [
    'Playbook', 'Learning', 'Prospecting', 'Templates',
    'Competitive', 'Operational', 'Scheduling', 'Brand', 'Insights'
];

export default function ResourcesClient() {
    const { user } = useAuth();
    const [activeTab, setActiveTab] = useState<'mine' | 'company'>('mine');
    const [activeCategory, setActiveCategory] = useState<ResourceCategory | 'All'>('All');
    const [searchQuery, setSearchQuery] = useState('');

    const [userResources, setUserResources] = useState<Resource[]>([]);
    const [companyResources, setCompanyResources] = useState<Resource[]>([]);
    const [loading, setLoading] = useState(true);
    const [isUploadOpen, setIsUploadOpen] = useState(false);

    // Upload State
    const [uploadFile, setUploadFile] = useState<File | null>(null);
    const [uploadCategory, setUploadCategory] = useState<ResourceCategory>('Playbook');
    const [uploadDesc, setUploadDesc] = useState('');
    const [uploadVisibility, setUploadVisibility] = useState<'private' | 'company'>('private');
    const [isUploading, setIsUploading] = useState(false);

    // Fetch Resources
    useEffect(() => {
        async function fetchResources() {
            setLoading(true);
            try {
                // Always fetch company resources
                const companyData = await ResourcesService.getCompanyResources();
                setCompanyResources(companyData);

                // Fetch user resources if logged in
                if (user?.uid) {
                    const userData = await ResourcesService.getUserResources(user.uid);
                    setUserResources(userData);
                }
            } catch (error) {
                console.error("Failed to fetch resources:", error);
                toast.error("Failed to load resources.");
            } finally {
                setLoading(false);
            }
        }

        fetchResources();
    }, [user?.uid]);

    const handleUpload = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!uploadFile || !user?.uid) return;

        setIsUploading(true);
        try {
            await ResourcesService.uploadResource(user.uid, uploadFile, uploadCategory, uploadDesc, uploadVisibility);
            toast.success("Resource uploaded successfully!");

            // Refresh list
            if (user?.uid) {
                const userData = await ResourcesService.getUserResources(user.uid);
                setUserResources(userData);
            }
            const companyData = await ResourcesService.getCompanyResources();
            setCompanyResources(companyData);

            // Reset form
            setUploadFile(null);
            setUploadDesc('');
            setUploadVisibility('private');
            setIsUploadOpen(false);
        } catch (error) {
            console.error("Upload failed:", error);
            toast.error("Failed to upload resource.");
        } finally {
            setIsUploading(false);
        }
    };

    const displayResources = activeTab === 'mine' ? userResources : companyResources;

    // Merge Mock data (System Resources) with User Resources ONLY if in Company View? 
    // Actually, MOCK_RESOURCES are usually global, so let's put them in Company view.
    const allResources = activeTab === 'company' ? [...displayResources, ...MOCK_RESOURCES] : displayResources;

    const filteredResources = allResources.filter(resource => {
        const matchesCategory = activeCategory === 'All' || resource.category === activeCategory;
        const matchesSearch = resource.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
            resource.description.toLowerCase().includes(searchQuery.toLowerCase());
        return matchesCategory && matchesSearch;
    });

    const getIcon = (type: string) => {
        switch (type) {
            case 'video': return <Video className="w-5 h-5 text-red-400" />;
            case 'deck': return <Presentation className="w-5 h-5 text-orange-400" />;
            case 'sheet': return <FileSpreadsheet className="w-5 h-5 text-green-400" />;
            case 'link': return <LinkIcon className="w-5 h-5 text-blue-400" />;
            default: return <FileText className="w-5 h-5 text-slate-300" />;
        }
    };

    return (
        <div className="flex h-screen bg-[#0b1121]">
            {/* Category Sidebar */}
            <aside className="w-64 border-r border-slate-800 p-6 flex-shrink-0">
                <h2 className="text-xl font-bold text-white mb-6 px-2">Knowledge Hub</h2>
                <div className="space-y-1">
                    <button
                        onClick={() => setActiveCategory('All')}
                        className={`w-full text-left px-3 py-2 rounded-md text-sm font-medium transition-colors ${activeCategory === 'All' ? 'bg-blue-600/20 text-blue-300' : 'text-slate-400 hover:text-white hover:bg-white/5'
                            }`}
                    >
                        All Resources
                    </button>

                    <div className="my-4 border-t border-slate-800" />

                    {CATEGORIES.map(category => (
                        <button
                            key={category}
                            onClick={() => setActiveCategory(category)}
                            className={`w-full text-left px-3 py-2 rounded-md text-sm font-medium transition-colors ${activeCategory === category ? 'bg-blue-600/20 text-blue-300' : 'text-slate-400 hover:text-white hover:bg-white/5'
                                }`}
                        >
                            {category}
                        </button>
                    ))}
                </div>
            </aside>

            {/* Main Content */}
            <main className="flex-1 overflow-y-auto p-8 relative">
                <header className="mb-8 flex flex-col md:flex-row justify-between items-start gap-4">
                    <div>
                        <h1 className="text-3xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-pink-400 to-purple-400 mb-2">
                            Enablement & Resources
                        </h1>
                        <p className="text-slate-400 mb-6">Access all sales materials, training, and operational tools.</p>

                        <div className="flex items-center gap-4 mb-4">
                            <div className="p-1 bg-slate-800 rounded-lg flex items-center">
                                <button
                                    onClick={() => setActiveTab('mine')}
                                    className={`px-4 py-2 rounded-md text-sm font-medium transition-all ${activeTab === 'mine' ? 'bg-indigo-600 text-white shadow-lg' : 'text-slate-400 hover:text-white'}`}
                                >
                                    My Resources
                                </button>
                                <button
                                    onClick={() => setActiveTab('company')}
                                    className={`px-4 py-2 rounded-md text-sm font-medium transition-all ${activeTab === 'company' ? 'bg-indigo-600 text-white shadow-lg' : 'text-slate-400 hover:text-white'}`}
                                >
                                    Company Depo
                                </button>
                            </div>
                        </div>

                        <div className="relative max-w-md">
                            <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-500" />
                            <input
                                type="text"
                                placeholder="Search guides, scripts, videos..."
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                className="glass-input pl-10 h-10 text-base w-full"
                            />
                        </div>
                    </div>
                    <button
                        onClick={() => setIsUploadOpen(true)}
                        className="glass-button flex items-center gap-2 bg-blue-600/20 hover:bg-blue-600/40 text-blue-300 whitespace-nowrap"
                    >
                        <Plus className="w-4 h-4" /> Upload Resource
                    </button>
                </header>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {filteredResources.map(resource => (
                        <GlassCard key={resource.id} className="group hover:bg-slate-800/60 transition-colors flex flex-col h-full">
                            <div className="flex justify-between items-start mb-4">
                                <div className="p-2 bg-slate-800 rounded-lg group-hover:bg-slate-700 transition-colors">
                                    {getIcon(resource.type)}
                                </div>
                                <Badge variant="default">{resource.category}</Badge>
                            </div>

                            <h3 className="font-semibold text-lg text-white mb-2 group-hover:text-blue-300 transition-colors">
                                {resource.title}
                            </h3>
                            <p className="text-slate-400 text-sm mb-4 line-clamp-2 flex-grow">
                                {resource.description}
                            </p>

                            <div className="flex items-center justify-between mt-auto pt-4 border-t border-slate-700/50">
                                <span className="text-xs text-slate-500">
                                    {new Date(resource.updatedAt).toLocaleDateString()}
                                </span>
                                <a
                                    href={resource.url}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    className="text-blue-400 hover:text-blue-300 text-sm font-medium flex items-center gap-1"
                                >
                                    Access <Download className="w-3 h-3" />
                                </a>
                            </div>
                        </GlassCard>
                    ))}
                </div>

                {filteredResources.length === 0 && (
                    <div className="text-center py-20 text-slate-500">
                        <p>No resources found matching your search.</p>
                    </div>
                )}
            </main>

            {/* Upload Modal */}
            {isUploadOpen && (
                <div className="fixed inset-0 bg-black/80 backdrop-blur-sm z-50 flex items-center justify-center p-4">
                    <GlassCard className="w-full max-w-md relative animate-in fade-in zoom-in-95 duration-200">
                        <button
                            onClick={() => setIsUploadOpen(false)}
                            className="absolute top-4 right-4 text-slate-400 hover:text-white"
                        >
                            <X className="w-5 h-5" />
                        </button>

                        <h2 className="text-xl font-bold text-white mb-1">Upload Resource</h2>
                        <p className="text-sm text-slate-400 mb-6">Add a document to your knowledge hub.</p>

                        <form onSubmit={handleUpload} className="space-y-4">
                            <div>
                                <label className="block text-sm font-medium text-slate-300 mb-1">Category</label>
                                <select
                                    value={uploadCategory}
                                    onChange={(e) => setUploadCategory(e.target.value as ResourceCategory)}
                                    className="glass-input w-full"
                                >
                                    {CATEGORIES.map(c => <option key={c} value={c}>{c}</option>)}
                                </select>
                            </div>

                            <div>
                                <label className="block text-sm font-medium text-slate-300 mb-1">Description</label>
                                <textarea
                                    value={uploadDesc}
                                    onChange={(e) => setUploadDesc(e.target.value)}
                                    className="glass-input w-full min-h-[80px]"
                                    placeholder="Brief summary..."
                                />
                            </div>

                            <div className="border-2 border-dashed border-slate-700 rounded-lg p-6 text-center hover:border-blue-500/50 transition-colors">
                                <input
                                    type="file"
                                    id="file-upload"
                                    className="hidden"
                                    onChange={(e) => setUploadFile(e.target.files?.[0] || null)}
                                    accept=".pdf,.doc,.docx,.txt,.md"
                                />
                                <label htmlFor="file-upload" className="cursor-pointer block">
                                    <CloudIconOrText file={uploadFile} />
                                </label>
                            </div>

                            <button
                                type="submit"
                                disabled={isUploading || !uploadFile}
                                className="glass-button w-full bg-blue-600/20 hover:bg-blue-600/40 text-blue-300 flex justify-center"
                            >
                                {isUploading ? <Loader2 className="w-5 h-5 animate-spin" /> : 'Upload Resource'}
                            </button>
                        </form>
                    </GlassCard>
                </div>
            )}
        </div>
    );
}

function CloudIconOrText({ file }: { file: File | null }) {
    if (file) {
        return (
            <div className="text-blue-400">
                <FileText className="w-8 h-8 mx-auto mb-2" />
                <p className="text-sm font-medium">{file.name}</p>
                <p className="text-xs text-slate-500 mt-1">Click to change</p>
            </div>
        );
    }
    return (
        <div className="text-slate-400">
            <UploadCloud className="w-8 h-8 mx-auto mb-2" />
            <p className="text-sm">Click to select file</p>
            <p className="text-xs text-slate-500 mt-1">PDF, Docs, Text (Max 10MB)</p>
        </div>
    );
}

'use client';

import React, { useState, useEffect } from 'react';
import { GlassCard } from '@/components/ui/GlassCard';
import { useAuth } from '@/components/providers/AuthProvider';
import { Badge } from '@/components/ui/Badge';
import { CreditCard, ExternalLink, ShieldCheck } from 'lucide-react';
import { isGmailConnected } from '@/lib/gmail/gmail-service';
import { toast } from 'sonner';

async function checkTwilioStatusViaAPI(userId: string): Promise<{ connected: boolean; phoneNumber: string | null }> {
    try {
        const response = await fetch(`/api/twilio/status?userId=${userId}`);
        if (!response.ok) {
            return { connected: false, phoneNumber: null };
        }
        return await response.json();
    } catch {
        return { connected: false, phoneNumber: null };
    }
}

export default function SettingsClient() {
    const { user, profile, loading: authLoading } = useAuth();
    const [gmailConnected, setGmailConnected] = useState(false);
    const [twilioConnected, setTwilioConnected] = useState(false);
    const [twilioPhone, setTwilioPhone] = useState<string | null>(null);
    const [isPortalLoading, setIsPortalLoading] = useState(false);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        if (authLoading) return;
        if (!user) {
            setIsLoading(false);
            return;
        }
        checkConnections();
    }, [user, authLoading]);

    const checkConnections = async () => {
        if (!user) return;
        try {
            const [gmailStatus, twilioStatus] = await Promise.all([
                isGmailConnected(user.uid),
                checkTwilioStatusViaAPI(user.uid),
            ]);
            setGmailConnected(gmailStatus);
            setTwilioConnected(twilioStatus?.connected ?? false);
            setTwilioPhone(twilioStatus?.phoneNumber ?? null);
        } catch (error) {
            console.error('Error checking connections:', error);
        } finally {
            setIsLoading(false);
        }
    };

    const handleConnectGmail = () => {
        if (!user) {
            toast.error('Please log in first');
            return;
        }
        window.location.href = `/api/auth/gmail?userId=${user.uid}`;
    };

    const handleManageBilling = async () => {
        if (!user) return;
        setIsPortalLoading(true);
        try {
            const response = await fetch('/api/stripe/portal', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ userId: user.uid, customerId: (profile as any)?.stripeCustomerId }),
            });
            const data = await response.json();
            if (data.url) {
                window.location.href = data.url;
            } else {
                toast.error('Could not open billing portal');
            }
        } catch (error) {
            toast.error('Billing portal service unavailable');
            console.error(error);
        } finally {
            setIsPortalLoading(false);
        }
    };

    if (authLoading || isLoading) {
        return (
            <div className="p-8 min-h-screen flex items-center justify-center">
                <div className="text-slate-500 italic animate-pulse">Synchronizing configurations...</div>
            </div>
        );
    }

    if (!user) return null;

    return (
        <div className="p-8 min-h-screen">
            <header className="mb-8">
                <h1 className="text-3xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-white to-slate-400">
                    Settings & Account
                </h1>
                <p className="text-slate-500 text-sm mt-1">Configure your professional workspace and commercial integrations</p>
            </header>

            <div className="grid gap-6 max-w-2xl">
                {/* Billing Logic... (Previous cards remain same) */}
                <GlassCard className="border-l-4 border-l-indigo-500">
                    <div className="flex items-start justify-between">
                        <div className="flex items-center gap-4">
                            <div className="w-12 h-12 rounded-xl bg-gradient-to-br from-indigo-500 to-violet-600 flex items-center justify-center text-white">
                                <CreditCard size={24} />
                            </div>
                            <div>
                                <h3 className="text-lg font-semibold text-white">Subscription & Billing</h3>
                                <div className="flex items-center gap-2 mt-1">
                                    <span className="text-sm text-slate-400">Current Plan:</span>
                                    <Badge className="bg-indigo-500/20 text-indigo-300 border-indigo-500/30 uppercase text-[10px] font-bold tracking-wider">
                                        {profile?.tier || 'Free'}
                                    </Badge>
                                </div>
                            </div>
                        </div>
                        <button
                            onClick={handleManageBilling}
                            disabled={isPortalLoading}
                            className="flex items-center gap-2 px-4 py-2 bg-white/5 hover:bg-white/10 text-white rounded-lg text-sm font-medium border border-white/10 transition-all disabled:opacity-50"
                        >
                            {isPortalLoading ? 'Loading...' : 'Manage Billing'}
                            <ExternalLink size={14} />
                        </button>
                    </div>
                </GlassCard>

                <GlassCard>
                    <div className="flex items-start justify-between">
                        <div className="flex items-center gap-4">
                            <div className="w-12 h-12 rounded-xl bg-gradient-to-br from-red-500 to-red-600 flex items-center justify-center text-2xl"></div>
                            <div>
                                <h3 className="text-lg font-semibold text-white">Gmail Integration</h3>
                                <p className="text-sm text-slate-400">Sync emails with leads, track replies, and send emails directly</p>
                            </div>
                        </div>
                        <div className="flex items-center gap-3">
                            {gmailConnected ? (
                                <span className="text-sm text-green-400 font-medium">Connected</span>
                            ) : (
                                <button onClick={handleConnectGmail} className="glass-button px-4 py-2 text-xs">Connect Gmail</button>
                            )}
                        </div>
                    </div>
                </GlassCard>

                <GlassCard>
                    <div className="flex items-start justify-between">
                        <div className="flex items-center gap-4">
                            <div className="w-12 h-12 rounded-xl bg-gradient-to-br from-green-500 to-emerald-600 flex items-center justify-center text-2xl"></div>
                            <div>
                                <h3 className="text-lg font-semibold text-white">Twilio Telephony</h3>
                                <p className="text-sm text-slate-400">Omnichannel communication hub for professional outreach</p>
                            </div>
                        </div>
                        <div className="flex items-center gap-3">
                            {twilioConnected ? (
                                <Badge variant="success">Active: {twilioPhone}</Badge>
                            ) : (
                                <Badge variant="warning">Setup Required</Badge>
                            )}
                        </div>
                    </div>
                </GlassCard>
            </div>
        </div>
    );
}

