                                    </div>
                                ))}
                            </div>
                        </GlassCard>
                    ) : (
                        <GlassCard className="flex items-center justify-center min-h-[400px]">
                            <div className="text-center">
                                <BookOpen className="w-12 h-12 text-slate-600 mx-auto mb-4" />
                                <p className="text-slate-500">Select a course to view modules</p>
                            </div>
                        </GlassCard>
                    )}
                </div>
            </div>
        </div>
    );
}

'use client';

import React, { useState, useEffect } from 'react';
import { Lead } from '@/types';
import { useAuth } from '@/components/providers/AuthProvider';
import { generateEmail, enhanceEmail, EnhancementAction } from '@/lib/firebase/ai-service';
import { ActivitiesService, EmailThreadsService } from '@/lib/firebase/services';
import { toast } from 'sonner';

interface AIEmailDraftProps {
    lead: Lead;
    isOpen: boolean;
    onClose: () => void;
}

const ENHANCEMENT_ACTIONS: { action: EnhancementAction; icon: string; label: string }[] = [
    { action: 'shorter', icon: '', label: 'Shorter' },
    { action: 'professional', icon: '', label: 'Professional' },
    { action: 'friendly', icon: '', label: 'Friendly' },
    { action: 'urgency', icon: '', label: 'Add Urgency' },
    { action: 'social-proof', icon: '', label: 'Add Proof' },
];

export function AIEmailDraft({ lead, isOpen, onClose }: AIEmailDraftProps) {
    const { user } = useAuth();
    const [subject, setSubject] = useState('');
    const [email, setEmail] = useState('');
    const [customPrompt, setCustomPrompt] = useState('');
    const [isGenerating, setIsGenerating] = useState(false);
    const [isEnhancing, setIsEnhancing] = useState<EnhancementAction | null>(null);
    const [copied, setCopied] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const [showCustomPrompt, setShowCustomPrompt] = useState(false);

    const senderName = user?.displayName || 'Sales Team';
    const senderEmail = user?.email || '';

    useEffect(() => {
        if (isOpen && !email) {
            handleGenerate();
        }
    }, [isOpen]);

    const handleGenerate = async (prompt?: string) => {
        setIsGenerating(true);
        setError(null);
        try {
            const result = await generateEmail(lead, senderName, prompt);
            setSubject(result.subject);
            setEmail(result.body);
        } catch (err) {
            setError('Failed to generate email. Using template instead.');
        } finally {
            setIsGenerating(false);
        }
    };

    const handleEnhance = async (action: EnhancementAction) => {
        if (!email) return;
        setIsEnhancing(action);
        setError(null);
        try {
            const enhanced = await enhanceEmail(email, action, lead);
            setEmail(enhanced);
            toast.success(`Email made ${action}`);
        } catch (err) {
            setError('Enhancement failed. Please try again.');
        } finally {
            setIsEnhancing(null);
        }
    };

    const handleCopy = () => {
        navigator.clipboard.writeText(`Subject: ${subject}\n\n${email}`);
        setCopied(true);
        toast.success('Copied to clipboard');
        setTimeout(() => setCopied(false), 2000);
    };

    const handleSendEmail = async () => {
        // Log the email activity if user is logged in
        if (user) {
            try {
                // Create email thread record
                await EmailThreadsService.createThread(user.uid, {
                    leadId: lead.id,
                    subject,
                    body: email,
                    status: 'sent',
                    sentAt: Date.now(),
                    aiGenerated: true
                });

                // Log activity
                await ActivitiesService.logEmail(user.uid, lead.id, subject, email.substring(0, 200));
                toast.success('Email logged to activity history');
            } catch (error) {
                console.error('Failed to log email:', error);
            }
        }

        // Open email client
        const mailtoUrl = `mailto:${lead.email}?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(email)}`;
        window.open(mailtoUrl, '_blank');
    };

    const handleCustomGenerate = () => {
        if (customPrompt.trim()) {
            handleGenerate(customPrompt);
            setShowCustomPrompt(false);
            setCustomPrompt('');
        }
    };

    if (!isOpen) return null;

    return (
        <div className="fixed inset-0 z-50 flex items-center justify-center p-4">
            {/* Backdrop */}
            <div
                className="absolute inset-0 bg-black/70 backdrop-blur-sm"
                onClick={onClose}
            />

            {/* Modal */}
            <div className="relative bg-slate-900 border border-slate-700 rounded-2xl w-full max-w-2xl shadow-2xl max-h-[90vh] overflow-hidden flex flex-col">
                {/* Header */}
                <div className="p-6 border-b border-slate-800">
                    <div className="flex items-center justify-between">
                        <div className="flex items-center gap-3">
                            <div className="w-10 h-10 rounded-full bg-gradient-to-br from-violet-500 to-fuchsia-500 flex items-center justify-center">
                                <span className="text-lg"></span>
                            </div>
                            <div>
                                <h2 className="text-xl font-bold text-white">AI Email Draft</h2>
                                <p className="text-sm text-slate-400">For {lead.companyName}  {lead.status}</p>
                            </div>
                        </div>
                        <button
                            onClick={onClose}
                            className="text-slate-400 hover:text-white transition-colors text-2xl"
                        >
                            
                        </button>
                    </div>

                    {/* Sender/Recipient Info */}
                    <div className="mt-4 flex items-center gap-4 text-sm">
                        <div className="flex items-center gap-2">
                            <span className="text-slate-500">From:</span>
                            <span className="text-slate-300">{senderEmail || 'Not logged in'}</span>
                        </div>
                        <div className="flex items-center gap-2">
                            <span className="text-slate-500">To:</span>
                            <span className="text-slate-300">{lead.email}</span>
                        </div>
                    </div>
                </div>

                {/* Content */}
                <div className="flex-1 overflow-y-auto p-6">
                    {isGenerating ? (
                        <div className="h-64 flex items-center justify-center">
                            <div className="text-center">
                                <div className="w-10 h-10 border-2 border-violet-500 border-t-transparent rounded-full animate-spin mx-auto mb-3" />
                                <p className="text-slate-400">AI is crafting your email...</p>
                                <p className="text-xs text-slate-600 mt-1">Using company playbooks & lead context</p>
                            </div>
                        </div>
                    ) : (
                        <>
                            {error && (
                                <div className="mb-4 p-3 bg-amber-500/10 border border-amber-500/30 rounded-lg text-amber-400 text-sm">
                                    {error}
                                </div>
                            )}

                            {/* Custom Prompt Input */}
                            {showCustomPrompt && (
                                <div className="mb-4 p-4 bg-slate-800/50 border border-slate-700 rounded-lg">
                                    <label className="block text-sm text-slate-400 mb-2">What should the email focus on?</label>
                                    <textarea
                                        value={customPrompt}
                                        onChange={(e) => setCustomPrompt(e.target.value)}
                                        placeholder="E.g., 'Focus on cost savings and mention their Q1 budget planning'"
                                        className="glass-input w-full h-20 resize-none text-sm"
                                    />
                                    <div className="flex gap-2 mt-2">
                                        <button
                                            onClick={handleCustomGenerate}
                                            className="glass-button px-4 py-1.5 text-xs"
                                        >
                                            Generate
                                        </button>
                                        <button
                                            onClick={() => setShowCustomPrompt(false)}
                                            className="text-slate-400 hover:text-white text-xs"
                                        >
                                            Cancel
                                        </button>
                                    </div>
                                </div>
                            )}

                            {/* Subject Line */}
                            <div className="mb-4">
                                <label className="block text-sm text-slate-400 mb-2">Subject Line</label>
                                <input
                                    type="text"
                                    value={subject}
                                    onChange={(e) => setSubject(e.target.value)}
                                    className="glass-input w-full"
                                />
                            </div>

                            {/* AI Enhancement Buttons */}
                            <div className="mb-4">
                                <div className="flex items-center justify-between mb-2">
                                    <label className="text-sm text-slate-400">AI Enhancements</label>
                                    <button
                                        onClick={() => setShowCustomPrompt(true)}
                                        className="text-xs text-violet-400 hover:text-violet-300"
                                    >
                                        + Custom prompt
                                    </button>
                                </div>
                                <div className="flex flex-wrap gap-2">
                                    {ENHANCEMENT_ACTIONS.map(({ action, icon, label }) => (
                                        <button
                                            key={action}
                                            onClick={() => handleEnhance(action)}
                                            disabled={isEnhancing !== null}
                                            className={`px-3 py-1.5 text-xs rounded-lg border transition-all flex items-center gap-1.5
                                                ${isEnhancing === action
                                                    ? 'bg-violet-500/20 border-violet-500/50 text-violet-300'
                                                    : 'bg-slate-800/50 border-slate-700 text-slate-400 hover:bg-slate-700 hover:text-white hover:border-slate-600'}
                                                ${isEnhancing !== null && isEnhancing !== action ? 'opacity-50 cursor-not-allowed' : ''}
                                            `}
                                        >
                                            {isEnhancing === action ? (
                                                <div className="w-3 h-3 border border-violet-300 border-t-transparent rounded-full animate-spin" />
                                            ) : (
                                                <span>{icon}</span>
                                            )}
                                            {label}
                                        </button>
                                    ))}
                                </div>
                            </div>

                            {/* Email Body */}
                            <div className="mb-4">
                                <label className="block text-sm text-slate-400 mb-2">Email Body</label>
                                <textarea
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    className="glass-input w-full h-56 resize-none font-mono text-sm leading-relaxed"
                                />
                            </div>
                        </>
                    )}
                </div>

                {/* Actions Footer */}
                {!isGenerating && (
                    <div className="p-6 border-t border-slate-800 bg-slate-900/80">
                        <div className="flex justify-between items-center">
                            <button
                                onClick={() => handleGenerate()}
                                className="px-4 py-2 text-slate-400 hover:text-white transition-colors flex items-center gap-2"
                            >
                                <span></span> Regenerate
                            </button>
                            <div className="flex gap-3">
                                <button
                                    onClick={handleCopy}
                                    className="px-4 py-2 bg-slate-800 hover:bg-slate-700 text-white rounded-lg transition-colors"
                                >
                                    {copied ? ' Copied!' : ' Copy'}
                                </button>
                                <button
                                    onClick={handleSendEmail}
                                    disabled={!lead.email}
                                    className="glass-button px-6 py-2 flex items-center gap-2"
                                >
                                     Send & Log
                                </button>
                            </div>
                        </div>
                        <p className="text-xs text-slate-600 mt-3 text-center">
                            {user ? 'Email will be logged to this lead\'s activity timeline' : 'Log in to track email activity'}
                        </p>
                    </div>
                )}
            </div>
        </div>
    );
}

'use client';

import React, { useMemo } from 'react';
import { useRouter } from 'next/navigation';
import { useAuth } from '@/components/providers/AuthProvider';
import { useLeads } from '@/lib/api/hooks/useLeads';
import { useActivities } from '@/lib/api/hooks/useActivities';
import { Skeleton } from '@/components/ui/Skeleton';
import { EmptyState } from '@/components/ui/EmptyState';
import { Activity, Lead } from '@/types';
import { ActivityPerformanceChart } from '@/components/dashboard/ActivityPerformanceChart';

export default function DashboardClient() {
    const router = useRouter();
    const { user, loading: authLoading } = useAuth();

    // 1. Fetch Data via Hooks (Deduplicated & Cached)
    const { leads, isLoading: leadsLoading } = useLeads();
    const { activities, isLoading: activitiesLoading } = useActivities();

    const isLoading = authLoading || leadsLoading || activitiesLoading;

    // 2. Client-Side Aggregation (Memoized)
    // Since we now fetch raw lists once, we calculate stats on the fly.
    // This is fast for <5k items. For >5k, we would need server-side aggregation.
    const metrics = useMemo(() => {
        if (!user || isLoading) return null;

        const now = new Date();
        const startOfDay = new Date(now.setHours(0, 0, 0, 0)).getTime();
        const startOfWeek = new Date();
        startOfWeek.setDate(now.getDate() - 6);
        startOfWeek.setHours(0, 0, 0, 0);

        // Daily Activities
        const todayActivities = activities.filter((a: Activity) => a.timestamp >= startOfDay);
        const dials = todayActivities.filter((a: Activity) => a.type === 'call').length;
        const meetingsHeld = todayActivities.filter((a: Activity) => a.type === 'meeting').length;

        // Connect Rate (Simple version for now)
        const calls = todayActivities.filter((a: Activity) => a.type === 'call');
        const connects = calls.filter((a: Activity) => ['connected', 'meeting_set', 'qualified'].includes(a.outcome)).length;
        const connectRate = calls.length > 0 ? (connects / calls.length) * 100 : 0;

        // Pipeline Value
        const activeLeads = leads.filter((l: Lead) => !['Lost', 'Closed'].includes(l.status));
        const pipelineValue = activeLeads.reduce((sum: number, lead: Lead) => sum + (lead.value || 0), 0);

        // Pipeline Distribution
        const pipelineStatusCounts: Record<string, number> = {};
        activeLeads.forEach((lead: Lead) => {
            pipelineStatusCounts[lead.status] = (pipelineStatusCounts[lead.status] || 0) + 1;
        });

        return {
            dials,
            connectRate,
            pipelineValue,
            meetingsHeld,
            pipelineStatusCounts
        };
    }, [user, isLoading, leads, activities]);

    const hasData = leads.length > 0 || activities.length > 0;

    // Loading State
    if (isLoading) {
        return <DashboardSkeleton />;
    }

    // Empty State (New User)
    if (!hasData) {
        return (
            <div className="py-12">
                <EmptyState
                    title="Welcome to SalesTracker"
                    description="Your dashboard is looking a bit empty. Start by adding your first lead to track your sales pipeline."
                    actionLabel="Add Lead"
                    actionLink="/leads"
                />
            </div>
        );
    }

    // Pipeline Status Display Data
    const pipelineRows = Object.entries(metrics?.pipelineStatusCounts || {}).map(([status, count]) => ({
        label: status,
        count: count,
        color: getColorForStatus(status)
    })).sort((a, b) => b.count - a.count);

    return (
        <>
            {/* KPI Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-10">
                <KpiCard
                    title="Daily Dials"
                    value={metrics?.dials || 0}
                    subvalue="/50"
                    glowColor="bg-blue-500"
                    percent={(metrics?.dials || 0) / 50 * 100}
                />
                <KpiCard
                    title="Connect Rate"
                    value={`${(metrics?.connectRate || 0).toFixed(1)}%`}
                    subtext="Target: 15%"
                    glowColor="bg-emerald-500"
                    textColor="text-emerald-400"
                />
                <KpiCard
                    title="Pipeline Value"
                    value={`$${((metrics?.pipelineValue || 0) / 1000).toFixed(1)}k`}
                    subtext={`${leads.length} Active Leads`}
                    glowColor="bg-violet-500"
                    textColor="text-violet-400"
                />
                <KpiCard
                    title="Meetings Held"
                    value={metrics?.meetingsHeld || 0}
                    subtext="Today"
                    glowColor="bg-amber-500"
                    textColor="text-amber-400"
                />
            </div>

            {/* Main Content Grid */}
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                {/* Activity Feed / Chart */}
                <div className="glass-card lg:col-span-2 min-h-[500px] flex flex-col">
                    <h2 className="text-xl font-bold text-slate-100 mb-6 flex items-center gap-2">
                        Activity Performance
                        <span className="px-2 py-0.5 rounded-full bg-slate-800 text-xs text-slate-400 font-normal border border-white/5">This Week</span>
                    </h2>

                    {/* Placeholder for Recharts implementation - utilizing data prop later */}
                    <div className="flex-1 flex items-center justify-center border-2 border-dashed border-slate-800/50 rounded-2xl bg-slate-900/20">
                        <ActivityPerformanceChart data={activities} />
                    </div>
                </div>

                {/* Lead Status */}
                <div className="glass-card flex flex-col">
                    <h2 className="text-xl font-bold text-slate-100 mb-6">Pipeline Status</h2>
                    <div className="space-y-3">
                        {pipelineRows.length === 0 ? (
                            <div className="text-slate-500 text-sm text-center py-4">No active opportunities</div>
                        ) : (
                            pipelineRows.map((stage) => (
                                <div key={stage.label} className="group flex items-center justify-between p-4 bg-slate-900/40 border border-white/5 rounded-xl hover:border-white/10 hover:bg-slate-800/60 transition-all cursor-pointer" onClick={() => router.push('/leads')}>
                                    <div className="flex items-center gap-3">
                                        <div className={`w-2 h-2 rounded-full ${stage.color} shadow-[0_0_8px_currentColor] opacity-80 group-hover:opacity-100`} />
                                        <span className="text-sm font-medium text-slate-300 group-hover:text-white transition-colors">{stage.label}</span>
                                    </div>
                                    <span className="text-xs font-bold bg-slate-950 px-2.5 py-1 rounded-md text-slate-400 border border-white/5 group-hover:border-white/10 group-hover:text-white transition-all">
                                        {stage.count}
                                    </span>
                                </div>
                            ))
                        )}
                    </div>

                    <button
                        onClick={() => router.push('/leads')}
                        className="mt-6 w-full glass-button py-3 text-sm"
                    >
                        View Full Pipeline
                    </button>
                </div>
            </div>
        </>
    );
}

// --- Subcomponents for Cleanliness ---

interface KpiCardProps {
    title: string;
    value: string | number;
    subvalue?: string;
    subtext?: string;
    glowColor: string;
    percent?: number;
    textColor?: string;
}

function KpiCard({ title, value, subvalue, subtext, glowColor, percent, textColor = "text-slate-100" }: KpiCardProps) {
    return (
        <div className="glass-card group relative overflow-hidden">
            <div className="absolute top-0 right-0 p-4 opacity-10 group-hover:opacity-20 transition-opacity">
                <div className={`w-24 h-24 ${glowColor} rounded-full blur-3xl`}></div>
            </div>
            <h3 className="text-sm text-slate-400 font-medium uppercase tracking-wider">{title}</h3>
            <div className={`text-4xl font-bold ${textColor} mt-4`}>
                {value}
                {subvalue && <span className="text-slate-500 text-xl font-normal">{subvalue}</span>}
            </div>

            {percent !== undefined ? (
                <div className="mt-4 flex items-center gap-2">
                    <div className="flex-1 h-1.5 bg-slate-800 rounded-full overflow-hidden">
                        <div className={`h-full ${glowColor.replace('bg-', 'bg-')} shadow-[0_0_12px_rgba(59,130,246,0.5)]`} style={{ width: `${percent}%` }}></div>
                    </div>
                    <span className="text-xs text-blue-400 font-bold">{percent.toFixed(0)}%</span>
                </div>
            ) : (
                <p className="text-xs text-slate-500 mt-2 font-medium">{subtext}</p>
            )}
        </div>
    );
}

function DashboardSkeleton() {
    return (
        <div className="animate-pulse">
            <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-10">
                {[1, 2, 3, 4].map(i => <div key={i} className="h-40 bg-slate-800/50 rounded-2xl" />)}
            </div>
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                <div className="lg:col-span-2 h-[500px] bg-slate-800/50 rounded-2xl" />
                <div className="h-[500px] bg-slate-800/50 rounded-2xl" />
            </div>
        </div>
    );
}

function getColorForStatus(status: string) {
    switch (status) {
        case 'New': return 'bg-blue-500';
        case 'Contacted': return 'bg-indigo-500';
        case 'Qualified': return 'bg-emerald-500';
        case 'Proposal': return 'bg-violet-500';
        case 'Negotiation': return 'bg-amber-500';
        case 'Closed': return 'bg-green-500';
        default: return 'bg-slate-500';
    }
}

'use client';

import React, { useState, useEffect } from 'react';
import Link from 'next/link';
import { usePathname, useRouter } from 'next/navigation';
import Image from 'next/image';
import {
    LayoutDashboard, Target, Activity, Database, BarChart3, GraduationCap,
    ChevronLeft, ChevronRight, Menu, LogOut, Settings, LogIn
} from 'lucide-react';
import { AuthProvider, useAuth } from '@/components/providers/AuthProvider';
import { QueryProvider } from '@/components/providers/QueryProvider';
import { ErrorBoundary } from '@/components/ui/ErrorBoundary';
import { CookieConsent } from '@/components/ui/CookieConsent';
import { WelcomeTour } from '@/components/onboarding/WelcomeTour';
import { toast, Toaster } from 'sonner';
import { AIContextProvider } from '@/components/providers/AIContext';

function SidebarContent({ collapsed, mobileMenuOpen }: { collapsed: boolean; mobileMenuOpen: boolean }) {
    const { user, loading, logout, signInWithGoogle } = useAuth();
    const router = useRouter();

    const checkUserRole = async (userId: string) => {
        try {
            // This function's implementation seems to be a placeholder or incomplete based on its name.
            // It currently performs a logout operation.
            await logout();
            toast.success('Signed out successfully');
            router.push('/');
        } catch (error) {
            toast.error('Failed to sign out');
        }
    };

    const handleLogout = async () => {
        try {
            await logout();
            toast.success('Signed out successfully');
            router.push('/');
        } catch (error) {
            toast.error('Failed to sign out');
        }
    };

    const handleSignIn = async () => {
        try {
            await signInWithGoogle();
            toast.success('Signed in successfully!');
        } catch (error: unknown) {
            if (error instanceof Error && (error as any).code === 'auth/popup-closed-by-user') return;
            toast.error('Failed to sign in');
        }
    };

    const showLabel = !collapsed || mobileMenuOpen;

    return (
        <>
            {/* Navigation */}
            <nav className="flex-1 px-3 space-y-2 mt-4 overflow-y-auto overflow-x-hidden custom-scrollbar">
                <NavLink href="/" icon={<LayoutDashboard size={20} />} label="Dashboard" collapsed={collapsed} mobile={mobileMenuOpen} />
                <NavLink href="/leads" icon={<Target size={20} />} label="Leads Pipeline" collapsed={collapsed} mobile={mobileMenuOpen} />
                <NavLink href="/activities" icon={<Activity size={20} />} label="Activities" collapsed={collapsed} mobile={mobileMenuOpen} />
                <NavLink href="/resources" icon={<Database size={20} />} label="Enablement" collapsed={collapsed} mobile={mobileMenuOpen} />
                <NavLink href="/analytics" icon={<BarChart3 size={20} />} label="Analytics" collapsed={collapsed} mobile={mobileMenuOpen} />
                <NavLink href="/training" icon={<GraduationCap size={20} />} label="Training" collapsed={collapsed} mobile={mobileMenuOpen} />
                <NavLink href="/reality-link" icon={<Activity size={20} />} label="Reality Link HUD" collapsed={collapsed} mobile={mobileMenuOpen} />

                {/* Settings - show only when logged in */}
                {user && (
                    <NavLink href="/settings" icon={<Settings size={20} />} label="Settings" collapsed={collapsed} mobile={mobileMenuOpen} />
                )}
            </nav>

            {/* Footer / User */}
            <div className="p-4 border-t border-white/5 overflow-x-hidden">
                {loading ? (
                    <div className="flex items-center justify-center py-3">
                        <div className="w-5 h-5 border-2 border-indigo-500 border-t-transparent rounded-full animate-spin" />
                    </div>
                ) : user ? (
                    <>
                        {/* User Profile */}
                        <div className={`flex items-center gap-3 mb-3 ${collapsed && !mobileMenuOpen ? 'justify-center' : ''}`}>
                            {user.photoURL ? (
                                <div className="relative w-9 h-9 flex-shrink-0">
                                    <Image
                                        src={user.photoURL}
                                        alt={user.displayName || 'User'}
                                        fill
                                        className="rounded-full border border-white/10 shadow-lg object-cover"
                                    />
                                </div>
                            ) : (
                                <div className="w-9 h-9 flex-shrink-0 rounded-full bg-gradient-to-tr from-indigo-500 to-violet-500 border border-white/10 shadow-lg shadow-indigo-500/20 flex items-center justify-center text-white font-medium text-sm">
                                    {user.displayName?.charAt(0) || user.email?.charAt(0) || '?'}
                                </div>
                            )}
                            {showLabel && (
                                <div className="overflow-hidden flex-1 min-w-0">
                                    <div className="text-sm font-medium text-slate-200 truncate">
                                        {user.displayName || 'User'}
                                    </div>
                                    <div className="text-[10px] text-slate-500 truncate">
                                        {user.email}
                                    </div>
                                </div>
                            )}
                        </div>

                        {/* Sign Out Button */}
                        <button
                            onClick={handleLogout}
                            className={`w-full flex items-center gap-2 text-sm text-slate-400 hover:text-white hover:bg-white/5 rounded-lg transition-all ${showLabel ? 'px-3 py-2' : 'p-3 justify-center'
                                }`}
                            title={!showLabel ? 'Sign Out' : ''}
                        >
                            <LogOut size={18} />
                            {showLabel && <span>Sign Out</span>}
                        </button>
                    </>
                ) : (
                    /* Sign In Button */
                    <button
                        onClick={handleSignIn}
                        className={`w-full flex items-center gap-2 bg-gradient-to-r from-indigo-500 to-violet-500 hover:from-indigo-600 hover:to-violet-600 text-white font-medium rounded-xl transition-all shadow-lg shadow-indigo-500/25 ${showLabel ? 'px-4 py-3' : 'p-3 justify-center'
                            }`}
                        title={!showLabel ? 'Sign In' : ''}
                    >
                        <LogIn size={18} />
                        {showLabel && <span>Sign In</span>}
                    </button>
                )}
            </div>
        </>
    );
}

export function ClientLayout({ children }: { children: React.ReactNode }) {
    const [collapsed, setCollapsed] = useState(false);
    const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
    const [mounted, setMounted] = useState(false);

    useEffect(() => {
        const timeout = setTimeout(() => {
            setMounted(true);
        }, 0);
        return () => clearTimeout(timeout);
    }, []);

    // Close mobile menu on route change
    const pathname = usePathname();
    useEffect(() => {
        if (mobileMenuOpen) {
            const timeout = setTimeout(() => {
                setMobileMenuOpen(false);
            }, 0);
            return () => clearTimeout(timeout);
        }
    }, [pathname, mobileMenuOpen]);

    // Simple loading state for SSR
    if (!mounted) {
        return (
            <QueryProvider>
                <AuthProvider>
                    <AIContextProvider>
                        <div className="flex min-h-screen">
                            <div className="hidden md:block w-64 bg-slate-900/40 border-r border-white/5 fixed h-full z-50" />
                            <main className="flex-1 md:ml-64 relative z-0">{children}</main>
                        </div>
                        <Toaster position="top-right" theme="dark" closeButton richColors />
                    </AIContextProvider>
                </AuthProvider>
            </QueryProvider>
        );
    }

    return (
        <QueryProvider>
            <AuthProvider>
                <AIContextProvider>
                    {/* Global Dark Background Base (Fix for App Hosting) */}
                    <div className="fixed inset-0 bg-slate-950 z-[-2]" />

                    {/* Background Gradients (Global) */}
                    <div className="fixed inset-0 pointer-events-none z-[-1]">
                        <div className="absolute top-0 left-[15%] w-[500px] h-[500px] bg-blue-500/10 rounded-full blur-[100px]" />
                        <div className="absolute bottom-0 right-[15%] w-[500px] h-[500px] bg-violet-500/10 rounded-full blur-[100px]" />
                    </div>

                    {/* Mobile Menu Overlay */}
                    {mobileMenuOpen && (
                        <div
                            className="fixed inset-0 bg-slate-950/80 backdrop-blur-sm z-[60] md:hidden animate-fade-in"
                            onClick={() => setMobileMenuOpen(false)}
                        />
                    )}

                    {/* Sidebar */}
                    <aside
                        className={`
                        fixed top-0 bottom-0 z-[70] flex flex-col glass-panel border-r border-white/5 border-t-0 border-b-0 border-l-0 transition-transform duration-300 ease-in-out
                        md:left-0
                        ${mobileMenuOpen ? 'translate-x-0' : '-translate-x-full md:translate-x-0'}
                        ${collapsed ? 'md:w-20' : 'md:w-64'}
                        w-64
                        overflow-hidden md:overflow-visible
                    `}
                    >
                        {/* Header / Logo */}
