                            {collapsed && !mobileMenuOpen && (
                                <Link href="/" className="w-8 h-8 rounded-lg bg-gradient-to-br from-indigo-500 to-violet-500 flex items-center justify-center font-bold text-white text-xs">
                                    ST
                                </Link>
                            )}
                        </div>

                        {/* Sidebar Content with Auth */}
                        <SidebarContent collapsed={collapsed} mobileMenuOpen={mobileMenuOpen} />

                        {/* Collapse Toggle (Desktop Only) */}
                        <button
                            onClick={() => setCollapsed(!collapsed)}
                            aria-label={collapsed ? "Expand sidebar" : "Collapse sidebar"}
                            className="hidden md:flex absolute -right-3 top-24 w-6 h-6 rounded-full bg-slate-800 border border-slate-600 items-center justify-center text-slate-400 hover:text-white hover:border-white/50 transition-all shadow-lg z-50"
                        >
                            {collapsed ? <ChevronRight size={14} /> : <ChevronLeft size={14} />}
                        </button>
                    </aside>

                    {/* Main Content Area */}
                    <main
                        className={`
                        flex-1 relative z-0 transition-all duration-300 ease-in-out min-h-screen flex flex-col
                        ${collapsed ? 'md:ml-20' : 'md:ml-64'}
                        ml-0
                    `}
                    >
                        {/* Mobile Header */}
                        <div className="md:hidden sticky top-0 z-40 bg-slate-900/80 backdrop-blur-md border-b border-white/5 p-4 flex items-center justify-between">
                            <div className="text-lg font-bold bg-clip-text text-transparent bg-gradient-to-r from-indigo-400 to-violet-400">
                                SalesTracker
                            </div>
                            <button
                                onClick={() => setMobileMenuOpen(true)}
                                aria-label="Open mobile menu"
                                className="p-2 text-slate-300 hover:text-white hover:bg-white/5 rounded-lg transition-colors"
                            >
                                <Menu size={24} />
                            </button>
                        </div>

                        <div className="flex-1 w-full max-w-full overflow-x-hidden">
                            <ErrorBoundary>
                                {children}
                            </ErrorBoundary>
                        </div>

                        {/* Professional Footer */}
                        <footer className="mt-auto border-t border-white/5 p-8 text-center md:text-left">
                            <div className="flex flex-col md:flex-row justify-between items-center gap-4">
                                <div className="text-sm text-slate-500">
                                     {new Date().getFullYear()} SalesTracker AI. All rights reserved.
                                </div>
                                <div className="flex items-center gap-6 text-sm">
                                    <Link href="/privacy" className="text-slate-500 hover:text-indigo-400 transition-colors">Privacy Policy</Link>
                                    <Link href="/terms" className="text-slate-500 hover:text-indigo-400 transition-colors">Terms of Service</Link>
                                    <a href="mailto:support@salestracker-ai.com" className="text-slate-500 hover:text-indigo-400 transition-colors">Support</a>
                                </div>
                            </div>
                        </footer>
                    </main>

                    {/* Overlay components */}
                    <CookieConsent />
                    <WelcomeTour />

                    {/* Toast notifications */}
                    <Toaster position="top-right" theme="dark" closeButton richColors />
                </AIContextProvider>
            </AuthProvider>
        </QueryProvider>
    );
}

function NavLink({ href, icon, label, collapsed, mobile }: { href: string; icon: React.ReactNode; label: string; collapsed: boolean, mobile: boolean }) {
    const pathname = usePathname();
    const isActive = pathname === href;

    const showLabel = !collapsed || mobile;

    return (
        <Link
            href={href}
            title={!showLabel ? label : ''}
            aria-label={label}
            className={`
                group flex items-center rounded-xl transition-all duration-300 ease-out relative
                ${!showLabel ? 'justify-center p-3' : 'px-4 py-3'}
                ${isActive
                    ? 'bg-gradient-to-r from-indigo-500/20 to-violet-500/5 text-indigo-300 border-l-2 border-indigo-500 shadow-[0_0_15px_rgba(99,102,241,0.15)]'
                    : 'text-slate-400 hover:text-white hover:bg-white/5 border-l-2 border-transparent'}
            `}
        >
            {/* Active Glow for Icon */}
            {isActive && !showLabel && (
                <div className="absolute inset-0 bg-indigo-500/20 rounded-xl blur-md"></div>
            )}

            <span className={`relative z-10 transition-transform duration-200 ${isActive ? 'scale-110' : 'group-hover:scale-110'}`}>
                {React.cloneElement(icon as React.ReactElement<any>, {
                    className: isActive ? 'text-indigo-400 drop-shadow-[0_0_5px_rgba(99,102,241,0.5)]' : ''
                })}
            </span>

            {showLabel && (
                <span className={`ml-3 text-sm font-medium transition-opacity duration-200 ${isActive ? 'text-white' : ''}`}>
                    {label}
                </span>
            )}

            {/* Tooltip for collapsed state (Desktop only) */}
            {!showLabel && (
                <div className="absolute left-full ml-4 px-2 py-1 bg-slate-800 text-white text-xs rounded border border-white/10 opacity-0 group-hover:opacity-100 pointer-events-none transition-opacity whitespace-nowrap z-50 shadow-xl hidden md:block">
                    {label}
                </div>
            )}
        </Link>
    );
}

'use client';

import React, { useState } from 'react';
import { Phone, PhoneOff, Loader2 } from 'lucide-react';
import { useAuth } from '@/components/providers/AuthProvider';
import { toast } from 'sonner';

interface CallButtonProps {
    leadId: string;
    leadName: string;
    leadPhone: string;
    size?: 'sm' | 'md' | 'lg';
    variant?: 'primary' | 'ghost';
}

type CallStatus = 'idle' | 'connecting' | 'ringing' | 'in-progress' | 'completed' | 'failed';

export function CallButton({
    leadId,
    leadName,
    leadPhone,
    size = 'md',
    variant = 'primary'
}: CallButtonProps) {
    const { user: authUser } = useAuth();
    // Fake user for testing if not logged in
    const user = authUser || { uid: 'test-user', displayName: 'Test User' };

    const [callStatus, setCallStatus] = useState<CallStatus>('idle');
    const [callSid, setCallSid] = useState<string | null>(null);

    const handleCall = async () => {
        if (!leadPhone) {
            toast.error('No phone number for this lead');
            return;
        }

        setCallStatus('connecting');

        try {
            const response = await fetch('/api/twilio/call', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    leadPhone,
                    userId: user.uid,
                    leadId,
                    leadName,
                }),
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.error || 'Failed to initiate call');
            }

            setCallSid(data.callSid);
            setCallStatus('ringing');
            toast.success(`Calling ${leadName}...`);

            // Simulate call progress (in production, use webhooks)
            setTimeout(() => setCallStatus('in-progress'), 3000);

        } catch (error) {
            console.error('Call error:', error);
            setCallStatus('failed');
            toast.error(error instanceof Error ? error.message : 'Failed to initiate call');

            // Reset after error
            setTimeout(() => setCallStatus('idle'), 2000);
        }
    };

    const handleEndCall = () => {
        // In production, you'd call Twilio API to end the call
        setCallStatus('completed');
        toast.success('Call ended');

        setTimeout(() => {
            setCallStatus('idle');
            setCallSid(null);
        }, 1500);
    };

    const sizeClasses = {
        sm: 'p-1.5',
        md: 'p-2',
        lg: 'p-3',
    };

    const iconSize = {
        sm: 14,
        md: 18,
        lg: 22,
    };

    // Active call state
    if (callStatus !== 'idle' && callStatus !== 'failed') {
        return (
            <div className="flex items-center gap-2">
                <button
                    onClick={handleEndCall}
                    className={`${sizeClasses[size]} rounded-lg bg-red-500 hover:bg-red-600 text-white transition-all shadow-lg animate-pulse`}
                    title="End call"
                >
                    <PhoneOff size={iconSize[size]} />
                </button>
                <span className="text-xs text-slate-400 capitalize">
                    {callStatus === 'connecting' && 'Connecting...'}
                    {callStatus === 'ringing' && 'Ringing...'}
                    {callStatus === 'in-progress' && 'In Progress'}
                    {callStatus === 'completed' && 'Completed'}
                </span>
            </div>
        );
    }

    // Idle/Failed state
    const baseClasses = variant === 'primary'
        ? 'bg-green-500 hover:bg-green-600 text-white shadow-lg shadow-green-500/20'
        : 'text-slate-400 hover:text-green-400 hover:bg-white/5';

    return (
        <button
            onClick={handleCall}
            disabled={false}
            className={`${sizeClasses[size]} rounded-lg ${baseClasses} transition-all disabled:opacity-50`}
            title={`Call ${leadName}`}
        >
            <Phone size={iconSize[size]} />
        </button>
    );
}

'use client';

import React, { useState, useEffect } from 'react';
import { Lead } from '@/types';
import { EmailRecord, getLeadEmails, syncEmailsForLead } from '@/lib/gmail/gmail-service';
import { analyzeEmail, analyzeEmailThread, EmailAnalysis } from '@/lib/gmail/email-analyzer';
import { toast } from 'sonner';

interface EmailThreadsPanelProps {
    lead: Lead;
    userId: string;
    isOpen: boolean;
    onClose: () => void;
    onDraftReply: (subject: string, body: string) => void;
}

export function EmailThreadsPanel({ lead, userId, isOpen, onClose, onDraftReply }: EmailThreadsPanelProps) {
    const [emails, setEmails] = useState<EmailRecord[]>([]);
    const [isLoading, setIsLoading] = useState(true);
    const [isSyncing, setIsSyncing] = useState(false);
    const [threadAnalysis, setThreadAnalysis] = useState<{
        threadSummary: string;
        overallSentiment: 'positive' | 'neutral' | 'negative';
        engagementLevel: 'high' | 'medium' | 'low';
        nextBestAction: string;
        dealHealth: 'healthy' | 'at-risk' | 'stalled';
    } | null>(null);
    const [emailAnalyses, setEmailAnalyses] = useState<Record<string, EmailAnalysis>>({});
    const [expandedEmailId, setExpandedEmailId] = useState<string | null>(null);

    useEffect(() => {
        if (isOpen && lead.email) {
            loadEmails();
        }
    }, [isOpen, lead.email]);

    const loadEmails = async () => {
        setIsLoading(true);
        try {
            const data = await getLeadEmails(userId, lead.id);
            setEmails(data);

            // Analyze thread if we have emails
            if (data.length > 0) {
                const analysis = await analyzeEmailThread(data);
                setThreadAnalysis(analysis);
            }
        } catch (error) {
            console.error('Error loading emails:', error);
        } finally {
            setIsLoading(false);
        }
    };

    const handleSync = async () => {
        setIsSyncing(true);
        try {
            const synced = await syncEmailsForLead(userId, lead.email, lead.id);
            setEmails(synced.sort((a, b) => b.timestamp - a.timestamp));
            toast.success(`Synced ${synced.length} emails`);

            // Re-analyze thread
            if (synced.length > 0) {
                const analysis = await analyzeEmailThread(synced);
                setThreadAnalysis(analysis);
            }
        } catch (error: unknown) {
            if (error instanceof Error && error.message === 'Gmail not connected') {
                toast.error('Please connect Gmail in Settings first');
            } else {
                toast.error('Failed to sync emails');
            }
        } finally {
            setIsSyncing(false);
        }
    };

    const handleAnalyzeEmail = async (email: EmailRecord) => {
        if (emailAnalyses[email.id]) return;

        try {
            const analysis = await analyzeEmail(email);
            setEmailAnalyses(prev => ({ ...prev, [email.id]: analysis }));
        } catch (error) {
            toast.error('Failed to analyze email');
        }
    };

    const getSentimentColor = (sentiment: string) => {
        switch (sentiment) {
            case 'positive': return 'text-green-400 bg-green-500/10 border-green-500/30';
            case 'negative': return 'text-red-400 bg-red-500/10 border-red-500/30';
            default: return 'text-slate-400 bg-slate-500/10 border-slate-500/30';
        }
    };

    const getDealHealthColor = (health: string) => {
        switch (health) {
            case 'healthy': return 'text-green-400';
            case 'at-risk': return 'text-amber-400';
            default: return 'text-red-400';
        }
    };

    if (!isOpen) return null;

    return (
        <div className="fixed inset-0 z-50 flex items-center justify-center p-4">
            {/* Backdrop */}
            <div className="absolute inset-0 bg-black/70 backdrop-blur-sm" onClick={onClose} />

            {/* Panel */}
            <div className="relative bg-slate-900 border border-slate-700 rounded-2xl w-full max-w-3xl max-h-[85vh] shadow-2xl flex flex-col">
                {/* Header */}
                <div className="p-6 border-b border-slate-800">
                    <div className="flex items-center justify-between">
                        <div className="flex items-center gap-3">
                            <div className="w-10 h-10 rounded-full bg-gradient-to-br from-blue-500 to-cyan-500 flex items-center justify-center text-lg">
                                
                            </div>
                            <div>
                                <h2 className="text-xl font-bold text-white">Email History</h2>
                                <p className="text-sm text-slate-400">{lead.companyName}  {lead.email}</p>
                            </div>
                        </div>
                        <div className="flex items-center gap-2">
                            <button
                                onClick={handleSync}
                                disabled={isSyncing}
                                className="px-4 py-2 text-sm bg-slate-800 hover:bg-slate-700 border border-slate-700 rounded-lg text-slate-300 transition-colors flex items-center gap-2"
                            >
                                {isSyncing ? (
                                    <div className="w-4 h-4 border-2 border-slate-400 border-t-transparent rounded-full animate-spin" />
                                ) : (
                                    <span></span>
                                )}
                                Sync Gmail
                            </button>
                            <button
                                onClick={onClose}
                                className="text-slate-400 hover:text-white text-2xl px-2"
                            >
                                
                            </button>
                        </div>
                    </div>

                    {/* Thread Analysis Summary */}
                    {threadAnalysis && (
                        <div className="mt-4 p-4 bg-slate-800/50 rounded-lg border border-slate-700">
                            <div className="flex items-center justify-between mb-2">
                                <span className="text-sm text-slate-400">AI Thread Analysis</span>
                                <span className={`text-xs font-medium ${getDealHealthColor(threadAnalysis.dealHealth)}`}>
                                    {threadAnalysis.dealHealth.toUpperCase()}
                                </span>
                            </div>
                            <p className="text-sm text-slate-300 mb-2">{threadAnalysis.threadSummary}</p>
                            <div className="flex items-center gap-4 text-xs">
                                <span className={`px-2 py-1 rounded border ${getSentimentColor(threadAnalysis.overallSentiment)}`}>
                                    {threadAnalysis.overallSentiment}
                                </span>
                                <span className="text-slate-500">
                                    Engagement: {threadAnalysis.engagementLevel}
                                </span>
                            </div>
                            <div className="mt-2 flex items-center gap-2">
                                <span className="text-amber-400"></span>
                                <span className="text-sm text-amber-300">{threadAnalysis.nextBestAction}</span>
                            </div>
                        </div>
                    )}
                </div>

                {/* Email List */}
                <div className="flex-1 overflow-y-auto p-6">
                    {isLoading ? (
                        <div className="flex items-center justify-center py-12">
                            <div className="w-8 h-8 border-2 border-blue-500 border-t-transparent rounded-full animate-spin" />
                        </div>
                    ) : emails.length === 0 ? (
                        <div className="text-center py-12">
                            <p className="text-slate-500 mb-4">No emails found for this lead</p>
                            <button
                                onClick={handleSync}
                                className="glass-button px-4 py-2"
                            >
                                 Sync from Gmail
                            </button>
                        </div>
                    ) : (
                        <div className="space-y-4">
                            {emails.map((email) => (
                                <div
                                    key={email.id}
                                    className={`p-4 rounded-lg border transition-all cursor-pointer ${email.direction === 'sent'
                                        ? 'bg-blue-500/5 border-blue-500/20 ml-8'
                                        : 'bg-slate-800/50 border-slate-700 mr-8'
                                        }`}
                                    onClick={() => {
                                        setExpandedEmailId(expandedEmailId === email.id ? null : email.id);
                                        if (!emailAnalyses[email.id]) {
                                            handleAnalyzeEmail(email);
                                        }
                                    }}
                                >
                                    <div className="flex items-start justify-between mb-2">
                                        <div className="flex items-center gap-2">
                                            <span className="text-lg">
                                                {email.direction === 'sent' ? '' : ''}
                                            </span>
                                            <span className="text-sm font-medium text-slate-300">
                                                {email.direction === 'sent' ? 'You' : lead.contactName}
                                            </span>
                                        </div>
                                        <span className="text-xs text-slate-500">
                                            {new Date(email.timestamp).toLocaleString()}
                                        </span>
                                    </div>

                                    <p className="text-sm font-medium text-white mb-1">{email.subject}</p>
                                    <p className="text-sm text-slate-400">
                                        {expandedEmailId === email.id ? email.body : email.snippet}
                                    </p>

                                    {/* AI Analysis */}
                                    {expandedEmailId === email.id && emailAnalyses[email.id] && (
                                        <div className="mt-3 pt-3 border-t border-slate-700">
                                            <div className="flex flex-wrap gap-2 mb-2">
                                                <span className={`text-xs px-2 py-1 rounded border ${getSentimentColor(emailAnalyses[email.id].sentiment)}`}>
                                                    {emailAnalyses[email.id].sentiment}
                                                </span>
                                                <span className="text-xs px-2 py-1 rounded border border-slate-600 text-slate-400">
                                                    {emailAnalyses[email.id].intent}
                                                </span>
                                            </div>
                                            <p className="text-xs text-slate-500 mb-2">{emailAnalyses[email.id].summary}</p>
                                            {emailAnalyses[email.id].dealSignals.length > 0 && (
                                                <div className="flex flex-wrap gap-1">
                                                    {emailAnalyses[email.id].dealSignals.map((signal, i) => (
                                                        <span key={i} className="text-xs px-2 py-0.5 bg-amber-500/10 text-amber-400 rounded">
                                                             {signal}
                                                        </span>
                                                    ))}
                                                </div>
                                            )}
                                        </div>
                                    )}
                                </div>
                            ))}
                        </div>
                    )}
                </div>

                {/* Footer */}
                <div className="p-6 border-t border-slate-800">
                    <button
                        onClick={() => {
                            const lastEmail = emails[0];
                            onDraftReply(
                                'Re: ' + (lastEmail?.subject || ''),
                                ''
                            );
                            onClose();
                        }}
                        className="glass-button w-full py-2"
                    >
                         Draft AI Reply
                    </button>
                </div>
            </div>
        </div>
    );
}

'use client';

import React, { useState } from 'react';
import { Lead } from '@/types';
import { ActivitiesService, LeadsService } from '@/lib/firebase/services';
import { toast } from 'sonner';

interface LogReplyModalProps {
    lead: Lead;
    userId: string;
    isOpen: boolean;
    onClose: () => void;
    onSuccess: () => void;
}

export function LogReplyModal({ lead, userId, isOpen, onClose, onSuccess }: LogReplyModalProps) {
    const [replyContent, setReplyContent] = useState('');
    const [sentiment, setSentiment] = useState<'positive' | 'neutral' | 'negative'>('positive');
    const [nextStep, setNextStep] = useState('');
    const [isSaving, setIsSaving] = useState(false);

    const handleSave = async () => {
        if (!replyContent.trim()) {
            toast.error('Please enter the reply content');
            return;
        }

        setIsSaving(true);
        try {
            // Log the reply as an email activity
            await ActivitiesService.logActivity(userId, {
                type: 'email',
                outcome: sentiment === 'positive' ? 'connected' : sentiment === 'negative' ? 'none' : 'connected',
                leadId: lead.id,
                notes: ` REPLY RECEIVED (${sentiment}): ${replyContent}`,
                timestamp: Date.now(),
                repId: userId
            });

            // Update lead
            const updates: Partial<Lead> = {
                lastContact: Date.now()
            };

            if (nextStep) {
                updates.nextStep = nextStep;
            }

            // If positive reply and lead is New/Contacted, move to Contacted/Qualified
            if (sentiment === 'positive') {
                if (lead.status === 'New') {
                    updates.status = 'Contacted';
                } else if (lead.status === 'Contacted') {
                    updates.status = 'Qualified';
                }
            }

            await LeadsService.updateLead(userId, lead.id, updates);

            toast.success('Reply logged! Lead updated.');
            onSuccess();
            onClose();

            // Reset form
            setReplyContent('');
            setSentiment('positive');
            setNextStep('');
        } catch (error: unknown) {
            console.error('Error logging reply:', error);
            toast.error(error instanceof Error ? error.message : 'Error logging reply');
        } finally {
            setIsSaving(false);
        }
    };

    if (!isOpen) return null;

    return (
        <div className="fixed inset-0 z-50 flex items-center justify-center p-4">
            {/* Backdrop */}
            <div className="absolute inset-0 bg-black/70 backdrop-blur-sm" onClick={onClose} />

            {/* Modal */}
            <div className="relative bg-slate-900 border border-slate-700 rounded-2xl w-full max-w-md shadow-2xl">
                {/* Header */}
                <div className="p-6 border-b border-slate-800">
                    <div className="flex items-center gap-3">
                        <div className="w-10 h-10 rounded-full bg-gradient-to-br from-amber-500 to-orange-600 flex items-center justify-center text-lg">
                            
                        </div>
                        <div>
                            <h2 className="text-xl font-bold text-white">Log Email Reply</h2>
                            <p className="text-sm text-slate-400">{lead.companyName}  {lead.contactName}</p>
                        </div>
                    </div>
                </div>

                {/* Content */}
                <div className="p-6 space-y-5">
                    {/* Sentiment */}
                    <div>
                        <label className="block text-sm text-slate-400 mb-2">Reply Sentiment</label>
                        <div className="flex gap-2">
                            {[
                                { value: 'positive', label: 'Positive', icon: '', color: 'green' },
                                { value: 'neutral', label: 'Neutral', icon: '', color: 'slate' },
                                { value: 'negative', label: 'Negative', icon: '', color: 'red' },
                            ].map((s) => (
                                <button
                                    key={s.value}
                                    onClick={() => setSentiment(s.value as any)}
                                    className={`flex-1 p-3 rounded-lg border text-center transition-all ${sentiment === s.value
                                        ? `bg-${s.color}-500/20 border-${s.color}-500/50 text-${s.color}-300`
                                        : 'bg-slate-800/50 border-slate-700 text-slate-400 hover:bg-slate-700'
                                        }`}
                                    style={{
                                        backgroundColor: sentiment === s.value
                                            ? s.color === 'green' ? 'rgba(34, 197, 94, 0.2)'
                                                : s.color === 'red' ? 'rgba(239, 68, 68, 0.2)'
                                                    : 'rgba(100, 116, 139, 0.2)'
                                            : undefined,
                                        borderColor: sentiment === s.value
                                            ? s.color === 'green' ? 'rgba(34, 197, 94, 0.5)'
                                                : s.color === 'red' ? 'rgba(239, 68, 68, 0.5)'
                                                    : 'rgba(100, 116, 139, 0.5)'
                                            : undefined
                                    }}
                                >
                                    <div className="text-xl mb-1">{s.icon}</div>
                                    <div className="text-xs">{s.label}</div>
                                </button>
                            ))}
                        </div>
                    </div>

                    {/* Reply Content */}
                    <div>
                        <label className="block text-sm text-slate-400 mb-2">Reply Content or Summary</label>
                        <textarea
                            value={replyContent}
                            onChange={(e) => setReplyContent(e.target.value)}
                            className="glass-input w-full h-32 resize-none"
                            placeholder="Paste the reply or write a summary of what they said..."
                        />
                        <p className="text-xs text-slate-500 mt-1">Tip: Paste the email reply directly for full context</p>
                    </div>

                    {/* Next Step */}
                    <div>
                        <label className="block text-sm text-slate-400 mb-2">Next Step</label>
                        <input
                            type="text"
                            value={nextStep}
                            onChange={(e) => setNextStep(e.target.value)}
                            className="glass-input w-full"
                            placeholder="e.g., Schedule call, Send pricing"
                        />
                    </div>
                </div>

                {/* Footer */}
                <div className="p-6 border-t border-slate-800 flex justify-end gap-3">
                    <button
                        onClick={onClose}
                        className="px-4 py-2 text-slate-400 hover:text-white transition-colors"
                    >
                        Cancel
                    </button>
                    <button
                        onClick={handleSave}
                        disabled={isSaving || !replyContent.trim()}
                        className="glass-button px-6 py-2"
                    >
                        {isSaving ? 'Saving...' : 'Log Reply'}
                    </button>
                </div>
            </div>
        </div>
    );
}

'use client';

import React, { useState, useEffect, useRef } from 'react';
import { Send, Loader2, Phone, X, MoreVertical } from 'lucide-react';
import { collection, query, where, orderBy, onSnapshot, addDoc, serverTimestamp } from 'firebase/firestore';
import { db } from '@/lib/firebase/config';
import { useAuth } from '@/components/providers/AuthProvider';
import { toast } from 'sonner';

interface Message {
    id: string;
    body: string;
    direction: 'inbound' | 'outbound';
    status: string;
    createdAt: any;
    leadId: string;
}

interface SMSChatWindowProps {
    leadId: string;
    leadName: string;
    leadPhone: string;
    onClose: () => void;
}

export function SMSChatWindow({ leadId, leadName, leadPhone, onClose }: SMSChatWindowProps) {
    const { user } = useAuth();
    const [messages, setMessages] = useState<Message[]>([]);
    const [newMessage, setNewMessage] = useState('');
    const [isLoading, setIsLoading] = useState(true);
    const [isSending, setIsSending] = useState(false);
    const scrollRef = useRef<HTMLDivElement>(null);

    // Scroll to bottom on new messages
    useEffect(() => {
        if (scrollRef.current) {
            scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
        }
    }, [messages]);

    // Listen for messages
    useEffect(() => {
        if (!user) return;

        const q = query(
            collection(db, 'users', user.uid, 'messages'),
            where('leadId', '==', leadId),
            orderBy('createdAt', 'asc')
        );

        const unsubscribe = onSnapshot(q, (snapshot) => {
            const msgs = snapshot.docs.map(doc => ({
                id: doc.id,
                ...doc.data()
            })) as Message[];
            setMessages(msgs);
            setIsLoading(false);
        }, (error) => {
            console.error("Error fetching messages:", error);
            setIsLoading(false);
            // Index might be required
        });

        return () => unsubscribe();
    }, [user, leadId]);

    const handleSend = async () => {
        if (!newMessage.trim() || !user) return;

        setIsSending(true);
        try {
            const response = await fetch('/api/twilio/sms', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    toNumber: leadPhone,
                    message: newMessage.trim(),
                    userId: user.uid,
                    leadId,
                }),
            });

            if (!response.ok) {
                const data = await response.json();
                throw new Error(data.error || 'Failed to send');
            }

            setNewMessage('');
            // Optimistic update handled by snapshot listener mostly, 
            // but we could add a temp message if needed. 
            // Twilio API is fast enough usually.
        } catch (error: unknown) {
            toast.error('Failed to send message');
            console.error(error);
        } finally {
            setIsSending(false);
        }
    };

    const handleKeyDown = (e: React.KeyboardEvent) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            handleSend();
        }
    };

    const formatTime = (timestamp: any) => {
        if (!timestamp) return '';
        const date = timestamp.toDate ? timestamp.toDate() : new Date(timestamp);
        return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    };

    return (
        <div className="flex flex-col h-[600px] w-full max-w-md bg-slate-900 rounded-2xl border border-slate-800 shadow-2xl overflow-hidden">
            {/* Header - Smartphone style */}
            <div className="bg-slate-800/80 backdrop-blur-md p-4 flex items-center justify-between border-b border-white/5 z-10">
                <div className="flex items-center gap-3">
                    <div className="w-10 h-10 rounded-full bg-indigo-500/20 flex items-center justify-center text-indigo-400 font-bold">
                        {leadName.charAt(0)}
                    </div>
                    <div>
                        <h3 className="font-semibold text-white text-sm">{leadName}</h3>
                        <p className="text-xs text-slate-400">{leadPhone}</p>
                    </div>
                </div>
                <div className="flex items-center gap-2">
                    <button onClick={onClose} className="p-2 hover:bg-white/5 rounded-full text-slate-400 transition-colors">
                        <X size={18} />
                    </button>
                </div>
            </div>

            {/* Chat Area */}
            <div ref={scrollRef} className="flex-1 overflow-y-auto p-4 space-y-4 bg-slate-950/50 scroll-smooth">
                {isLoading && (
                    <div className="flex justify-center py-8">
                        <Loader2 className="animate-spin text-slate-500" />
                    </div>
                )}

                {!isLoading && messages.length === 0 && (
                    <div className="text-center py-8 text-slate-500 text-sm">
                        No messages yet. Start the conversation!
                    </div>
                )}

                {messages.map((msg) => {
                    const isOutbound = msg.direction === 'outbound';
                    return (
                        <div
                            key={msg.id}
                            className={`flex ${isOutbound ? 'justify-end' : 'justify-start'}`}
                        >
                            <div
                                className={`max-w-[75%] px-4 py-2 rounded-2xl text-sm leading-relaxed ${isOutbound
                                    ? 'bg-indigo-600 text-white rounded-br-none'
                                    : 'bg-slate-800 text-slate-200 rounded-bl-none'
                                    }`}
                            >
                                <p>{msg.body}</p>
                                <div className={`text-[10px] mt-1 flex items-center gap-1 ${isOutbound ? 'text-indigo-200 justify-end' : 'text-slate-500'
                                    }`}>
                                    {formatTime(msg.createdAt)}
                                    {isOutbound && (
                                        <span>
                                            {msg.status === 'delivered' ? '' : msg.status === 'sent' ? '' : ''}
                                        </span>
                                    )}
                                </div>
                            </div>
                        </div>
                    );
                })}
            </div>

            {/* Input Area */}
            <div className="p-4 bg-slate-800/50 border-t border-white/5">
                <div className="flex items-end gap-2 bg-slate-900/50 p-2 rounded-xl border border-white/5 focus-within:border-indigo-500/50 transition-colors">
                    <textarea
                        value={newMessage}
                        onChange={(e) => setNewMessage(e.target.value)}
                        onKeyDown={handleKeyDown}
                        placeholder="iMessage..."
                        className="flex-1 bg-transparent border-none text-white placeholder-slate-500 focus:ring-0 text-sm resize-none max-h-32 p-2"
                        rows={1}
                        style={{ minHeight: '40px' }}
                    />
                    <button
                        onClick={handleSend}
                        disabled={!newMessage.trim() || isSending}
                        className={`p-2 rounded-full mb-0.5 transition-all ${newMessage.trim()
                            ? 'bg-indigo-500 text-white hover:bg-indigo-600'
                            : 'bg-slate-800 text-slate-500'
                            }`}
                    >
                        {isSending ? <Loader2 size={18} className="animate-spin" /> : <Send size={18} />}
                    </button>
                </div>
            </div>
        </div>
    );
}

'use client';

import React, { useState } from 'react';
import { ShieldCheck, Loader2, AlertCircle, CheckCircle2 } from 'lucide-react';
import { toast } from 'sonner';

interface VerifyNumberButtonProps {
    phoneNumber: string;
    onVerified?: (data: any) => void;
}

export function VerifyNumberButton({ phoneNumber, onVerified }: VerifyNumberButtonProps) {
    const [isLoading, setIsLoading] = useState(false);
    const [status, setStatus] = useState<'idle' | 'valid' | 'invalid'>('idle');
    const [carrierInfo, setCarrierInfo] = useState<string | null>(null);

    const handleVerify = async (e: React.MouseEvent) => {
        e.stopPropagation();

        if (!phoneNumber) {
            toast.error('No phone number to verify');
            return;
        }

        setIsLoading(true);
        setStatus('idle');
        setCarrierInfo(null);

        try {
            const response = await fetch('/api/twilio/lookup', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ phoneNumber }),
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.error || 'Verification failed');
            }

            if (data.valid) {
                setStatus('valid');
                const carrier = data.carrier?.name || 'Unknown Carrier';
                const type = data.carrier?.type?.toUpperCase() || '';
                const info = `${carrier} ${type ? `(${type})` : ''}`;
                setCarrierInfo(info);
                toast.success(`Number Verified: ${info}`);

                if (onVerified) onVerified(data);
            } else {
                setStatus('invalid');
                toast.error('Invalid phone number');
            }

        } catch (error: unknown) {
            toast.error(error instanceof Error ? error.message : 'Failed to send code');
            setStatus('invalid');
            toast.error('Verification failed');
        } finally {
            setIsLoading(false);
        }
    };

    if (status === 'valid') {
        return (
            <div className="flex items-center gap-1.5 text-xs text-emerald-400 bg-emerald-500/10 px-2 py-1 rounded-full border border-emerald-500/20" title={carrierInfo || 'Verified Valid'}>
                <CheckCircle2 size={12} />
                <span>Valid</span>
            </div>
        );
    }

    if (status === 'invalid') {
        return (
            <div className="flex items-center gap-1.5 text-xs text-red-400 bg-red-500/10 px-2 py-1 rounded-full border border-red-500/20" title="Number appears invalid">
                <AlertCircle size={12} />
                <span>Invalid</span>
            </div>
        );
    }

    return (
        <button
            onClick={handleVerify}
            disabled={isLoading}
            className={`
                group flex items-center gap-1.5 px-2 py-1 rounded-full text-xs font-medium transition-all
                ${isLoading
                    ? 'bg-slate-800 text-slate-400 cursor-not-allowed'
                    : 'bg-indigo-500/10 text-indigo-400 hover:bg-indigo-500/20 hover:text-indigo-300 border border-indigo-500/20 hover:border-indigo-500/40'
                }
            `}
            title="Verify this number with Twilio Lookup"
        >
            {isLoading ? (
                <Loader2 size={12} className="animate-spin" />
            ) : (
                <ShieldCheck size={12} />
            )}
            <span>{isLoading ? 'Verifying...' : 'Verify'}</span>
        </button>
    );
}

'use client';

import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { GlassCard } from '@/components/ui/GlassCard';
import { ShieldAlert, Target, Zap, Rocket, Check, ChevronRight, X } from 'lucide-react';
import confetti from 'canvas-confetti';
import { ProfileService } from '@/lib/firebase/services';
import { useAuth } from '@/components/providers/AuthProvider';

interface TourStep {
    title: string;
    description: string;
    icon: React.ReactNode;
    color: string;
}

const TOUR_STEPS: TourStep[] = [
    {
        title: "Welcome to SalesTracker AI",
        description: "Your new command center for high-stakes sales. We've built this to help you master every interaction using advanced enterprise telemetry.",
        icon: <Rocket className="w-8 h-8 text-blue-400" />,
        color: "blue"
    },
    {
        title: "The Intelligence Pipeline",
        description: "Track your leads with sub-millisecond precision. Use the new Export feature to keep your data portable and secure.",
        icon: <Zap className="w-8 h-8 text-amber-400" />,
        color: "amber"
    },
    {
        title: "The Simulation Lab",
        description: "Go beyond tracking. Enter the War Room or the Objection Dojo to wargame deals with aggressive AI buying committees.",
        icon: < ShieldAlert className="w-8 h-8 text-red-400" />,
        color: "red"
    },
    {
        title: "You're Ready to Scale",
        description: "All enterprise guardrails are active. Your journey to venture-scale performance starts now.",
        icon: <Check className="w-8 h-8 text-green-400" />,
        color: "green"
    }
];

export function WelcomeTour() {
    const { user, profile, refreshProfile } = useAuth();
    const [currentStep, setCurrentStep] = useState(0);
    const [isOpen, setIsOpen] = useState(true);

    if (!profile || profile.onboarded || !isOpen) return null;

    const handleNext = async () => {
        if (currentStep < TOUR_STEPS.length - 1) {
            setCurrentStep(currentStep + 1);
        } else {
            // Final step
            confetti({
                particleCount: 150,
                spread: 70,
                origin: { y: 0.6 },
                colors: ['#6366f1', '#a855f7', '#ec4899']
            });

            if (user) {
                await ProfileService.setOnboarded(user.uid);
                await refreshProfile();
            }
            setIsOpen(false);
        }
    };

    const handleSkip = async () => {
        if (user) {
            await ProfileService.setOnboarded(user.uid);
            await refreshProfile();
        }
        setIsOpen(false);
    };

    const step = TOUR_STEPS[currentStep];

    return (
        <div className="fixed inset-0 z-[200] flex items-center justify-center p-6 bg-slate-950/60 backdrop-blur-sm">
            <motion.div
                initial={{ opacity: 0, scale: 0.9, y: 20 }}
                animate={{ opacity: 1, scale: 1, y: 0 }}
                className="w-full max-w-lg"
            >
                <GlassCard className="relative overflow-hidden border border-white/10 shadow-2xl">
                    {/* Background Glow */}
                    <div className={`absolute -top-24 -right-24 w-48 h-48 bg-${step.color}-500/10 blur-[80px] rounded-full`} />

                    <button
                        onClick={handleSkip}
                        className="absolute top-4 right-4 text-slate-500 hover:text-white transition-colors p-2"
                    >
                        <X size={20} />
                    </button>

                    <div className="p-8">
                        <AnimatePresence mode="wait">
                            <motion.div
                                key={currentStep}
                                initial={{ opacity: 0, x: 20 }}
                                animate={{ opacity: 1, x: 0 }}
                                exit={{ opacity: 0, x: -20 }}
                                transition={{ duration: 0.3 }}
                                className="text-center"
                            >
                                <div className={`w-16 h-16 bg-${step.color}-500/20 rounded-2xl flex items-center justify-center mx-auto mb-6 shadow-xl`}>
                                    {step.icon}
                                </div>

                                <h2 className="text-2xl font-bold text-white mb-4">
                                    {step.title}
                                </h2>

                                <p className="text-slate-400 leading-relaxed mb-8 min-h-[80px]">
                                    {step.description}
                                </p>
                            </motion.div>
                        </AnimatePresence>

                        <div className="flex items-center justify-between">
                            {/* Progress Dots */}
                            <div className="flex gap-2">
                                {TOUR_STEPS.map((_, i) => (
                                    <div
                                        key={i}
                                        className={`h-1.5 rounded-full transition-all duration-300 ${i === currentStep ? 'w-8 bg-indigo-500' : 'w-2 bg-slate-700'
                                            }`}
                                    />
                                ))}
                            </div>

                            <button
                                onClick={handleNext}
                                className="group flex items-center gap-2 px-6 py-3 bg-gradient-to-r from-indigo-600 to-violet-600 hover:from-indigo-500 hover:to-violet-500 text-white rounded-xl font-semibold transition-all shadow-lg active:scale-95"
                            >
                                {currentStep === TOUR_STEPS.length - 1 ? "Get Started" : "Continue"}
                                <ChevronRight className="w-4 h-4 group-hover:translate-x-1 transition-transform" />
                            </button>
                        </div>
                    </div>
                </GlassCard>
            </motion.div>
        </div>
    );
}

'use client';

import React, { useState, useEffect, useRef } from 'react';
import {
    X, Users, MessageSquare, Mic, MicOff,
    BrainCircuit, AlertCircle, Play, Send
} from 'lucide-react';
import Image from 'next/image';
import { GeminiService } from '@/lib/ai/gemini';
import { useAuth } from '@/components/providers/AuthProvider';
import { AIBoardroomAgent, AIBoardroomTranscriptItem } from '@/types/ai';
import { TimeMachine } from '@/components/training/TimeMachine';

interface BoardroomRunnerProps {
    onClose: () => void;
}

type Agent = AIBoardroomAgent;

type TranscriptItem = AIBoardroomTranscriptItem;

export function BoardroomRunner({ onClose }: BoardroomRunnerProps) {
    const { user } = useAuth();
    const [status, setStatus] = useState<'init' | 'playing' | 'loading_turn' | 'time_machine'>('init');
    const [agents, setAgents] = useState<Agent[]>([]);
    const [transcript, setTranscript] = useState<TranscriptItem[]>([]);
    const [inputValue, setInputValue] = useState("");
    const [outcome, setOutcome] = useState<'win' | 'loss'>('loss'); // Default to loss for drama
    const transcriptEndRef = useRef<HTMLDivElement>(null);

    // --- INITIALIZATION ---
    useEffect(() => {
        async function init() {
            try {
                const token = await user?.getIdToken();
                const scenario = await GeminiService.generateBoardroomScenario(token);
                if (scenario && scenario.stakeholders) {
                    setAgents(scenario.stakeholders);
                    setStatus('playing');
                    // Initial welcome message from the Champion
                    const champion = scenario.stakeholders.find((s: AIBoardroomAgent) => s.archetype === 'Champion') || scenario.stakeholders[0];
                    setTranscript([{
                        id: 'init',
                        speaker: champion.name,
                        speakerId: champion.id,
                        message: "Thanks for coming in. We&apos;re all here. Why don&apos;t you kick us off?",
                        type: 'speech'
                    }]);
                }
            } catch (e) {
                console.error("Failed to init boardroom", e);
            }
        }
        init();
    }, [user]);

    // --- SCROLL TO BOTTOM ---
    useEffect(() => {
        transcriptEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [transcript]);

    // --- GAME LOOP ---
    const handleTurn = async () => {
        if (!inputValue.trim() || status === 'loading_turn') return;

        const userMsg = inputValue;
        setInputValue("");
        setStatus('loading_turn');

        // 1. Add User Message
        const newTranscript = [...transcript, {
            id: Date.now().toString(),
            speaker: 'You',
            speakerId: 'user',
            message: userMsg,
            type: 'speech' as const
        }];
        setTranscript(newTranscript);

        try {
            const token = await user?.getIdToken();
            const result = await GeminiService.evaluateBoardroomTurn(agents, userMsg, newTranscript, token);

            if (result) {
                const turnId = Date.now().toString();

                // Add AI Responses to transcript
                const updates: TranscriptItem[] = [];

                // 2. Main Response
                if (result.response) {
                    const speaker = agents.find(a => a.id === result.mainSpeakerId);
                    updates.push({
                        id: `resp-${turnId}`,
                        speaker: speaker?.name || 'Unknown',
                        speakerId: result.mainSpeakerId,
                        message: result.response,
                        type: 'speech'
                    });
                }

                // 3. Whispers (Thoughts)
                if (result.whispers) {
                    result.whispers.forEach((w: { agentId: string; thought: string }, idx: number) => {
                        const agent = agents.find(a => a.id === w.agentId);
                        updates.push({
                            id: `whisp-${turnId}-${idx}`,
                            speaker: agent?.name || 'Unknown',
                            speakerId: w.agentId,
                            message: w.thought,
                            type: 'whisper'
                        });
                    });
                }

                // 4. Sidebar
                if (result.sidebar) {
                    const from = agents.find(a => a.id === result.sidebar!.fromId);
                    const to = agents.find(a => a.id === result.sidebar!.toId);
                    updates.push({
                        id: `side-${turnId}`,
                        speaker: from?.name || 'Unknown',
                        speakerId: result.sidebar.fromId,
                        targetId: result.sidebar.toId,
                        message: `(to ${to?.name?.split(' ')[0]}): ${result.sidebar.message}`,
                        type: 'sidebar'
                    });
                }

                setTranscript(prev => [...prev, ...updates]);
            }
        } catch (e) {
            console.error("Turn failed", e);
        } finally {
            setStatus('playing');
        }
    };

    const handleEndMeeting = () => {
        // Simple heuristic: If average patience > 50, it's a win.
        // In reality, this should be an AI evaluation.
        const avgPatience = agents.reduce((acc, curr) => acc + curr.patience, 0) / agents.length;
        setOutcome(avgPatience > 50 ? 'win' : 'loss');
        setStatus('time_machine');
    };

    if (status === 'time_machine') {
        return <TimeMachine transcript={transcript} outcome={outcome} onClose={onClose} />;
    }

    if (status === 'init') {
        return (
            <div className="fixed inset-0 z-50 bg-slate-950 flex flex-col items-center justify-center p-4">
                <div className="w-16 h-16 border-4 border-blue-500/30 border-t-blue-500 rounded-full animate-spin mb-4"></div>
                <h2 className="text-xl font-bold text-white tracking-widest uppercase">Assembling The Board...</h2>
            </div>
        );
    }

    return (
        <div className="fixed inset-0 z-50 bg-slate-950 text-slate-200 font-sans flex flex-col">

            {/* HEADER */}
            <div className="h-16 border-b border-white/10 bg-slate-900 flex items-center justify-between px-6 shrink-0 relative z-20">
                <h1 className="text-lg font-bold text-white uppercase tracking-wider flex items-center gap-2">
                    <Users className="text-blue-500" /> The Boardroom
                </h1>
                <div className="flex items-center gap-2">
                    <button
                        onClick={handleEndMeeting}
                        className="px-3 py-1 bg-red-500/10 text-red-400 hover:bg-red-500 hover:text-white rounded-full text-xs font-bold uppercase tracking-wider border border-red-500/20 transition-all"
                    >
                        End Meeting
                    </button>
                    <button onClick={onClose} className="p-2 hover:bg-white/10 rounded-full transition-colors">
                        <X size={20} />
                    </button>
                </div>
            </div>

            {/* MAIN STAGE (Isometric View Simulation) */}
            <div className="flex-1 overflow-hidden relative bg-[#0f172a] perspective-1000">

                {/* Table Surface */}
                <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-[800px] h-[400px] bg-indigo-950/20 rounded-[100px] border border-indigo-500/20 shadow-[0_0_100px_rgba(79,70,229,0.1)] transform rotate-x-60"></div>

                {/* Agents */}
                <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
                    {/* Left Agent (Champion) */}
                    <div className="absolute left-[15%] top-[40%] text-center transform hover:scale-105 transition-transform duration-500 pointer-events-auto">
                        <AgentAvatar agent={agents[1]} isSpeaking={false} />
                    </div>

                    {/* Middle Agent (Decision Maker) */}
                    <div className="absolute top-[20%] text-center transform hover:scale-105 transition-transform duration-500 pointer-events-auto">
                        <AgentAvatar agent={agents[2]} isSpeaking={false} />
                    </div>

                    {/* Right Agent (Blocker) */}
                    <div className="absolute right-[15%] top-[40%] text-center transform hover:scale-105 transition-transform duration-500 pointer-events-auto">
                        <AgentAvatar agent={agents[0]} isSpeaking={false} />
                    </div>
                </div>

                {/* Transcript Overlay (Floating HUD) */}
                <div className="absolute bottom-24 left-1/2 -translate-x-1/2 w-full max-w-3xl h-[300px] pointer-events-auto">
                    <div className="h-full overflow-y-auto px-4 space-y-4 custom-scrollbar mask-image-linear-to-t">
                        {transcript.map(item => (
                            <ChatBubble key={item.id} item={item} />
                        ))}
                        {status === 'loading_turn' && (
                            <div className="flex justify-start">
                                <div className="bg-slate-800/50 rounded-lg px-4 py-2 text-xs text-slate-400 italic flex items-center gap-2">
                                    <BrainCircuit size={12} className="animate-pulse" /> The Board is deliberating...
                                </div>
                            </div>
                        )}
                        <div ref={transcriptEndRef} />
                    </div>
                </div>

            </div>

            {/* INPUT AREA */}
            <div className="h-20 bg-slate-900 border-t border-white/5 p-4 flex items-center justify-center gap-4 relative z-20">
                <div className="w-full max-w-3xl relative">
                    <input
                        type="text"
                        value={inputValue}
                        onChange={(e) => setInputValue(e.target.value)}
                        onKeyDown={(e) => e.key === 'Enter' && handleTurn()}
                        placeholder="Make your pitch..."
                        className="w-full bg-slate-950/50 border border-slate-700 rounded-full px-6 py-3 text-white placeholder-slate-500 focus:outline-none focus:border-blue-500 focus:ring-1 focus:ring-blue-500 transition-all font-medium"
                        autoFocus
                    />
                    <button
                        onClick={handleTurn}
                        disabled={status === 'loading_turn' || !inputValue.trim()}
                        className="absolute right-2 top-1/2 -translate-y-1/2 p-2 bg-blue-600 hover:bg-blue-500 text-white rounded-full transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                        <Send size={18} />
                    </button>
                </div>
            </div>

        </div>
    );
}

// --- SUBCOMPONENTS ---

function AgentAvatar({ agent, isSpeaking }: { agent: Agent, isSpeaking: boolean }) {
    if (!agent) return null;
    return (
        <div className="flex flex-col items-center group">
            <div className={`
                w-24 h-24 rounded-full border-4 overflow-hidden bg-slate-800 shadow-2xl relative transition-all duration-300
                ${agent.archetype === 'Blocker' ? 'border-red-500/30 group-hover:border-red-500' :
                    agent.archetype === 'Champion' ? 'border-green-500/30 group-hover:border-green-500' :
                        'border-blue-500/30 group-hover:border-blue-500'}
                ${isSpeaking ? 'scale-110 shadow-[0_0_30px_rgba(255,255,255,0.2)]' : ''}
            `}>
                <Image
                    src={agent.avatar}
                    alt={agent.name}
                    fill
                    className="object-cover"
                />
            </div>

            {/* Nameplate */}
            <div className="mt-4 bg-slate-900/80 backdrop-blur border border-white/10 px-4 py-2 rounded-lg text-center transform transition-transform group-hover:-translate-y-1">
                <div className="font-bold text-white text-sm">{agent.name}</div>
                <div className="text-[10px] uppercase tracking-wider font-mono text-slate-400">{agent.role}</div>
            </div>

            {/* Hover Info */}
            <div className="mt-2 opacity-0 group-hover:opacity-100 transition-opacity text-[10px] bg-black/60 px-2 py-1 rounded text-slate-300">
                {agent.archetype}  Dom: {agent.dominance}%
            </div>
        </div>
    );
}

function ChatBubble({ item }: { item: TranscriptItem }) {
    if (item.type === 'whisper') {
        return (
            <div className="flex justify-center my-2">
                <div className="bg-slate-900/40 border border-slate-700/50 rounded-full px-4 py-1.5 flex items-center gap-2 max-w-lg backdrop-blur-sm animate-in fade-in zoom-in-95">
                    <BrainCircuit size={12} className="text-purple-400" />
                    <span className="text-[10px] font-bold text-purple-400 uppercase tracking-wider">{item.speaker}&apos;s Thought:</span>
                    <span className="text-xs text-slate-300 italic">&ldquo;{item.message}&rdquo;</span>
                </div>
            </div>
        );
    }

    if (item.type === 'sidebar') {
        return (
            <div className="flex justify-center my-2">
                <div className="bg-amber-900/20 border border-amber-500/30 rounded-full px-4 py-1.5 flex items-center gap-2 max-w-lg backdrop-blur-sm animate-in fade-in zoom-in-95">
                    <MessageSquare size={12} className="text-amber-500" />
                    <span className="text-[10px] font-bold text-amber-500 uppercase tracking-wider">Sidebar ({item.speaker}):</span>
                    <span className="text-xs text-amber-200/80 italic">&ldquo;{item.message}&rdquo;</span>
                </div>
            </div>
        );
    }

    // Standard Speech
    const isUser = item.speakerId === 'user';
    return (
        <div className={`flex ${isUser ? 'justify-end' : 'justify-start'} animate-in slide-in-from-bottom-2 duration-300`}>
            <div className={`max-w-md p-4 rounded-2xl shadow-lg border ${isUser
                ? 'bg-blue-600 text-white rounded-br-none border-blue-500'
                : 'bg-slate-800 text-slate-200 rounded-bl-none border-slate-700'
                }`}>
                {!isUser && <div className="text-xs font-bold text-slate-400 mb-1">{item.speaker}</div>}
                <div className="text-sm leading-relaxed">{item.message}</div>
            </div>
        </div>
    );
}

'use client';

import React, { useState, useEffect, useRef } from 'react';
import { X, Mic, Send, User, Bot, AlertCircle, CheckCircle, RefreshCw, Loader2, Database, FileText } from 'lucide-react';
import { GeminiService } from '@/lib/ai/gemini';
import { ResourcesService } from '@/lib/firebase/resources';
import { Resource } from '@/types';
import { useAuth } from '@/components/providers/AuthProvider';
import { AIPersona } from '@/types/ai';

interface DojoRunnerProps {
    onClose: () => void;
}

type Message = {
    id: string;
    sender: 'user' | 'ai';
    text: string;
    timestamp: number;
    score?: {
        empathy: number;
        clarity: number;
        confidence: number;
    };
};

// Default persona if generation fails
const DEFAULT_PERSONA: AIPersona = {
    name: "Marcus Steele",
    role: "CFO",
    company: "Nexus Financial",
    personality: "Direct, skeptical, focused on bottom-line ROI. Impatient with fluff.",
    painPoints: ["Rising operational costs", "Vendor sprawl"],
    hiddenAgenda: "Looking to cut 10% of budget to meet Q4 targets",
    objections: ["Price is too high", "Timing isn't right"]
};

export function DojoRunner({ onClose }: DojoRunnerProps) {
    const [messages, setMessages] = useState<Message[]>([]);
    const [inputText, setInputText] = useState('');
    const [isRecording, setIsRecording] = useState(false);
    const [isTyping, setIsTyping] = useState(false);
    const [loadingPersona, setLoadingPersona] = useState(false); // Start false, wait for setup
    const [persona, setPersona] = useState<AIPersona | null>(null);
    const [status, setStatus] = useState<'setup' | 'playing'>('setup');

    // Setup State
    const [resources, setResources] = useState<Resource[]>([]);
    const [selectedResourceId, setSelectedResourceId] = useState<string | null>(null);
    const [loadingResources, setLoadingResources] = useState(false);
    const [contextContent, setContextContent] = useState<string>("");

    const chatEndRef = useRef<HTMLDivElement>(null);
    const { user } = useAuth();

    // Fetch resources on mount
    useEffect(() => {
        setLoadingResources(true);
        const fetchData = async () => {
            try {
                const companyData = await ResourcesService.getCompanyResources();
                let userData: Resource[] = [];
                if (user) {
                    userData = await ResourcesService.getUserResources(user.uid);
                }
                const allData = [...companyData, ...userData];

                // Filter for likely text-based files
                const textBased = allData.filter(r => r.type === 'document' || r.type === 'sheet' || r.title.endsWith('.txt') || r.title.endsWith('.md'));
                setResources(textBased);
            } catch (err) {
                console.error(err);
            } finally {
                setLoadingResources(false);
            }
        };

        fetchData();
    }, [user]);

    const startSession = async () => {
        setStatus('playing');
        setLoadingPersona(true);

        try {
            // 1. Fetch text if selected
            let content = "";
            if (selectedResourceId) {
                const res = resources.find(r => r.id === selectedResourceId);
                if (res?.url) {
                    try {
                        const response = await fetch(res.url);
                        content = await response.text();
                        setContextContent(content); // Save for replies
                    } catch (e) { console.error(e); }
                }
            }

            // 2. Generate Persona
            const token = await user?.getIdToken();
            const newPersona = await GeminiService.generatePersona("CFO", "SaaS Tech", content, token);
            const activePersona = newPersona || DEFAULT_PERSONA;

            setPersona(activePersona);

            // 3. Greeting
            const greeting = await GeminiService.generateReply(activePersona, [], "Start the conversation by stating your main objection immediately.", content, token);

            setMessages([{
                id: 'welcome',
                sender: 'ai',
                text: greeting,
                timestamp: Date.now()
            }]);

        } catch (error) {
            console.error("Failed to init Dojo", error);
            setPersona(DEFAULT_PERSONA);
        } finally {
            setLoadingPersona(false);
        }
    };

    // Auto-scroll to bottom
    useEffect(() => {
        chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [messages, isTyping]);

    const handleSend = async () => {
        if (!inputText.trim() || !persona) return;

        const newUserMsg: Message = {
            id: Date.now().toString(),
            sender: 'user',
            text: inputText,
            timestamp: Date.now(),
        };

        setMessages(prev => [...prev, newUserMsg]);
        setInputText('');
        setIsTyping(true);

        try {
            // Convert current messages to history format
            const history = messages.map(m => ({
                role: m.sender,
                content: m.text
            }));

            // Add latest user message to context implicitly or explicitly
            // Add latest user message to context implicitly or explicitly
            const token = await user?.getIdToken();
            const aiResponseText = await GeminiService.generateReply(persona, history, newUserMsg.text, contextContent, token);

            const newAiMsg: Message = {
                id: (Date.now() + 1).toString(),
                sender: 'ai',
                text: aiResponseText,
                timestamp: Date.now(),
                // Mock score for now until we add a specific "Evaluate" endpoint
                score: {
                    empathy: Math.floor(Math.random() * 20) + 70,
                    clarity: Math.floor(Math.random() * 20) + 75,
                    confidence: Math.floor(Math.random() * 20) + 80,
                }
            };

            setMessages(prev => [...prev, newAiMsg]);
        } catch (error) {
            console.error("AI Reply Failed", error);
        } finally {
            setIsTyping(false);
        }
    };

    const handleKeyDown = (e: React.KeyboardEvent) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            handleSend();
        }
    };

    if (status === 'setup') {
        return (
            <div className="fixed inset-0 z-50 flex items-center justify-center bg-slate-950/95 backdrop-blur-md p-4">
                <div className="max-w-2xl w-full bg-slate-900 border border-slate-800 rounded-xl p-8 shadow-2xl relative overflow-hidden">
                    <div className="absolute top-0 right-0 w-64 h-64 bg-indigo-500/5 rounded-full blur-3xl -translate-y-1/2 translate-x-1/2 pointer-events-none"></div>

                    <button onClick={onClose} className="absolute top-4 right-4 text-slate-500 hover:text-white">
                        <X className="w-6 h-6" />
                    </button>

                    <div className="mb-8">
                        <h2 className="text-3xl font-bold text-white mb-2 flex items-center gap-3">
                            <span className="p-2 bg-indigo-600/20 rounded-lg text-indigo-400"><Bot className="w-6 h-6" /></span>
                            Dojo Setup
                        </h2>
                        <p className="text-slate-400">Configure your sparring partner. Upload enablement docs to practice against real objections.</p>
                    </div>

                    <div className="space-y-6">
                        <div>
                            <label className="block text-sm font-medium text-slate-300 mb-3 uppercase tracking-wider">Enablement Context (Optional)</label>

                            {loadingResources ? (
                                <div className="flex items-center gap-2 text-slate-500 p-4 border border-dashed border-slate-800 rounded-lg">
                                    <Loader2 className="w-4 h-4 animate-spin" /> Loading resources...
                                </div>
                            ) : resources.length === 0 ? (
                                <div className="p-4 border border-dashed border-slate-800 rounded-lg text-slate-500 text-sm text-center">
                                    No text-based resources found. Upload documents in the Resources tab.
                                </div>
                            ) : (
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-3 max-h-60 overflow-y-auto pr-2 custom-scrollbar">
                                    {resources.map(res => (
                                        <div
                                            key={res.id}
                                            onClick={() => setSelectedResourceId(selectedResourceId === res.id ? null : res.id)}
                                            className={`
                                                p-3 rounded-lg border cursor-pointer transition-all flex items-center gap-3
                                                ${selectedResourceId === res.id
                                                    ? 'bg-indigo-600/20 border-indigo-500 text-white shadow-[0_0_15px_rgba(99,102,241,0.2)]'
                                                    : 'bg-slate-800/50 border-slate-700 text-slate-400 hover:bg-slate-800 hover:border-slate-600'}
                                            `}
                                        >
                                            <FileText className={`w-5 h-5 ${selectedResourceId === res.id ? 'text-indigo-400' : 'text-slate-500'}`} />
                                            <div className="flex-1 min-w-0">
                                                <div className="font-medium truncate text-sm">{res.title}</div>
                                                <div className="text-xs opacity-60 truncate">{res.category}</div>
                                            </div>
                                            {selectedResourceId === res.id && <CheckCircle className="w-4 h-4 text-indigo-400" />}
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>

                        <div className="pt-6 border-t border-slate-800 flex justify-end gap-3">
                            <button onClick={onClose} className="px-6 py-3 rounded-lg text-slate-400 font-medium hover:text-white transition-colors">
                                Cancel
                            </button>
                            <button
                                onClick={startSession}
                                className="px-8 py-3 rounded-lg bg-indigo-600 hover:bg-indigo-500 text-white font-bold shadow-lg shadow-indigo-900/20 flex items-center gap-2 transition-all transform hover:scale-[1.02]"
                            >
                                <Bot className="w-5 h-5" />
                                Enter Dojo
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    if (loadingPersona || !persona) {
        return (
            <div className="fixed inset-0 left-64 z-50 flex items-center justify-center p-4 bg-slate-900/95 backdrop-blur-md">
                <div className="flex flex-col items-center gap-4 text-white">
                    <Loader2 size={48} className="animate-spin text-indigo-500" />
                    <div className="text-xl font-bold animate-pulse">Summoning Prospect...</div>
                    <div className="text-sm text-slate-400">Generative AI is creating a unique persona</div>
                </div>
            </div>
        );
    }

    return (
        <div className="fixed inset-0 left-64 z-50 flex items-center justify-center p-4 bg-slate-900/95 backdrop-blur-md">
            <div className="w-full max-w-5xl h-[90vh] flex gap-6">

                {/* Main Chat Area */}
                <div className="flex-1 glass-card flex flex-col p-0 overflow-hidden relative">
                    {/* Header */}
                    <div className="p-4 border-b border-white/10 flex justify-between items-center bg-indigo-900/20">
                        <div className="flex items-center gap-3">
                            <div className="w-10 h-10 rounded-full bg-gradient-to-br from-indigo-500 to-violet-600 flex items-center justify-center border-2 border-indigo-400/30">
                                <Bot size={20} className="text-white" />
                            </div>
                            <div>
                                <h3 className="font-bold text-white">{persona.name}</h3>
                                <div className="flex items-center gap-1 text-xs text-indigo-300">
                                    <span className="w-2 h-2 rounded-full bg-green-500 animate-pulse"></span>
                                    {persona.role} @ {persona.company}
                                </div>
                            </div>
                        </div>
                        <button onClick={onClose} className="p-2 hover:bg-white/10 rounded-full text-slate-400 hover:text-white transition-colors">
                            <X size={20} />
                        </button>
                    </div>

                    {/* Messages */}
                    <div className="flex-1 overflow-y-auto p-6 space-y-6 custom-scrollbar">
                        <div className="p-4 bg-indigo-500/10 border border-indigo-500/20 rounded-xl mb-6">
                            <h4 className="text-sm font-bold text-indigo-300 flex items-center gap-2 mb-1">
                                <User size={14} /> Application Context
                            </h4>
                            <p className="text-xs text-slate-300 italic">
                                &ldquo;{persona.personality}&rdquo;
                                <br />
                                <span className="opacity-75">Agenda: {persona.painPoints[0]}</span>
                            </p>
                        </div>

                        {messages.map((msg) => (
                            <div key={msg.id} className={`flex gap-4 ${msg.sender === 'user' ? 'flex-row-reverse' : ''}`}>
                                <div className={`w-8 h-8 rounded-full flex-shrink-0 flex items-center justify-center ${msg.sender === 'user'
                                    ? 'bg-fuchsia-600 text-white'
                                    : 'bg-indigo-600 text-white'
                                    }`}>
                                    {msg.sender === 'user' ? <User size={14} /> : <Bot size={14} />}
                                </div>

                                <div className={`max-w-[70%] space-y-2`}>
                                    <div className={`p-4 rounded-2xl ${msg.sender === 'user'
                                        ? 'bg-fuchsia-600/20 border border-fuchsia-500/30 text-white rounded-tr-none'
                                        : 'bg-indigo-600/20 border border-indigo-500/30 text-slate-100 rounded-tl-none'
                                        }`}>
                                        <p className="leading-relaxed text-sm md:text-base">{msg.text}</p>
                                    </div>

                                    {/* Score Feedback */}
                                    {msg.sender === 'ai' && msg.id !== 'welcome' && (
                                        <div className="flex gap-2 text-xs">
                                            <span className="px-2 py-0.5 rounded-full bg-emerald-500/10 text-emerald-400 border border-emerald-500/20">
                                                Empathy: {msg.score?.empathy}%
                                            </span>
                                            <span className="px-2 py-0.5 rounded-full bg-blue-500/10 text-blue-400 border border-blue-500/20">
                                                Clarity: {msg.score?.clarity}%
                                            </span>
                                        </div>
                                    )}
                                </div>
                            </div>
                        ))}

                        {isTyping && (
                            <div className="flex gap-4">
                                <div className="w-8 h-8 rounded-full bg-indigo-600 flex items-center justify-center">
                                    <Bot size={14} className="text-white" />
                                </div>
                                <div className="bg-indigo-600/20 border border-indigo-500/30 px-4 py-3 rounded-2xl rounded-tl-none flex gap-1 items-center">
                                    <span className="w-2 h-2 bg-indigo-400 rounded-full animate-bounce"></span>
                                    <span className="w-2 h-2 bg-indigo-400 rounded-full animate-bounce delay-75"></span>
                                    <span className="w-2 h-2 bg-indigo-400 rounded-full animate-bounce delay-150"></span>
                                </div>
                            </div>
                        )}
                        <div ref={chatEndRef} />
                    </div>

                    {/* Input Area */}
                    <div className="p-4 bg-slate-900/50 border-t border-white/10">
                        <div className="flex gap-2">
                            <button
                                className={`p-3 rounded-xl border border-slate-700 transition-colors ${isRecording
                                    ? 'bg-red-500/20 border-red-500/50 text-red-500 animate-pulse'
                                    : 'hover:bg-slate-800 text-slate-400 hover:text-white'
                                    }`}
                                onClick={() => setIsRecording(!isRecording)}
                            >
                                <Mic size={20} />
                            </button>
                            <div className="flex-1 relative">
                                <input
                                    type="text"
                                    value={inputText}
                                    onChange={(e) => setInputText(e.target.value)}
                                    onKeyDown={handleKeyDown}
                                    placeholder="Type your objection handler..."
                                    className="w-full bg-slate-800/50 border border-slate-700 rounded-xl py-3 px-4 text-white focus:outline-none focus:border-indigo-500 focus:ring-1 focus:ring-indigo-500 placeholder:text-slate-600"
                                />
                            </div>
                            <button
                                onClick={handleSend}
                                disabled={!inputText.trim() || isTyping}
                                className="p-3 bg-indigo-600 hover:bg-indigo-500 text-white rounded-xl transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                            >
                                <Send size={20} />
                            </button>
                        </div>
                        <div className="text-center mt-2 text-xs text-slate-500">
                            Tip: Use &ldquo;Tactical Empathy&rdquo; to disarm the prospect.
                        </div>
                    </div>
                </div>

                {/* Right Sidebar: Real-time Analysis */}
                <div className="w-80 glass-card p-6 flex flex-col hidden lg:flex">
                    <h3 className="font-bold text-white mb-6 flex items-center gap-2">
                        <AlertCircle size={18} className="text-indigo-400" />
                        Live Coaching
                    </h3>

                    <div className="space-y-6">
                        <div className="p-4 rounded-xl bg-white/5 border border-white/10">
                            <div className="text-xs font-bold text-slate-400 uppercase mb-2">Current Tone</div>
                            <div className="flex items-center gap-2 text-emerald-400 font-semibold">
                                <CheckCircle size={16} /> Calm & Assertive
                            </div>
                        </div>

                        <div>
                            <div className="flex justify-between text-xs text-slate-400 mb-2">
                                <span>Pacing</span>
                                <span>120 wpm</span>
                            </div>
                            <div className="h-1 bg-slate-700 rounded-full overflow-hidden">
                                <div className="h-full bg-indigo-500 w-[60%]"></div>
                            </div>
                        </div>

                        <div>
                            <div className="flex justify-between text-xs text-slate-400 mb-2">
                                <span>Sentiment</span>
                                <span>Positive</span>
                            </div>
                            <div className="h-1 bg-slate-700 rounded-full overflow-hidden">
                                <div className="h-full bg-fuchsia-500 w-[75%]"></div>
                            </div>
                        </div>

                        <div className="mt-8">
                            <h4 className="font-bold text-white text-sm mb-3">AI Suggestions</h4>
                            <div className="space-y-2">
                                <button onClick={() => setInputText("It sounds like budget is a major concern specifically for Q4.")} className="w-full text-left p-2 rounded hover:bg-white/5 text-xs text-slate-300 border border-transparent hover:border-indigo-500/30 transition-all">
                                    &ldquo;It sounds like budget is a major concern...&rdquo;
                                </button>
                                <button onClick={() => setInputText("What if we structured this to start billing in January?")} className="w-full text-left p-2 rounded hover:bg-white/5 text-xs text-slate-300 border border-transparent hover:border-indigo-500/30 transition-all">
                                    &ldquo;What if we structured this to start billing in Jan?&rdquo;
                                </button>
                            </div>
                        </div>
                    </div>
                </div>

            </div>
        </div>
    );
}

'use client';

import React, { useState, useEffect } from 'react';
import { X, Trophy, Target, Award, BrainCircuit, ArrowRight, CheckCircle, XCircle } from 'lucide-react';
import { Question } from '@/types/learning';
import { generateQuizBatch } from '@/lib/ai/courseGenerator';

interface EndlessQuizRunnerProps {
    onClose: () => void;
}

type QuizState = 'setup' | 'playing' | 'checkpoint_summary';

export function EndlessQuizRunner({ onClose }: EndlessQuizRunnerProps) {
    // Game Config & State
    const [gameState, setGameState] = useState<QuizState>('setup');
    const [checkpointSize, setCheckpointSize] = useState(10); // 10-50
    const [loading, setLoading] = useState(false);

    // Session Stats
    const [totalQuestionsAnswered, setTotalQuestionsAnswered] = useState(0);
    const [totalCorrect, setTotalCorrect] = useState(0);
    const [checkpointsPassed, setCheckpointsPassed] = useState(0);

    // Current Batch State
    const [questions, setQuestions] = useState<Question[]>([]);
    const [currentIndex, setCurrentIndex] = useState(0);
    const [selectedOption, setSelectedOption] = useState<number | null>(null);
    const [batchCorrect, setBatchCorrect] = useState(0);
    const [showExplanation, setShowExplanation] = useState(false);

    // Start a new batch
    const startCheckpoint = async () => {
        setLoading(true);
        const batch = await generateQuizBatch(checkpointSize);
        setQuestions(batch);
        setCurrentIndex(0);
        setBatchCorrect(0);
        setSelectedOption(null);
        setShowExplanation(false);
        setLoading(false);
        setGameState('playing');
    };

    // Handle Answer Selection
    const handleAnswer = (optionIndex: number) => {
        if (selectedOption !== null) return; // Prevent double clicking

        setSelectedOption(optionIndex);
        setShowExplanation(true);

        const isCorrect = optionIndex === questions[currentIndex].correctOptionIndex;
        if (isCorrect) {
            setBatchCorrect(prev => prev + 1);
            setTotalCorrect(prev => prev + 1);
        }
        setTotalQuestionsAnswered(prev => prev + 1);
    };

    // Next Question or Checkpoint Summary
    const handleNext = () => {
        if (currentIndex < questions.length - 1) {
            setCurrentIndex(prev => prev + 1);
            setSelectedOption(null);
            setShowExplanation(false);
        } else {
            // End of Batch
            setCheckpointsPassed(prev => prev + 1);
            setGameState('checkpoint_summary');
        }
    };

    const overallAccuracy = totalQuestionsAnswered > 0
        ? Math.round((totalCorrect / totalQuestionsAnswered) * 100)
        : 0;

    const batchAccuracy = questions.length > 0
        ? Math.round((batchCorrect / questions.length) * 100)
        : 0;

    // --- SETUP SCREEN ---
    if (gameState === 'setup') {
        return (
            <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-slate-900/80 backdrop-blur-sm animate-in fade-in duration-200">
                <div className="glass-card w-full max-w-lg relative overflow-hidden p-8">
                    <button onClick={onClose} className="absolute top-4 right-4 text-slate-400 hover:text-white">
                        <X size={24} />
                    </button>

                    <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-fuchsia-500 to-indigo-500"></div>

                    <div className="text-center mb-10">
                        <div className="w-16 h-16 rounded-2xl bg-gradient-to-br from-fuchsia-600 to-indigo-600 flex items-center justify-center mx-auto mb-6 shadow-lg shadow-fuchsia-500/20">
                            <BrainCircuit size={32} className="text-white" />
                        </div>
                        <h2 className="text-3xl font-bold text-white mb-2">Endless Mastery</h2>
                        <p className="text-slate-400">
                            Test your sales knowledge against an infinite stream of AI-generated scenarios.
                        </p>
                    </div>

                    <div className="space-y-8">
                        <div>
                            <div className="flex justify-between text-sm font-semibold mb-4">
                                <span className="text-slate-300">Checkpoint Length</span>
                                <span className="text-fuchsia-400">{checkpointSize} Questions</span>
                            </div>
                            <input
                                type="range"
                                min="10"
                                max="50"
                                step="5"
                                value={checkpointSize}
                                onChange={(e) => setCheckpointSize(parseInt(e.target.value))}
                                className="w-full h-2 bg-slate-700 rounded-lg appearance-none cursor-pointer accent-fuchsia-500"
                            />
                            <div className="flex justify-between text-xs text-slate-500 mt-2">
                                <span>Quick (10)</span>
                                <span>Marathon (50)</span>
                            </div>
                        </div>

                        <button
                            onClick={startCheckpoint}
                            disabled={loading}
                            className="glass-button w-full py-4 text-lg font-bold flex items-center justify-center gap-2 group"
                        >
                            {loading ? (
                                <span className="animate-pulse">Generating...</span>
                            ) : (
                                <>
                                    Start Session <ArrowRight size={20} className="group-hover:translate-x-1 transition-transform" />
                                </>
                            )}
                        </button>
                    </div>
                </div>
            </div>
        );
    }

    // --- PLAYING SCREEN ---
    if (gameState === 'playing') {
        const currentQ = questions[currentIndex];
        return (
            <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-slate-900 bg-opacity-95 backdrop-blur-md">
                <div className="w-full max-w-4xl max-h-[90vh] flex flex-col">
                    {/* Header HUD */}
                    <div className="flex items-center justify-between mb-6 px-4">
                        <div className="flex items-center gap-4">
                            <div className="glass-panel px-4 py-2 rounded-full flex items-center gap-2 text-sm font-semibold text-slate-300">
                                <Target size={16} className="text-fuchsia-400" />
                                <span>Progress: {currentIndex + 1} / {questions.length}</span>
                            </div>
                            <div className="glass-panel px-4 py-2 rounded-full flex items-center gap-2 text-sm font-semibold text-slate-300">
                                <Trophy size={16} className="text-amber-400" />
                                <span>Score: {batchCorrect}</span>
                            </div>
                        </div>
                        <button onClick={onClose} className="p-2 rounded-full bg-slate-800 hover:bg-slate-700 text-slate-400 hover:text-white transition-colors">
                            <X size={24} />
                        </button>
                    </div>

                    {/* Question Card */}
                    <div className="glass-card flex-1 overflow-y-auto custom-scrollbar relative">
                        {/* Progress Bar */}
                        <div className="absolute top-0 left-0 h-1 bg-gradient-to-r from-fuchsia-500 to-indigo-500 transition-all duration-300" style={{ width: `${((currentIndex) / questions.length) * 100}%` }}></div>

                        <div className="max-w-3xl mx-auto py-8">
                            <h3 className="text-2xl md:text-3xl font-bold text-white mb-8 leading-tight">
                                {currentQ.question}
                            </h3>

                            <div className="space-y-4">
                                {currentQ.options.map((option, idx) => {
                                    const isSelected = selectedOption === idx;
                                    const isCorrect = idx === currentQ.correctOptionIndex;

                                    let buttonStyle = "border-slate-700 hover:border-slate-500 hover:bg-slate-800/50";
                                    if (showExplanation) {
                                        if (isCorrect) buttonStyle = "border-green-500 bg-green-500/10 text-green-200";
                                        else if (isSelected) buttonStyle = "border-red-500 bg-red-500/10 text-red-200";
                                        else buttonStyle = "border-slate-800 opacity-50";
                                    } else if (isSelected) {
                                        buttonStyle = "border-fuchsia-500 bg-fuchsia-500/10 text-white";
                                    }

                                    return (
                                        <button
                                            key={idx}
                                            onClick={() => handleAnswer(idx)}
                                            disabled={showExplanation}
                                            className={`w-full text-left p-6 rounded-xl border-2 transition-all duration-200 text-lg ${buttonStyle}`}
                                        >
                                            <div className="flex items-start gap-4">
                                                <div className={`mt-1 min-w-[24px] h-6 rounded-full border-2 flex items-center justify-center text-xs font-bold ${showExplanation && isCorrect ? 'border-green-500 bg-green-500 text-black' :
                                                    showExplanation && isSelected ? 'border-red-500 bg-red-500 text-white' :
                                                        'border-slate-600 text-slate-400'
                                                    }`}>
                                                    {["A", "B", "C", "D"][idx]}
                                                </div>
                                                <span>{option}</span>
                                            </div>
                                        </button>
                                    );
                                })}
                            </div>

                            {/* Explanation & Next */}
                            {showExplanation && (
                                <div className="mt-8 animate-in slide-in-from-bottom-4 fade-in duration-300">
                                    <div className={`p-6 rounded-xl border mb-6 ${selectedOption === currentQ.correctOptionIndex
                                        ? 'bg-green-950/30 border-green-500/30'
                                        : 'bg-red-950/30 border-red-500/30'
                                        }`}>
                                        <div className="flex items-center gap-2 font-bold mb-2">
                                            {selectedOption === currentQ.correctOptionIndex ? (
                                                <><CheckCircle className="text-green-400" size={20} /> <span className="text-green-400">Correct!</span></>
                                            ) : (
                                                <><XCircle className="text-red-400" size={20} /> <span className="text-red-400">Incorrect</span></>
                                            )}
                                        </div>
                                        <p className="text-slate-300 leading-relaxed">
                                            {currentQ.explanation}
                                        </p>
                                    </div>

                                    <button
                                        onClick={handleNext}
                                        className="glass-button w-full py-4 text-lg font-bold flex items-center justify-center gap-2"
                                    >
                                        {currentIndex < questions.length - 1 ? 'Next Question' : 'Finish Checkpoint'} <ArrowRight size={20} />
                                    </button>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    // --- CHECKPOINT SUMMARY ---
    if (gameState === 'checkpoint_summary') {
        return (
            <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-slate-900/90 backdrop-blur-md">
                <div className="glass-card w-full max-w-lg text-center p-10 animate-in zoom-in-95 duration-300">
                    <div className="w-20 h-20 rounded-full mx-auto mb-6 flex items-center justify-center bg-gradient-to-br from-amber-400 to-orange-500 shadow-xl shadow-orange-500/20">
                        <Award size={40} className="text-white" />
                    </div>

                    <h2 className="text-3xl font-bold text-white mb-2">Checkpoint Cleared!</h2>
                    <p className="text-slate-400 mb-8">Great work. Here&apos;s how you performed.</p>

                    <div className="grid grid-cols-2 gap-4 mb-8">
                        <div className="glass-panel p-4 rounded-xl">
                            <div className="text-slate-400 text-sm mb-1">Accuracy</div>
                            <div className={`text-2xl font-bold ${batchAccuracy >= 80 ? 'text-green-400' : 'text-fuchsia-400'}`}>
                                {batchAccuracy}%
                            </div>
                        </div>
                        <div className="glass-panel p-4 rounded-xl">
                            <div className="text-slate-400 text-sm mb-1">Score</div>
                            <div className="text-2xl font-bold text-white">
                                {batchCorrect} / {questions.length}
                            </div>
                        </div>
                    </div>

                    {/* Overall Session Stats */}
                    <div className="border-t border-white/10 pt-6 mb-8">
                        <div className="text-xs font-bold text-slate-500 uppercase tracking-wider mb-4">Session Total</div>
                        <div className="flex justify-between text-sm">
                            <span className="text-slate-400">Total Questions</span>
                            <span className="text-white font-mono">{totalQuestionsAnswered}</span>
                        </div>
                        <div className="flex justify-between text-sm mt-2">
                            <span className="text-slate-400">Avg Accuracy</span>
                            <span className="text-white font-mono">{overallAccuracy}%</span>
                        </div>
                        <div className="flex justify-between text-sm mt-2">
                            <span className="text-slate-400">Checkpoints</span>
                            <span className="text-white font-mono">{checkpointsPassed}</span>
                        </div>
                    </div>

                    <div className="flex gap-4">
                        <button
                            onClick={onClose}
                            className="flex-1 py-3 px-6 rounded-xl border border-slate-600 text-slate-300 hover:bg-slate-800 transition-colors font-semibold"
                        >
                            End Session
                        </button>
                        <button
                            onClick={() => setGameState('setup')}
                            className="flex-1 glass-button py-3 px-6 font-semibold"
                        >
                            Continue
                        </button>
                    </div>
                </div>
            </div>
        );
    }

    return null;
}

'use client';

import React, { useState } from 'react';
import { Course, Module, Lesson } from '@/types/learning';
import { X, ChevronLeft, Layout, CheckCircle, Lock } from 'lucide-react';
import { SlideDeck } from './SlideDeck';
import { QuizComponent } from './QuizComponent';
import { SlideContent, QuizContent } from '@/types/learning';
import confetti from 'canvas-confetti';

interface LMSPlayerProps {
    course: Course;
    onClose: () => void;
}

export function LMSPlayer({ course, onClose }: LMSPlayerProps) {
    // Flatten lessons for simpler navigation logic for this MVP
    // In a real app, we'd handle module boundaries more strictly
    const allLessons = course.modules.flatMap(m => m.lessons);

    const [currentLessonIndex, setCurrentLessonIndex] = useState(0);
    const [completedLessons, setCompletedLessons] = useState<Set<string>>(new Set());
    const [isSidebarOpen, setIsSidebarOpen] = useState(true);

    const toggleSidebar = () => setIsSidebarOpen(!isSidebarOpen);

    const currentLesson = allLessons[currentLessonIndex];
    const currentModule = course.modules.find(m => m.lessons.some(l => l.id === currentLesson.id));

    const handleLessonComplete = () => {
        const newCompleted = new Set(completedLessons);
        newCompleted.add(currentLesson.id);
        setCompletedLessons(newCompleted);

        // Auto-advance if not last
        if (currentLessonIndex < allLessons.length - 1) {
            setTimeout(() => {
                setCurrentLessonIndex(prev => prev + 1);
            }, 1000);
        } else {
            // Course Complete!
            confetti({
                particleCount: 200,
                spread: 100,
                origin: { y: 0.6 }
            });
        }
    };

    return (
        <div className="fixed inset-0 z-[100] bg-[#0b1121] text-white flex flex-col md:flex-row animate-fade-in">

            {/* Sidebar (Lesson Navigation) */}
            <aside
                className={`
                    bg-slate-900 border-r border-white/5 flex-shrink-0 flex flex-col transition-all duration-300 ease-in-out relative
                    ${isSidebarOpen ? 'w-full md:w-80 h-[30vh] md:h-full opacity-100' : 'w-0 h-0 md:h-full opacity-0 overflow-hidden border-none'}
                `}
            >
                <div className="p-4 border-b border-white/5 flex items-center justify-between gap-3">
                    <div className="flex items-center gap-3 overflow-hidden">
                        <button onClick={onClose} className="p-2 hover:bg-white/5 rounded-lg text-slate-400 hover:text-white transition-colors flex-shrink-0">
                            <X size={20} />
                        </button>
                        <h1 className="font-bold text-sm truncate whitespace-nowrap" title={course.title}>{course.title}</h1>
                    </div>
                    {/* Unique Close Sidebar Button */}
                    <button onClick={toggleSidebar} className="p-2 text-slate-400 hover:text-white md:hidden">
                        <Layout size={18} />
                    </button>
                </div>

                <div className="flex-1 overflow-y-auto p-4 space-y-6 custom-scrollbar whitespace-nowrap">
                    {course.modules.map((module) => (
                        <div key={module.id}>
                            <h3 className="text-xs font-bold text-slate-500 uppercase tracking-wider mb-3 px-2">
                                {module.title}
                            </h3>
                            <div className="space-y-1">
                                {module.lessons.map((lesson, idx) => {
                                    // Calculate global index for active state
                                    const globalIdx = allLessons.findIndex(l => l.id === lesson.id);
                                    const isActive = globalIdx === currentLessonIndex;
                                    const isCompleted = completedLessons.has(lesson.id);
                                    const isLocked = globalIdx > 0 && !completedLessons.has(allLessons[globalIdx - 1].id) && !isActive && !isCompleted;

                                    return (
                                        <button
                                            key={lesson.id}
                                            onClick={() => !isLocked && setCurrentLessonIndex(globalIdx)}
                                            disabled={isLocked}
                                            className={`w-full flex items-center gap-3 px-3 py-3 rounded-lg text-sm transition-all text-left
                                                ${isActive ? 'bg-indigo-600/10 text-indigo-300 border border-indigo-500/20' : 'text-slate-400 hover:bg-white/5'}
                                                ${isLocked ? 'opacity-50 cursor-not-allowed' : ''}
                                            `}
                                        >
                                            <div className={`w-6 h-6 rounded-full flex items-center justify-center flex-shrink-0 text-[10px] font-bold border
                                                ${isCompleted ? 'bg-emerald-500 border-emerald-500 text-white' :
                                                    isActive ? 'border-indigo-500 text-indigo-400' : 'border-slate-700 text-slate-600'}
                                            `}>
                                                {isCompleted ? <CheckCircle size={12} className="fill-emerald-500 text-white" /> : (idx + 1)}
                                            </div>
                                            <span className="truncate flex-1">{lesson.title}</span>
                                            {isLocked && <Lock size={12} className="text-slate-600" />}
                                        </button>
                                    );
                                })}
                            </div>
                        </div>
                    ))}
                </div>

                {/* Progress Footer */}
                <div className="p-4 border-t border-white/5 bg-slate-950/30">
                    <div className="flex justify-between text-xs text-slate-400 mb-2">
                        <span>Course Progress</span>
                        <span>{Math.round((completedLessons.size / allLessons.length) * 100)}%</span>
                    </div>
                    <div className="h-1.5 bg-slate-800 rounded-full overflow-hidden">
                        <div className="h-full bg-emerald-500 transition-all duration-500" style={{ width: `${(completedLessons.size / allLessons.length) * 100}%` }} />
                    </div>
                </div>
            </aside>

            {/* Main Content Player */}
            <main className="flex-1 flex flex-col relative overflow-hidden bg-slate-950">
                {/* Top Bar for Context */}
                <div className="h-16 border-b border-white/5 flex items-center justify-between px-4 md:px-8 bg-slate-900/50 backdrop-blur-md">
                    <div className="flex items-center gap-4 text-slate-400 text-sm overflow-hidden">
                        <button
                            onClick={toggleSidebar}
                            className="p-2 -ml-2 hover:bg-white/5 rounded-lg text-slate-400 hover:text-white transition-colors"
                            title={isSidebarOpen ? "Collapse Sidebar" : "Expand Sidebar"}
                        >
                            <Layout size={20} className={isSidebarOpen ? "text-indigo-400" : ""} />
                        </button>

                        <div className="flex items-center gap-2 truncate">
                            <span className="hidden md:inline">{currentModule?.title}</span>
                            <span className="text-slate-600 hidden md:inline">/</span>
                            <span className="text-white font-medium truncate">{currentLesson.title}</span>
                        </div>
                    </div>

                    {/* Close Course Button (Visible when sidebar is closed) */}
                    {!isSidebarOpen && (
                        <button onClick={onClose} className="p-2 hover:bg-white/5 rounded-lg text-slate-400 hover:text-white transition-colors">
                            <X size={20} />
                        </button>
                    )}
                </div>

                {/* Content Render Area */}
                <div className="flex-1 overflow-y-auto no-scrollbar relative">
                    {currentLesson.type === 'slide' && (
                        <SlideDeck
                            key={currentLesson.id} // Force remount on change
                            slides={(currentLesson.content as SlideContent).slides}
                            onComplete={handleLessonComplete}
                        />
                    )}

                    {currentLesson.type === 'quiz' && (
                        <QuizComponent
                            key={currentLesson.id}
                            content={currentLesson.content as QuizContent}
                            onComplete={(score) => {
                                handleLessonComplete();
                            }}
                        />
                    )}

                    {currentLesson.type === 'video' && (
                        <div className="flex items-center justify-center h-full text-slate-500">
                            Video Player Implementation Pending...
                        </div>
                    )}
                </div>
            </main>
        </div>
    );
}

'use client';

import React, { useState, useEffect, useRef } from 'react';
import { X, Mic, Square, Play, BarChart3, TrendingUp, AlertTriangle, CheckCircle2, Clock } from 'lucide-react';
import { GeminiService } from '@/lib/ai/gemini';
import { useAuth } from '@/components/providers/AuthProvider';
import { AIPitchAnalysis } from '@/types/ai';

interface PitchRecorderProps {
    onClose: () => void;
}

type RecordingState = 'idle' | 'recording' | 'analyzing' | 'complete';

export function PitchRecorder({ onClose }: PitchRecorderProps) {
    const [state, setState] = useState<RecordingState>('idle');
    const [duration, setDuration] = useState(0);
    const [transcript, setTranscript] = useState('');
    const [analysis, setAnalysis] = useState<AIPitchAnalysis | null>(null);
    const { user } = useAuth();

    const canvasRef = useRef<HTMLCanvasElement>(null);
    const timerRef = useRef<NodeJS.Timeout | null>(null);
    const recognitionRef = useRef<any>(null); // SpeechRecognition type is erratic in standard TS configs

    useEffect(() => {
        if (typeof window !== 'undefined') {
            const SpeechRecognition = (window as any).SpeechRecognition || (window as any).webkitSpeechRecognition;
            if (SpeechRecognition) {
                const recognition = new SpeechRecognition();
                recognition.continuous = true;
                recognition.interimResults = true;

                recognition.onresult = (event: SpeechRecognitionEvent) => {
                    let currentTranscript = '';
                    for (let i = event.resultIndex; i < event.results.length; i++) {
                        currentTranscript += event.results[i][0].transcript;
                    }
                    setTranscript(prev => prev + ' ' + currentTranscript);
                };

                recognitionRef.current = recognition;
            }
        }
    }, []);

    // Simulated Waveform Animation (Visuals only for now)
    useEffect(() => {
        if (state !== 'recording' || !canvasRef.current) return;

        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        if (!ctx) return;

        let animationId: number;
        const bars = 40;
        const barWidth = canvas.width / bars;

        const draw = () => {
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            ctx.fillStyle = '#06b6d4'; // Cyan-500

            for (let i = 0; i < bars; i++) {
                // Generate random bar height
                const height = Math.random() * (canvas.height * 0.8) + (canvas.height * 0.1);
                const x = i * barWidth;
                const y = (canvas.height - height) / 2;

                ctx.beginPath();
                ctx.roundRect(x + 2, y, barWidth - 4, height, 4);
                ctx.fill();
            }
            animationId = requestAnimationFrame(draw);
        };

        draw();

        return () => cancelAnimationFrame(animationId);
    }, [state]);

    // Timer Logic
    useEffect(() => {
        if (state === 'recording') {
            timerRef.current = setInterval(() => {
                setDuration(prev => prev + 1);
            }, 1000);
        } else if (state === 'idle') {
            const timeout = setTimeout(() => {
                setDuration(0);
                setTranscript('');
            }, 0);
            return () => clearTimeout(timeout);
        } else {
            if (timerRef.current) clearInterval(timerRef.current);
        }
        return () => {
            if (timerRef.current) clearInterval(timerRef.current);
        }
    }, [state]);

    const startRecording = () => {
        setTranscript('');
        setState('recording');
        if (recognitionRef.current) {
            try {
                recognitionRef.current.start();
            } catch (e) {
                console.error("Speech start error", e);
            }
        } else {
            alert("Speech Recognition not supported in this browser. Please use Chrome/Edge.");
        }
    };

    const stopRecording = async () => {
        setState('analyzing');
        if (recognitionRef.current) {
            recognitionRef.current.stop();
        }

        // Send to AI
        if (!transcript.trim()) {
            // Fallback for demo if mic failed or silence
            console.warn("No transcript captured. Using fallback.");
        }

        // Wait a tiny bit for final transcript
        await new Promise(r => setTimeout(r, 500));

        const textToAnalyze = transcript.trim() || "Hi, I'm calling from OmniStream to see if you have time to discuss your social media automation needs. Our tool is 20 percent cheaper than Hootsuite and has better analytics.";

        const token = await user?.getIdToken();
        const result = await GeminiService.analyzePitch(textToAnalyze, token);
        setAnalysis(result);
        setState('complete');
    };

    const formatTime = (seconds: number) => {
        const mins = Math.floor(seconds / 60);
        const secs = seconds % 60;
        return `${mins}:${secs.toString().padStart(2, '0')}`;
    };

    // --- IDLE STATE ---
    if (state === 'idle') {
        return (
            <div className="fixed inset-0 z-50 z-[100] overflow-y-auto bg-slate-900/90 backdrop-blur-md">
                <div className="flex min-h-full items-center justify-center p-4">
                    <div className="glass-card w-full max-w-lg text-center p-12 relative overflow-hidden my-auto">
                        <button onClick={onClose} className="absolute top-4 right-4 text-slate-400 hover:text-white">
                            <X size={24} />
                        </button>

                        <div className="mx-auto w-24 h-24 rounded-full bg-cyan-900/30 border-2 border-cyan-500/50 flex items-center justify-center mb-8 relative">
                            <div className="absolute inset-0 bg-cyan-500/20 rounded-full animate-ping"></div>
                            <Mic size={48} className="text-cyan-400 relative z-10" />
                        </div>

                        <h2 className="text-3xl font-bold text-white mb-4">Pitch Perfect</h2>
                        <p className="text-slate-400 mb-10 leading-relaxed">
                            Record your 30-second elevator pitch. Starts <b>listening</b> immediately.
                        </p>

                        <button
                            onClick={startRecording}
                            className="glass-button w-full py-4 text-lg font-bold bg-gradient-to-r from-cyan-600 to-blue-600 hover:from-cyan-500 hover:to-blue-500 border-none"
                        >
                            Start Recording
                        </button>
                        <div className="mt-4 text-xs text-slate-500 flex items-center justify-center gap-2">
                            <AlertTriangle size={12} /> Microphone access required
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    // --- RECORDING STATE ---
    if (state === 'recording') {
        return (
            <div className="fixed inset-0 z-50 z-[100] overflow-y-auto bg-slate-900/90 backdrop-blur-md">
                <div className="flex min-h-full items-center justify-center p-4">
                    <div className="glass-card w-full max-w-lg text-center p-12 relative my-auto">
                        <div className="mb-4 text-cyan-400 font-mono text-2xl tracking-widest">
                            {formatTime(duration)} / 0:30
                        </div>

                        <div className="h-32 w-full bg-slate-900/50 rounded-xl mb-10 overflow-hidden relative border border-white/5">
                            <canvas ref={canvasRef} width={400} height={128} className="w-full h-full" />
                        </div>

                        <div className="text-white text-lg font-medium mb-8 animate-pulse">
                            Listening...
                        </div>
                        {/* Live Transcript Preview */}
                        <div className="text-slate-500 text-xs mb-4 h-10 overflow-hidden">
                            {transcript.slice(-50)}...
                        </div>

                        <button
                            onClick={stopRecording}
                            className="w-20 h-20 rounded-full bg-red-500 hover:bg-red-600 flex items-center justify-center mx-auto shadow-lg shadow-red-500/30 transition-transform hover:scale-105"
                        >
                            <Square size={32} className="text-white fill-white" />
                        </button>
                        <div className="mt-4 text-xs text-slate-500 uppercase tracking-wider font-bold">Stop Recording</div>
                    </div>
                </div>
            </div>
        );
    }

    // --- ANALYZING STATE ---
    if (state === 'analyzing') {
        return (
            <div className="fixed inset-0 z-50 z-[100] overflow-y-auto bg-slate-900/90 backdrop-blur-md">
                <div className="flex min-h-full items-center justify-center p-4">
                    <div className="glass-card w-full max-w-md text-center p-12 my-auto">
                        <div className="w-16 h-16 border-4 border-cyan-500 border-t-transparent rounded-full animate-spin mx-auto mb-6"></div>
                        <h3 className="text-xl font-bold text-white mb-2">Analyzing Pitch...</h3>
                        <p className="text-slate-400 text-sm">Gemini 2.5 is reviewing your tone, pace, and content.</p>
                    </div>
                </div>
            </div>
        );
    }

    // --- COMPLETE STATE ---
    if (state === 'complete' && analysis) {
        return (
            <div className="fixed inset-0 z-50 z-[100] overflow-y-auto bg-slate-900/90 backdrop-blur-md">
                <div className="flex min-h-full items-center justify-center p-4">
                    <div className="glass-card w-full max-w-2xl relative p-0 overflow-hidden animate-in zoom-in-95 duration-300 my-auto">
                        <button onClick={onClose} className="absolute top-4 right-4 z-10 text-slate-400 hover:text-white">
                            <X size={24} />
                        </button>

                        <div className="bg-gradient-to-r from-cyan-900 to-blue-900 p-8 text-center relative overflow-hidden">
                            <div className="relative z-10">
                                <div className="text-cyan-300 text-sm font-bold uppercase tracking-widest mb-2">Analysis Complete</div>
                                <h2 className="text-4xl font-bold text-white mb-2">{analysis.score}/100</h2>
                                <p className="text-cyan-200">{analysis.oneLineFeedback}</p>
                            </div>
                            {/* Background Decor */}
                            <div className="absolute top-0 left-0 w-full h-full bg-[url('https://grainy-gradients.vercel.app/noise.svg')] opacity-20"></div>
                        </div>

                        <div className="p-8 grid grid-cols-1 md:grid-cols-3 gap-6">
                            {/* Pace */}
                            <div className="p-4 rounded-xl bg-white/5 border border-white/10 text-center">
                                <div className="mb-3 w-10 h-10 rounded-full bg-blue-500/20 flex items-center justify-center mx-auto text-blue-400">
                                    <Clock size={20} />
                                </div>
                                <div className="text-2xl font-bold text-white mb-1">{analysis.pace || '~'}</div>
                                <div className="text-xs text-slate-400 uppercase">Words / Min</div>
                                <div className="mt-2 text-xs text-green-400">Estimated</div>
                            </div>

                            {/* Filler Words */}
                            <div className="p-4 rounded-xl bg-white/5 border border-white/10 text-center">
                                <div className="mb-3 w-10 h-10 rounded-full bg-amber-500/20 flex items-center justify-center mx-auto text-amber-400">
                                    <AlertTriangle size={20} />
                                </div>
                                <div className="text-2xl font-bold text-white mb-1">{analysis.fillerWords?.length || 0}</div>
                                <div className="text-xs text-slate-400 uppercase">Filler Words</div>
                                <div className="mt-2 text-xs text-amber-400">{analysis.fillerWords?.join(', ') || 'None'}</div>
                            </div>

                            {/* Confidence */}
                            <div className="p-4 rounded-xl bg-white/5 border border-white/10 text-center">
                                <div className="mb-3 w-10 h-10 rounded-full bg-fuchsia-500/20 flex items-center justify-center mx-auto text-fuchsia-400">
                                    <TrendingUp size={20} />
                                </div>
                                <div className="text-2xl font-bold text-white mb-1">{analysis.confidence}</div>
                                <div className="text-xs text-slate-400 uppercase">Confidence</div>
                                <div className="mt-2 text-xs text-green-400">AI Analysis</div>
                            </div>
                        </div>

                        <div className="p-8 pt-0">
                            <div className="p-4 rounded-xl bg-slate-900/50 border border-white/5 mb-6">
                                <h4 className="font-bold text-white mb-2 flex items-center gap-2">
                                    <CheckCircle2 size={16} className="text-green-500" /> Key Strengths
                                </h4>
                                <ul className="text-sm text-slate-300 space-y-2 list-disc pl-5">
                                    {analysis.strengths?.map((s: string, i: number) => (
                                        <li key={i}>{s}</li>
                                    ))}
                                </ul>
                            </div>

                            {analysis.improvements && (
                                <div className="p-4 rounded-xl bg-slate-900/50 border border-white/5 mb-6">
                                    <h4 className="font-bold text-white mb-2 flex items-center gap-2">
                                        <AlertTriangle size={16} className="text-amber-500" /> Improvements
                                    </h4>
                                    <ul className="text-sm text-slate-300 space-y-2 list-disc pl-5">
                                        {analysis.improvements.map((s: string, i: number) => (
                                            <li key={i}>{s}</li>
                                        ))}
                                    </ul>
                                </div>
                            )}

                            <div className="flex gap-4">
                                <button onClick={() => setState('idle')} className="flex-1 glass-button py-3 text-sm font-bold">Try Again</button>
                                <button onClick={onClose} className="flex-1 py-3 border border-slate-700 rounded-xl text-slate-400 hover:text-white hover:bg-slate-800 transition-colors text-sm font-bold">Close</button>
                            </div>
                        </div>

                    </div>
                </div>
            </div>
        )
    }

    return null;
}

'use client';

import React, { useState } from 'react';
import { QuizContent } from '@/types/learning';
import { CheckCircle, XCircle, AlertCircle, RotateCcw, Trophy } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import confetti from 'canvas-confetti';

interface QuizComponentProps {
    content: QuizContent;
    onComplete: (score: number) => void;
}

export function QuizComponent({ content, onComplete }: QuizComponentProps) {
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [selectedOption, setSelectedOption] = useState<number | null>(null);
    const [isAnswered, setIsAnswered] = useState(false);
    const [score, setScore] = useState(0);
    const [showResults, setShowResults] = useState(false);

    const question = content.questions[currentQuestionIndex];
    const isCorrect = selectedOption === question.correctOptionIndex;

    const handleSelectOption = (index: number) => {
        if (isAnswered) return;
        setSelectedOption(index);
        setIsAnswered(true);

        if (index === question.correctOptionIndex) {
            setScore(s => s + 1);
        }
    };

    const handleNext = () => {
        if (currentQuestionIndex < content.questions.length - 1) {
            setCurrentQuestionIndex(prev => prev + 1);
            setSelectedOption(null);
            setIsAnswered(false);
        } else {
            finishQuiz();
        }
    };

    const finishQuiz = () => {
        setShowResults(true);
        const finalScore = Math.round(((score + (isCorrect ? 0 : 0)) / content.questions.length) * 100); // Wait, score is already updated? No, state update allows reading reliable next step.
        // Actually, let's recalculate based on current state knowing we passed previous step
        const calculateScore = Math.round((score / content.questions.length) * 100);

        if (calculateScore >= content.passingScore) {
            confetti({
                particleCount: 150,
                spread: 70,
                origin: { y: 0.6 }
            });
        }
        onComplete(calculateScore);
    };

    if (showResults) {
        const percentage = Math.round((score / content.questions.length) * 100);
        const passed = percentage >= content.passingScore;

        return (
            <div className="flex flex-col items-center justify-center h-full text-center p-8 animate-fade-in-up">
                <div className={`w-24 h-24 rounded-full flex items-center justify-center mb-6 shadow-2xl ${passed ? 'bg-emerald-500/20 text-emerald-400 border border-emerald-500/50' : 'bg-red-500/20 text-red-400 border border-red-500/50'}`}>
                    {passed ? <Trophy size={48} /> : <AlertCircle size={48} />}
                </div>

                <h2 className="text-3xl font-bold text-white mb-2">{passed ? 'Quiz Passed!' : 'Needs Improvement'}</h2>
                <p className="text-slate-400 mb-8 max-w-md">
                    {passed
                        ? 'Great job demonstrating your knowledge. You have officially completed this module.'
                        : 'You did not meet the passing score for this quiz. Review the material and try again to earn your XP.'}
                </p>

                <div className="text-6xl font-black text-white mb-2 tracking-tighter">{percentage}%</div>
                <div className="text-sm font-bold uppercase tracking-widest text-slate-500 mb-10">Final Score</div>

                <div className="flex gap-4">
                    {!passed && (
                        <button
                            onClick={() => {
                                setCurrentQuestionIndex(0);
                                setScore(0);
                                setSelectedOption(null);
                                setIsAnswered(false);
                                setShowResults(false);
                            }}
                            className="glass-button flex items-center gap-2 px-6 py-3"
                        >
                            <RotateCcw size={18} /> Retry Quiz
                        </button>
                    )}
                    <button
                        onClick={() => onComplete(percentage)} // Just close/finish
                        className="bg-indigo-600 hover:bg-indigo-500 text-white font-bold py-3 px-8 rounded-xl shadow-lg shadow-indigo-500/25 transition-all"
                    >
                        Success
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className="max-w-2xl mx-auto w-full py-10 px-4">
            {/* Header */}
            <div className="flex items-center justify-between mb-8">
                <div className="text-sm font-bold text-slate-500 uppercase tracking-wider">
                    Question {currentQuestionIndex + 1} / {content.questions.length}
                </div>
                <div className="flex items-center gap-2">
                    <span className="text-indigo-400 font-bold">{score}</span>
                    <span className="text-slate-600">points</span>
                </div>
            </div>

            {/* Question Card */}
            <div className="glass-card bg-slate-800/40 p-0 overflow-hidden border-none shadow-xl">
                <div className="p-8 border-b border-white/5 bg-slate-900/50">
                    <h3 className="text-xl md:text-2xl font-bold text-white leading-snug">
                        {question.question}
                    </h3>
                </div>

                <div className="p-4 space-y-3">
                    {question.options.map((option, index) => {
                        let statusClass = "border-white/5 hover:bg-white/5 hover:border-white/20 text-slate-300"; // Default

                        if (isAnswered) {
                            if (index === question.correctOptionIndex) {
                                statusClass = "bg-emerald-500/20 border-emerald-500/50 text-emerald-100 ring-1 ring-emerald-500/50";
                            } else if (index === selectedOption) {
                                statusClass = "bg-red-500/20 border-red-500/50 text-red-100";
                            } else {
                                statusClass = "opacity-50 border-transparent";
                            }
                        }

                        return (
                            <button
                                key={index}
                                onClick={() => handleSelectOption(index)}
                                disabled={isAnswered}
                                className={`w-full text-left p-4 rounded-xl border transition-all duration-200 flex items-center justify-between group ${statusClass} ${selectedOption === index ? 'ring-2 ring-indigo-500/50' : ''}`}
                            >
                                <span className="font-medium text-lg">{option}</span>
                                {isAnswered && index === question.correctOptionIndex && (
                                    <CheckCircle className="text-emerald-400" size={20} />
                                )}
                                {isAnswered && index === selectedOption && index !== question.correctOptionIndex && (
                                    <XCircle className="text-red-400" size={20} />
                                )}
                            </button>
                        );
                    })}
                </div>

                {/* Feedback & Continue */}
                <AnimatePresence>
                    {isAnswered && (
                        <motion.div
                            initial={{ height: 0, opacity: 0 }}
                            animate={{ height: 'auto', opacity: 1 }}
                            className={`p-6 border-t ${isCorrect ? 'bg-emerald-900/20 border-emerald-500/20' : 'bg-red-900/20 border-red-500/20'}`}
                        >
                            <div className="flex items-start gap-4 mb-6">
                                <div className={`mt-1 p-2 rounded-full ${isCorrect ? 'bg-emerald-500/20 text-emerald-400' : 'bg-red-500/20 text-red-400'}`}>
                                    {isCorrect ? <CheckCircle size={24} /> : <AlertCircle size={24} />}
                                </div>
                                <div>
                                    <h4 className={`font-bold mb-1 ${isCorrect ? 'text-emerald-300' : 'text-red-300'}`}>
                                        {isCorrect ? 'Correct!' : 'Incorrect'}
                                    </h4>
                                    <p className="text-slate-300 leading-relaxed text-sm">
                                        {question.explanation}
                                    </p>
                                </div>
                            </div>

                            <div className="flex justify-end">
                                <button
                                    onClick={handleNext}
                                    className="px-8 py-3 bg-white text-slate-900 font-bold rounded-xl hover:bg-slate-200 transition-colors shadow-lg shadow-white/10"
                                >
                                    {currentQuestionIndex === content.questions.length - 1 ? 'Finish Quiz' : 'Next Question'}
                                </button>
                            </div>
                        </motion.div>
                    )}
                </AnimatePresence>
            </div>
        </div>
    );
}

'use client';

import React, { useState, useEffect } from 'react';
import { GeminiService } from '@/lib/ai/gemini';
import { useAuth } from '@/components/providers/AuthProvider';
import { AIFutureArtifact, AIBoardroomTranscriptItem } from '@/types/ai';
import { X, ExternalLink, RefreshCw, Send, AlertTriangle, TrendingDown, TrendingUp, Lock } from 'lucide-react';
import Image from 'next/image';

interface TimeMachineProps {
    transcript: AIBoardroomTranscriptItem[];
    outcome: 'win' | 'loss';
    onClose: () => void;
}

export function TimeMachine({ transcript, outcome, onClose }: TimeMachineProps) {
    const { user } = useAuth();
    const [artifact, setArtifact] = useState<AIFutureArtifact | null>(null);
    const [loading, setLoading] = useState(true);
    const [year, setYear] = useState(2025);

    useEffect(() => {
        // Animation to "Travel Forward"
        const interval = setInterval(() => {
            setYear(prev => {
                if (prev >= 2026) {
                    clearInterval(interval);
                    return 2026;
                }
                return prev + 1;
            });
        }, 500);

        async function fetchArtifact() {
            try {
                const token = await user?.getIdToken();
                const result = await GeminiService.generateFutureArtifact(transcript, outcome, token);
                setArtifact(result);
            } catch (e) {
                console.error("Time Machine Failed", e);
            } finally {
                setLoading(false);
            }
        }

        fetchArtifact();
        return () => clearInterval(interval);
    }, [transcript, outcome, user]);

    if (loading) {
        return (
            <div className="fixed inset-0 z-[60] bg-black flex flex-col items-center justify-center overflow-hidden">
                <div className="text-6xl font-black text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-600 animate-pulse tracking-tighter">
                    {year}
                </div>
                <div className="mt-4 text-slate-500 font-mono text-sm tracking-widest uppercase animate-bounce">
                    Constructing Future Timeline...
                </div>
            </div>
        );
    }

    if (!artifact) return null;

    return (
        <div className="fixed inset-0 z-[60] bg-black/90 backdrop-blur-xl flex items-center justify-center p-4">
            <button onClick={onClose} className="absolute top-8 right-8 text-slate-500 hover:text-white transition-colors">
                <X size={32} />
            </button>
            <div className="max-w-4xl w-full animate-in zoom-in-95 duration-700">
                <div className="text-center mb-8">
                    <h2 className="text-2xl font-light text-slate-300 tracking-widest uppercase">
                        Timeline Status: <span className={outcome === 'win' ? "text-green-400 font-bold" : "text-red-500 font-bold"}>
                            {outcome === 'win' ? 'OPTIMAL' : 'CRITICAL FAILURE'}
                        </span>
                    </h2>
                    <p className="text-slate-500 text-sm mt-2">6 Months into the future...</p>
                </div>

                {/* ARTIFACT RENDERING LOGIC */}
                {artifact.type === 'news_article' && <NewsArtifact data={artifact} />}
                {artifact.type === 'email' && <EmailArtifact data={artifact} />}
                {artifact.type === 'slack' && <SlackArtifact data={artifact} />}
                {artifact.type === 'stock' && <StockArtifact data={artifact} />}

            </div>
        </div>
    );
}

// --- ARTIFACT COMPONENTS ---

function NewsArtifact({ data }: { data: AIFutureArtifact }) {
    return (
        <div className="bg-white text-black p-8 rounded-sm shadow-2xl skew-y-1 transform transition-all hover:skew-y-0">
            <div className="border-b-4 border-black pb-4 mb-6 flex justify-between items-end">
                <h1 className="text-4xl font-black tracking-tighter italic font-serif">TechCrunch</h1>
                <span className="font-mono text-sm text-gray-500">{data.date}</span>
            </div>
            <h2 className="text-3xl font-bold leading-tight mb-4">{data.headline}</h2>
            <div className="flex gap-6">
                <div className="w-1/3 bg-gray-200 h-48 rounded-sm flex items-center justify-center overflow-hidden grayscale relative">
                    {data.imageUrl && (
                        <Image
                            src={data.imageUrl}
                            alt="News"
                            fill
                            className="object-cover mix-blend-multiply"
                        />
                    )}
                </div>
                <div className="w-2/3 font-serif text-lg leading-relaxed text-gray-800">
                    <p className="first-letter:text-5xl first-letter:font-bold first-letter:mr-2 float-left">
                        {data.body?.charAt(0) || ""}
                    </p>
                    {data.body?.substring(1) || ""}
                </div>
            </div>
        </div>
    );
}

function EmailArtifact({ data }: { data: AIFutureArtifact }) {
    return (
        <div className="bg-white text-gray-900 rounded-lg shadow-2xl overflow-hidden border border-gray-200">
            <div className="bg-gray-100 p-2 border-b flex gap-2">
                <div className="w-3 h-3 rounded-full bg-red-400"></div>
                <div className="w-3 h-3 rounded-full bg-yellow-400"></div>
                <div className="w-3 h-3 rounded-full bg-green-400"></div>
            </div>
            <div className="p-8">
                <div className="flex items-center justify-between mb-8">
                    <div className="flex items-center gap-4">
                        <div className="w-12 h-12 bg-blue-600 rounded-full flex items-center justify-center text-white font-bold text-xl">
                            {data.from?.charAt(0) || "U"}
                        </div>
                        <div>
                            <div className="font-bold text-lg">{data.from || "Unknown Sender"}</div>
                            <div className="text-gray-500 text-sm">to {data.to || "User"}</div>
                        </div>
                    </div>
                    <div className="text-gray-400 text-sm">{data.date}</div>
                </div>
                <div className="text-2xl font-bold mb-6">{data.subject}</div>
                <div className="text-lg leading-relaxed whitespace-pre-wrap font-sans text-gray-700">
                    {data.body}
                </div>
                <div className="mt-12 pt-8 border-t border-gray-100 text-gray-400 text-sm italic">
                    Sent from my iPhone
                </div>
            </div>
        </div>
    );
}

function SlackArtifact({ data }: { data: AIFutureArtifact }) {
    return (
        <div className="bg-[#1a1d21] text-white rounded-lg shadow-2xl overflow-hidden border border-gray-800 w-full max-w-2xl mx-auto">
            <div className="bg-[#111315] p-4 border-b border-gray-800 flex items-center gap-3">
                <span className="text-gray-400 text-lg">#</span>
                <span className="font-bold">{data.channel}</span>
            </div>
            <div className="p-6 space-y-6">
                {data.messages?.map((msg, idx) => (
                    <div key={idx} className="flex gap-4 group">
                        <div className="w-10 h-10 rounded bg-gradient-to-tr from-purple-500 to-pink-500 flex items-center justify-center font-bold text-sm">
                            {msg.user.charAt(0)}
                        </div>
                        <div>
                            <div className="flex items-baseline gap-2">
                                <span className="font-bold text-sm">{msg.user}</span>
                                <span className="text-xs text-gray-500">10:4{idx} AM</span>
                            </div>
                            <p className="text-gray-300">{msg.text}</p>
                        </div>
                    </div>
                ))}
            </div>
            <div className="p-4 bg-[#222529] border-t border-gray-800">
                <div className="bg-[#1a1d21] border border-gray-600 rounded p-2 text-gray-500 text-sm">
                    Message #{data.channel}
                </div>
            </div>
        </div>
    );
}

function StockArtifact({ data }: { data: AIFutureArtifact }) {
    return (
        <div className="bg-slate-900 border border-slate-800 rounded-xl p-8 shadow-2xl relative overflow-hidden">
            <div className="absolute top-0 right-0 p-32 bg-red-500/10 blur-[100px] rounded-full"></div>

            <div className="flex justify-between items-start mb-12 relative z-10">
                <div>
                    <h1 className="text-6xl font-black text-white">{data.ticker}</h1>
                    <p className="text-slate-400 uppercase tracking-widest mt-2">{data.ticker} Corp.</p>
                </div>
                <div className="text-right">
                    <h2 className="text-6xl font-bold text-red-500 flex items-center justify-end gap-2">
                        <TrendingDown size={48} /> {data.price}
                    </h2>
                    <p className="text-red-400 font-mono text-xl mt-2">{data.change} Today</p>
                </div>
            </div>

            {/* Chart Line */}
            <div className="h-32 w-full flex items-end gap-1 mb-8">
                {[...Array(20)].map((_, i) => (
                    <div key={i} className="flex-1 bg-red-500/20 hover:bg-red-500/50 transition-colors" style={{ height: `${(i * 13) % 60 + 20}%` }}></div>
                ))}
            </div>

            <div className="bg-red-500/10 border-l-4 border-red-500 p-6 rounded-r-lg relative z-10">
                <div className="flex items-center gap-3 mb-2">
                    <AlertTriangle className="text-red-500" />
                    <span className="text-red-500 font-bold uppercase text-sm tracking-widest">Market Alert</span>
                </div>
                <p className="text-white text-xl font-bold leading-relaxed">{data.news}</p>
            </div>
        </div>
    );
}

'use client';

import React, { useState, useEffect, useRef } from 'react';
import {
    X, Mail, ShieldAlert, CheckCircle, Send, Users,
    Briefcase, Activity, Lock, Unlock, AlertTriangle,
    TrendingUp, TrendingDown, Clock, Play, RotateCcw,
    FileText, Phone, MessageSquare, Loader2, Database, ChevronRight
} from 'lucide-react';
import { GeminiService } from '@/lib/ai/gemini';
import { ResourcesService } from '@/lib/firebase/resources';
import { LeadsService } from '@/lib/firebase/services';
import { Resource, Lead } from '@/types';
import { AIStakeholder, AIScenario, DominanceLevel } from '@/types/ai';
import { useAuth } from '@/components/providers/AuthProvider';

interface WarRoomRunnerProps {
    onClose: () => void;
    initialContext?: {
        company: string;
        industry: string;
        value: string;
        stage?: string;
        contact?: string;
    };
}

// --- GAME TYPES ---

type Stakeholder = AIStakeholder & {
    status: 'unlocked' | 'locked'; // locked until engaged?
};

type ActionCard = {
    id: string;
    label: string;
    icon: React.ReactNode;
    risk: 'Low' | 'Medium' | 'High';
    description: string;
    cost: number; // Political capital cost?
};

type LogEntry = {
    id: string;
    type: 'system' | 'email' | 'event';
    title: string;
    description: string;
    timestamp: string;
    impact?: number;
    agentName?: string;
};

type GameState = {
    status: 'setup' | 'initializing' | 'playing' | 'won' | 'lost';
    dealHealth: number;
    scenario: Omit<AIScenario, 'stakeholders'> | null;
    stakeholders: Stakeholder[];
    logs: LogEntry[];
    turnCount: number;
    maxTurns: number;
};

// --- ACTION DECK ---
const ACTION_DECK: ActionCard[] = [
    { id: 'whitepaper', label: 'Send Whitepaper', icon: <FileText size={16} />, risk: 'Low', description: 'Safe, standard move. Good for analytical types.', cost: 1 },
    { id: 'demo', label: 'Schedule Demo', icon: <Play size={16} />, risk: 'Low', description: 'Show the product. effective if interest is real.', cost: 2 },
    { id: 'social', label: 'Social Engineering', icon: <MessageSquare size={16} />, risk: 'Medium', description: 'Dig for hidden agendas via backchannels.', cost: 2 },
    { id: 'ciso', label: 'CISO-to-CISO Call', icon: <Lock size={16} />, risk: 'Medium', description: 'High impact for security blockers.', cost: 3 },
    { id: 'executive', label: 'Executive Override', icon: <Briefcase size={16} />, risk: 'High', description: 'Go over their head. Huge risk, huge reward.', cost: 4 },
];

export function WarRoomRunner({ onClose, initialContext }: WarRoomRunnerProps) {
    const [gameState, setGameState] = useState<GameState>({
        status: 'setup',
        dealHealth: 50,
        scenario: null,
        stakeholders: [],
        logs: [],
        turnCount: 1,
        maxTurns: 8
    });

    const [selectedStakeholderId, setSelectedStakeholderId] = useState<string | null>(null);
    const [isProcessingTurn, setIsProcessingTurn] = useState(false);
    const logsEndRef = useRef<HTMLDivElement>(null);
    const { user } = useAuth();

    // Setup State
    const [resources, setResources] = useState<Resource[]>([]);
    const [leads, setLeads] = useState<Lead[]>([]);
    const [selectedResourceId, setSelectedResourceId] = useState<string | null>(null);
    const [selectedLeadId, setSelectedLeadId] = useState<string | null>(null);
    const [loadingResources, setLoadingResources] = useState(false);

    // Fetch resources and leads on mount
    useEffect(() => {
        setLoadingResources(true);
        const fetchData = async () => {
            try {
                // Resources
                const companyData = await ResourcesService.getCompanyResources();
                let userData: Resource[] = [];
                let userLeads: Lead[] = [];

                if (user) {
                    userData = await ResourcesService.getUserResources(user.uid);
                    userLeads = await LeadsService.getLeads(user.uid);
                }
                const allData = [...companyData, ...userData];

                // Filter for likely text-based files
                const textBased = allData.filter(r => r.type === 'document' || r.type === 'sheet' || r.title.endsWith('.txt') || r.title.endsWith('.md'));
                setResources(textBased);
                setLeads(userLeads);
            } catch (err) {
                console.error(err);
            } finally {
                setLoadingResources(false);
            }
        };

        fetchData();
    }, [user]);

    // --- GAME START ---
    const startGame = async () => {
        setGameState(prev => ({ ...prev, status: 'initializing' }));

        try {
            // 1. Fetch Context Content if selected
            let contextContent = "";
            if (selectedResourceId) {
                const res = resources.find(r => r.id === selectedResourceId);
                if (res?.url) {
                    try {
                        const response = await fetch(res.url);
                        contextContent = await response.text();
                    } catch (e) {
                        console.error("Failed to read resource text", e);
                    }
                }
            }

            // 2. Generate Scenario
            const level = Math.random() > 0.5 ? 'Hard' : 'Extreme';
            const token = await user?.getIdToken();

            // Crisis Ops: Use Selected Lead Data if available
            let leadContext = initialContext;
            if (selectedLeadId) {
                const lead = leads.find(l => l.id === selectedLeadId);
                if (lead) {
                    leadContext = {
                        company: lead.companyName,
                        industry: lead.industry || 'Tech',
                        value: lead.value.toString(),
                        // Additional context for the AI
                        stage: lead.status,
                        contact: lead.contactName
                    };
                }
            }

            const scenario = await GeminiService.generateDealScenario(level, leadContext, contextContent, token);

            if (scenario) {
                setGameState(prev => ({
                    ...prev,
                    status: 'playing',
                    scenario: {
                        company: scenario.company,
                        industry: scenario.industry,
                        dealValue: scenario.dealValue,
                        description: scenario.description,
                        difficulty: scenario.difficulty
                    },
                    stakeholders: (scenario.stakeholders || []).map((s: AIStakeholder) => ({ ...s, status: 'unlocked' as const })),
                    dealHealth: 50, // Start neutral
                    logs: [{
                        id: 'init',
                        type: 'system',
                        title: 'MISSION START',
                        description: `Target: ${scenario.company} (${scenario.industry}). Value: ${scenario.dealValue}. Mode: ${selectedLeadId ? 'CRISIS OPS (Real Deal)' : 'Standard Sim'}. Context: ${selectedResourceId ? 'Custom Data' : 'None'}.`,
                        timestamp: 'Day 1'
                    }]
                }));
                // Auto-select first stakeholder
                if (scenario.stakeholders && scenario.stakeholders.length > 0) {
                    setSelectedStakeholderId(scenario.stakeholders[0].id);
                }
            } else {
                // Handle failure
                setGameState(prev => ({ ...prev, status: 'setup' })); // Go back to setup?
            }
        } catch (error) {
            console.error("Failed to init game", error);
            setGameState(prev => ({ ...prev, status: 'setup' }));
        }
    };

    // --- SCROLL TO BOTTOM OF LOGS ---
    useEffect(() => {
        if (logsEndRef.current) {
            logsEndRef.current.scrollIntoView({ behavior: 'smooth' });
        }
    }, [gameState.logs]);

    // Re-run init when user is available and we haven't started (optional protection)
    useEffect(() => {
        if (user && gameState.status === 'initializing' && !gameState.scenario && gameState.turnCount === 1) {
            // We relying on the initial effect, but user might be null initially.
            // However, initGame is called on mount. If user is null then, token is undefined.
            // Service handles undefined token by not sending header (for now).
            // But server WILL require it.
            // We should reload initGame if user becomes available? 
            // Actually, let's just assume user is loaded or useAuth handles loading state.
        }
    }, [user]);

    // --- GAME LOGIC ---

    const handleAction = async (actionId: string) => {
        if (!selectedStakeholderId || isProcessingTurn) return;

        const actionCard = ACTION_DECK.find(a => a.id === actionId);
        if (!actionCard) return;

        setIsProcessingTurn(true);

        // Optimistic UI Log
        const turnId = Date.now().toString();
        const target = gameState.stakeholders.find(s => s.id === selectedStakeholderId);

        setGameState(prev => ({
            ...prev,
            logs: [...prev.logs, {
                id: `cmd-${turnId}`,
                type: 'system',
                title: 'EXECUTING STRATEGY...',
                description: `Action: ${actionCard.label} targeting ${target?.name}...`,
                timestamp: `Day ${prev.turnCount}`
            }]
        }));

        try {
            // AI Evaluation
            const result = await GeminiService.evaluateTurn(
                {
                    company: gameState.scenario?.company || 'Unknown',
                    stakeholders: gameState.stakeholders
                },
                actionCard.label,
                selectedStakeholderId,
                await user?.getIdToken()
            );

            if (result) {
                // Update State
                setGameState(prev => {
                    const newHealth = Math.max(0, Math.min(100, prev.dealHealth + (result.dealHealthDelta || 0)));
                    const newStatus = newHealth <= 0 ? 'lost' : newHealth >= 100 ? 'won' : prev.turnCount >= prev.maxTurns ? 'lost' : 'playing';

                    // Update specific stakeholder sentiment
                    const updatedStakeholders = prev.stakeholders.map(s =>
                        s.id === selectedStakeholderId
                            ? { ...s, sentiment: Math.max(0, Math.min(100, s.sentiment + (result.sentimentDelta || 0))) }
                            : s
                    );

                    return {
                        ...prev,
                        status: newStatus,
                        dealHealth: newHealth,
                        stakeholders: updatedStakeholders,
                        turnCount: prev.turnCount + 1,
                        logs: [...prev.logs, {
                            id: `res-${turnId}`,
                            type: 'email',
                            title: result.outcomeTitle,
                            description: result.outcomeDescription,
                            timestamp: `Day ${prev.turnCount}`,
                            impact: result.dealHealthDelta,
                            agentName: result.emailResponse?.from
                        }]
                    };
                });
            }

        } catch (error) {
            console.error("Turn Error", error);
        } finally {
            setIsProcessingTurn(false);
        }
    };

    // --- RENDERING ---

    // --- RENDERING ---

    if (gameState.status === 'setup') {
        return (
            <div className="fixed inset-0 z-50 flex items-center justify-center bg-slate-950/95 backdrop-blur-md p-4">
                <div className="max-w-2xl w-full bg-slate-900 border border-slate-800 rounded-xl p-8 shadow-2xl relative overflow-hidden">
                    {/* Decorative Background */}
                    <div className="absolute top-0 right-0 w-64 h-64 bg-blue-500/5 rounded-full blur-3xl -translate-y-1/2 translate-x-1/2 pointer-events-none"></div>

                    <button onClick={onClose} className="absolute top-4 right-4 text-slate-500 hover:text-white">
                        <X className="w-6 h-6" />
                    </button>

                    <div className="mb-8">
                        <h2 className="text-3xl font-bold text-white mb-2 flex items-center gap-3">
                            <span className="p-2 bg-blue-600/20 rounded-lg text-blue-400"><Database className="w-6 h-6" /></span>
                            Mission Parameters
                        </h2>
                        <p className="text-slate-400">Configure your simulation environment. Run &ldquo;Crisis Ops&rdquo; on live deals or standard training.</p>
                    </div>

                    <div className="space-y-6">
                        <div>
                            <label className="block text-sm font-medium text-slate-300 mb-3 uppercase tracking-wider">Enablement Context (Optional)</label>

                            {loadingResources ? (
                                <div className="flex items-center gap-2 text-slate-500 p-4 border border-dashed border-slate-800 rounded-lg">
                                    <Loader2 className="w-4 h-4 animate-spin" /> Loading resources...
                                </div>
                            ) : resources.length === 0 ? (
                                <div className="p-4 border border-dashed border-slate-800 rounded-lg text-slate-500 text-sm text-center">
                                    No text-based resources found. Upload documents in the Resources tab to use them here.
                                </div>
                            ) : (
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-3 max-h-60 overflow-y-auto pr-2 custom-scrollbar">
                                    {resources.map(res => (
                                        <div
                                            key={res.id}
                                            onClick={() => setSelectedResourceId(selectedResourceId === res.id ? null : res.id)}
                                            className={`
                                                p-3 rounded-lg border cursor-pointer transition-all flex items-center gap-3
                                                ${selectedResourceId === res.id
                                                    ? 'bg-blue-600/20 border-blue-500 text-white shadow-[0_0_15px_rgba(59,130,246,0.2)]'
                                                    : 'bg-slate-800/50 border-slate-700 text-slate-400 hover:bg-slate-800 hover:border-slate-600'}
                                            `}
                                        >
                                            <FileText className={`w-5 h-5 ${selectedResourceId === res.id ? 'text-blue-400' : 'text-slate-500'}`} />
                                            <div className="flex-1 min-w-0">
                                                <div className="font-medium truncate text-sm">{res.title}</div>
                                                <div className="text-xs opacity-60 truncate">{res.category}</div>
                                            </div>
                                            {selectedResourceId === res.id && <CheckCircle className="w-4 h-4 text-blue-400" />}
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>

                        {/* Crisis Ops Section */}
                        <div>
                            <label className="block text-sm font-medium text-slate-300 mb-3 uppercase tracking-wider">
                                Crisis Ops (Wargame a Live Deal)
                            </label>

                            {leads.length === 0 ? (
                                <div className="p-4 border border-dashed border-slate-800 rounded-lg text-slate-500 text-sm text-center">
                                    No active leads found in CRM.
                                </div>
                            ) : (
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-3 max-h-40 overflow-y-auto pr-2 custom-scrollbar">
                                    {leads.map(lead => (
                                        <div
                                            key={lead.id}
                                            onClick={() => setSelectedLeadId(selectedLeadId === lead.id ? null : lead.id)}
                                            className={`
                                                p-3 rounded-lg border cursor-pointer transition-all flex items-center gap-3
                                                ${selectedLeadId === lead.id
                                                    ? 'bg-red-900/40 border-red-500 text-white shadow-[0_0_15px_rgba(239,68,68,0.2)]'
                                                    : 'bg-slate-800/50 border-slate-700 text-slate-400 hover:bg-slate-800 hover:border-slate-600'}
                                            `}
                                        >
                                            <ShieldAlert className={`w-5 h-5 ${selectedLeadId === lead.id ? 'text-red-500' : 'text-slate-600'}`} />
                                            <div className="flex-1 min-w-0">
                                                <div className="font-medium truncate text-sm">{lead.companyName}</div>
                                                <div className="text-xs opacity-60 truncate">${lead.value?.toLocaleString()}  {lead.status}</div>
                                            </div>
                                            {selectedLeadId === lead.id && <CheckCircle className="w-4 h-4 text-red-500" />}
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>

                        <div className="pt-6 border-t border-slate-800 flex justify-end gap-3">
                            <button
                                onClick={onClose}
                                className="px-6 py-3 rounded-lg text-slate-400 font-medium hover:text-white transition-colors"
                            >
                                Cancel
                            </button>
                            <button
                                onClick={startGame}
                                className="px-8 py-3 rounded-lg bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-500 hover:to-indigo-500 text-white font-bold shadow-lg shadow-blue-900/20 flex items-center gap-2 group transition-all transform hover:scale-[1.02]"
                            >
                                <Play className="w-5 h-5 fill-current" />
                                Start Simulation
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    if (gameState.status === 'initializing') {
        return (
            <div className="fixed inset-0 left-64 z-50 flex items-center justify-center bg-slate-950">
                <div className="text-center space-y-4">
                    <div className="relative w-24 h-24 mx-auto">
                        <div className="absolute inset-0 border-4 border-orange-500/30 rounded-full animate-ping"></div>
                        <div className="absolute inset-0 border-4 border-t-orange-500 rounded-full animate-spin"></div>
                        <div className="absolute inset-0 flex items-center justify-center">
                            <Activity className="text-orange-500 animate-pulse" size={32} />
                        </div>
                    </div>
                    <h2 className="text-2xl font-bold text-white tracking-widest uppercase">Initializing War Room</h2>
                    <p className="text-orange-400 font-mono text-sm">Generating Procedural Scenario via Gemini 2.5 Flash...</p>
                </div>
            </div>
        );
    }

    const activeStakeholder = gameState.stakeholders.find(s => s.id === selectedStakeholderId);

    return (
        <div className="fixed inset-0 left-64 z-50 bg-slate-950 text-slate-200 font-sans overflow-hidden flex flex-col">

            {/* --- HEADER HUD --- */}
            <div className="h-16 border-b border-orange-500/20 bg-slate-900/50 backdrop-blur-md flex items-center justify-between px-6 shrink-0 relative z-20">
                <div className="flex items-center gap-4">
                    <div className="p-2 bg-orange-500/10 rounded border border-orange-500/20">
                        <ShieldAlert className="text-orange-500" size={20} />
                    </div>
                    <div>
                        <h1 className="font-bold text-white uppercase tracking-wider flex items-center gap-2">
                            {gameState.scenario?.company}
                            <span className="text-xs bg-slate-800 px-2 py-0.5 rounded text-orange-400 border border-orange-500/20">{gameState.scenario?.difficulty}</span>
                        </h1>
                        <div className="text-xs text-slate-500 font-mono">VAL: {gameState.scenario?.dealValue}  IND: {gameState.scenario?.industry}</div>
                    </div>
                </div>

                <div className="flex items-center gap-8">
                    {/* Deal Health Bar */}
                    <div className="flex flex-col items-end w-64">
                        <div className="flex justify-between w-full text-xs font-bold uppercase mb-1">
                            <span className="text-slate-500">Deal Probability</span>
                            <span className={gameState.dealHealth > 50 ? 'text-green-500' : 'text-red-500'}>{gameState.dealHealth}%</span>
                        </div>
                        <div className="w-full h-2 bg-slate-800 rounded-full overflow-hidden relative">
                            <div
                                className={`h-full transition-all duration-700 ease-out ${gameState.dealHealth > 66 ? 'bg-green-500' : gameState.dealHealth > 33 ? 'bg-amber-500' : 'bg-red-500'}`}
                                style={{ width: `${gameState.dealHealth}%` }}
                            />
                        </div>
                    </div>

                    {/* Turn Counter */}
                    <div className="text-center">
                        <div className="text-2xl font-bold text-white font-mono">{gameState.turnCount} <span className="text-slate-600 text-sm">/ {gameState.maxTurns}</span></div>
                        <div className="text-[10px] uppercase text-slate-500 tracking-widest">Days Elapsed</div>
                    </div>

                    <button onClick={onClose} className="p-2 hover:bg-white/10 rounded-full transition-colors">
                        <X size={24} className="text-slate-400 hover:text-white" />
                    </button>
                </div>
            </div>

            {/* --- MAIN CONTENT GRID --- */}
            <div className="flex-1 overflow-hidden flex relative">

                {/* LEFT: STAKEHOLDERS (The Board) */}
                <div className="w-1/3 border-r border-orange-500/10 bg-slate-900/30 flex flex-col p-6 overflow-y-auto">
                    <h3 className="text-xs font-bold uppercase text-slate-500 mb-4 flex items-center gap-2">
                        <Users size={14} /> Key Stakeholders
                    </h3>

                    <div className="space-y-4">
                        {gameState.stakeholders.map(stakeholder => (
                            <div
                                key={stakeholder.id}
                                onClick={() => setSelectedStakeholderId(stakeholder.id)}
                                className={`
                                    relative p-4 rounded-xl border cursor-pointer transition-all duration-300 group
                                    ${selectedStakeholderId === stakeholder.id
                                        ? 'bg-orange-500/10 border-orange-500 shadow-[0_0_20px_rgba(249,115,22,0.15)]'
                                        : 'bg-white/5 border-white/5 hover:bg-white/10 hover:border-white/20'}
                                `}
                            >
                                <div className="flex justify-between items-start mb-2">
                                    <div>
                                        <div className="font-bold text-slate-100">{stakeholder.name}</div>
                                        <div className="text-xs text-orange-400 font-mono">{stakeholder.role}</div>
                                    </div>
                                    <div className={`
                                        w-8 h-8 rounded-full flex items-center justify-center font-bold text-xs
                                        ${stakeholder.sentiment >= 70 ? 'bg-green-500/20 text-green-400' : stakeholder.sentiment <= 40 ? 'bg-red-500/20 text-red-400' : 'bg-amber-500/20 text-amber-400'}
                                    `}>
                                        {stakeholder.sentiment}
                                    </div>
                                </div>
                                <div className="flex flex-wrap gap-2 mt-3">
                                    {stakeholder.traits.map(trait => (
                                        <span key={trait} className="px-2 py-0.5 rounded text-[10px] uppercase bg-black/30 text-slate-400 border border-white/5">
                                            {trait}
                                        </span>
                                    ))}
                                </div>

                                {selectedStakeholderId === stakeholder.id && (
                                    <div className="absolute -right-1 top-1/2 -translate-y-1/2 w-2 h-12 bg-orange-500 rounded-l-full blur-[2px]"></div>
                                )}
                            </div>
                        ))}
                    </div>
                </div>

                {/* MIDDLE: ACTION CENTER */}
                <div className="flex-1 flex flex-col bg-slate-950 relative">

                    {/* Background Grid */}
                    <div className="absolute inset-0 bg-[linear-gradient(rgba(255,255,255,0.02)_1px,transparent_1px),linear-gradient(90deg,rgba(255,255,255,0.02)_1px,transparent_1px)] bg-[size:40px_40px]"></div>
                    <div className="absolute inset-0 bg-gradient-to-b from-slate-950 via-transparent to-slate-950"></div>

                    {/* Active Target Info */}
                    <div className="relative z-10 p-8 text-center border-b border-white/5 bg-slate-900/40 backdrop-blur-sm h-48 flex flex-col justify-center">
                        {activeStakeholder ? (
                            <div className="animate-in fade-in zoom-in-95 duration-300">
                                <div className="text-sm text-orange-400 font-mono uppercase tracking-widest mb-2">Target Locked</div>
                                <h2 className="text-3xl font-bold text-white mb-2">{activeStakeholder.name}</h2>
                                <p className="text-slate-400 max-w-md mx-auto">{gameState.status === 'playing' ? "Select a strategic action below to influence this stakeholder." : "Simulation Ended."}</p>
                            </div>
                        ) : (
                            <div className="text-slate-500 italic">Select a stakeholder to target...</div>
                        )}
                    </div>

                    {/* Action Deck */}
                    <div className="flex-1 p-8 grid grid-cols-2 md:grid-cols-3 gap-4 content-start overflow-y-auto z-10">
                        {gameState.status === 'playing' && ACTION_DECK.map(card => (
                            <button
                                key={card.id}
                                disabled={!selectedStakeholderId || isProcessingTurn}
                                onClick={() => handleAction(card.id)}
                                className={`
                                    relative group p-4 rounded-xl border text-left transition-all duration-300
                                    flex flex-col gap-3 overflow-hidden
                                    ${!selectedStakeholderId
                                        ? 'opacity-50 border-white/5 cursor-not-allowed grayscale'
                                        : 'bg-slate-900/80 border-white/10 hover:border-orange-500/50 hover:bg-slate-800 hover:shadow-lg hover:-translate-y-1'
                                    }
                                `}
                            >
                                <div className="flex justify-between items-start w-full">
                                    <div className="p-2 rounded bg-white/5 text-orange-400 group-hover:text-white group-hover:bg-orange-500 transition-colors">
                                        {card.icon}
                                    </div>
                                    <span className={`text-[10px] uppercase font-bold px-2 py-0.5 rounded border ${card.risk === 'Low' ? 'border-green-500/30 text-green-400' :
                                        card.risk === 'Medium' ? 'border-amber-500/30 text-amber-400' :
                                            'border-red-500/30 text-red-400'
                                        }`}>
                                        {card.risk} Risk
                                    </span>
                                </div>
                                <div>
                                    <div className="font-bold text-slate-200 group-hover:text-orange-100">{card.label}</div>
                                    <div className="text-xs text-slate-500 mt-1 leading-relaxed">{card.description}</div>
                                </div>
                            </button>
                        ))}
                    </div>
                </div>

                {/* RIGHT: LIVE LOG (The "Wire") */}
                <div className="w-80 border-l border-orange-500/10 bg-black/40 flex flex-col">
                    <div className="p-4 border-b border-white/10 bg-slate-900/50 backdrop-blur">
                        <h3 className="text-xs font-bold uppercase text-orange-500 flex items-center gap-2">
                            <Activity size={14} /> Mission Log
                        </h3>
                    </div>
                    <div className="flex-1 overflow-y-auto p-4 space-y-4 font-mono text-sm relative">
                        {gameState.logs.map((log) => (
                            <div key={log.id} className="animate-in slide-in-from-right-4 fade-in duration-300">
                                <div className="flex items-center gap-2 text-[10px] text-slate-500 mb-1">
                                    <span>{log.timestamp}</span>
                                    <div className="h-px bg-white/10 flex-1"></div>
                                </div>
                                <div className={`p-3 rounded border border-l-2 ${log.type === 'system' ? 'border-slate-800 border-l-slate-500 bg-slate-900/50' :
                                    (log.impact || 0) > 0 ? 'border-green-900/30 border-l-green-500 bg-green-950/10' :
                                        'border-red-900/30 border-l-red-500 bg-red-950/10'
                                    }`}>
                                    <div className="font-bold text-slate-300 mb-1 flex justify-between">
                                        {log.title}
                                        {log.impact && (
                                            <span className={log.impact > 0 ? "text-green-400" : "text-red-400"}>
                                                {log.impact > 0 ? '+' : ''}{log.impact}%
                                            </span>
                                        )}
                                    </div>
                                    <div className="text-slate-400 text-xs leading-relaxed">
                                        {log.description}
                                    </div>
                                    {log.type === 'email' && (
                                        <div className="mt-2 text-[10px] text-orange-400 flex items-center gap-1">
                                            <Mail size={10} /> From: {log.agentName}
                                        </div>
                                    )}
                                </div>
                            </div>
                        ))}
                        <div ref={logsEndRef} />

                        {isProcessingTurn && (
                            <div className="flex items-center gap-2 text-xs text-orange-400 animate-pulse mt-4">
                                <div className="w-2 h-2 bg-orange-500 rounded-full"></div>
                                Awaiting response...
                            </div>
                        )}
                    </div>
                </div>

            </div>

            {/* GAME OVER MODAL */}
            {(gameState.status === 'won' || gameState.status === 'lost') && (
                <div className="absolute inset-0 z-50 bg-slate-950/90 backdrop-blur-lg flex items-center justify-center animate-in fade-in duration-500">
                    <div className="max-w-md w-full glass-card p-8 text-center border-orange-500/30">
                        <div className={`w-20 h-20 mx-auto rounded-full flex items-center justify-center mb-6 border-4 ${gameState.status === 'won' ? 'border-green-500/30 bg-green-500/10 text-green-400' : 'border-red-500/30 bg-red-500/10 text-red-500'}`}>
                            {gameState.status === 'won' ? <CheckCircle size={40} /> : <AlertTriangle size={40} />}
                        </div>
                        <h2 className="text-4xl font-bold text-white mb-2">{gameState.status === 'won' ? 'DEAL CLOSED' : 'DEAL LOST'}</h2>
                        <p className="text-slate-400 mb-8">
                            {gameState.status === 'won'
                                ? "Excellent work. You successfully aligned the stakeholders."
                                : "The deal fell through. Review the logs to identify the breaking point."}
                        </p>
                        <button
                            onClick={onClose}
                            className="w-full py-4 rounded-lg bg-orange-600 hover:bg-orange-500 text-white font-bold transition-all"
                        >
                            Debrief & Exit
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
}

